// Range: 0..1
float intensity = 0.6;

// Number of octaves in the Perlin noise generation
const int iterations = 17;

float rand(vec2 co){
    return mod(fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453)*iTime/1.0, 1.0);
}

float getOctHarsh(float oct, vec2 pos, float att){
    float x = pos.x;
    float y = pos.y;
    float divx = iResolution.x / pow(2.0, oct);
    float divy = iResolution.y / pow(2.0, oct);
    x = floor(x / divx);
    y = floor(y / divy);
    return rand(divx * divy * vec2(x, y)) / (att * att * oct);
}

float getOct(float octave, vec2 pos, float att){
    float divx = iResolution.x / pow(2.0, octave);
    float divy = iResolution.y / pow(2.0, octave);

    // neighbor noise values
    vec2 below       = vec2( pos.x        , pos.y - divy );
    vec2 above       = vec2( pos.x        , pos.y + divy );
    vec2 left        = vec2( pos.x - divx , pos.y        );
    vec2 right       = vec2( pos.x + divx , pos.y        );
    vec2 topleft     = vec2( pos.x - divx , pos.y - divy );
    vec2 topright    = vec2( pos.x + divx , pos.y - divy );
    vec2 bottomleft  = vec2( pos.x - divx , pos.y + divy );
    vec2 bottomright = vec2( pos.x + divx , pos.y + divy );

    // LR weights
    float wr = mod(pos.x, divx)/divx;
    float wl = 1.0 - wr;
    wr = wr * wr - 0.5;
    wl = wl * wl - 0.5;
    if(wr < 0.0) wr = 0.0;
    if(wl < 0.0) wl = 0.0;

    // AB weights
    float wa = mod(pos.y, divy)/divy;
    float wb = 1.0 - wa;
    wa = wa * wa - 0.5;
    wb = wb * wb - 0.5;
    if(wa < 0.0) wa = 0.0;
    if(wb < 0.0) wb = 0.0;

    float corners = 2.0;
    // corner weigts
    float wtl = (wl + wa)/corners;
    float wtr = (wr + wa)/corners;
    float wbl = (wl + wb)/corners;
    float wbr = (wr + wb)/corners;

    return (1.0 - wl - wr - wa - wb - wtl - wtr - wbl - wbr) * getOctHarsh(octave, pos, att) +
        	wl * getOctHarsh(octave, left, att) +
        	wr * getOctHarsh(octave, right, att) + 
        	wa * getOctHarsh(octave, above, att) + 
        	wb * getOctHarsh(octave, below, att) +
        	wtl * getOctHarsh(octave, topleft, att) +
            wtr * getOctHarsh(octave, topright, att) +
       		wbl * getOctHarsh(octave, bottomleft, att) +
        	wbr * getOctHarsh(octave, bottomright, att);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 white = vec4(1.0, 1.0, 1.0, 1.0);
    vec4 red = vec4(1.0, 0.0, 0.0, 1.0);
    vec4 green = vec4(0.0, 1.0, 0.0, 1.0);
    vec4 blue = vec4(0.0, 0.0, 1.0, 1.0);
    float noise = 0.0;

    for(int i = 2; i < iterations; i++){
    	noise += getOct(float(i), fragCoord.xy, 1.0);    
    }

    noise *= intensity;
	fragColor = noise * red + (noise - 3.0) * green + (noise - 2.0) * blue;
}

