#define SMOOTH(r) (mix(1.0, 0.0, smoothstep(0.9,1.0, r)))
#define M_PI 3.1415926535897932384626433832795

float movingRing(vec2 uv, vec2 center, float r1, float r2)
{
    vec2 d = uv - center;
    float r = sqrt( dot( d, d ) );
    d = normalize(d);
    float theta = -atan(d.y,d.x);
    theta  = mod(-iTime+0.5*(1.0+theta/M_PI), 1.0);
    //anti aliasing for the ring's head (thanks to TDM !)
    theta -= max(theta - 1.0 + 1e-2, 0.0) * 1e2;
    return theta*(SMOOTH(r/r2)-SMOOTH(r/r1));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;
    float ring = movingRing(uv, vec2(iResolution.x/2.0,iResolution.y/2.0), 20.0, 30.0);
    fragColor = vec4( 0.1 + 0.9*ring );
}

#define PI	3.14159265359
#define PI2	( PI * 2.0 )

struct Quad
{
    vec3 a;
    vec3 b;
    vec3 c;
    vec3 d;
};

vec2 rotate( in vec2 p, in float t )
{
	return p * cos( -t ) + vec2( p.y, -p.x ) * sin( -t );
}   

vec3 rotate( in vec3 p, in vec3 t )
{
    p.yz = rotate( p.yz, t.x );
    p.zx = rotate( p.zx, t.y );
	p.xy = rotate( p.xy, t.z );
    return p;
}

Quad rotate( in Quad m, in vec3 t )
{
    m.a = rotate(m.a, t);
    m.b = rotate(m.b, t);
    m.c = rotate(m.c, t);
    m.d = rotate(m.d, t);
    return m;
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
	vec2 pa = p - a;
	vec2 ba = b - a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h );
}

float deStar( in vec2 p)
{
    float time = -1.5 * iTime;
    vec2 v0 = rotate( vec2(0.4, 0.0), PI2 / 5.0 * 0.0 + time) + vec2(0.5);
    vec2 v1 = rotate( vec2(0.4, 0.0), PI2 / 5.0 * 1.0 + time) + vec2(0.5);
    vec2 v2 = rotate( vec2(0.4, 0.0), PI2 / 5.0 * 2.0 + time) + vec2(0.5);
    vec2 v3 = rotate( vec2(0.4, 0.0), PI2 / 5.0 * 3.0 + time) + vec2(0.5);
    vec2 v4 = rotate( vec2(0.4, 0.0), PI2 / 5.0 * 4.0 + time) + vec2(0.5);
    float de = 1.0;
    de  = min( de, sdSegment( p, v0, v2) );
    de  = min( de, sdSegment( p, v2, v4) );
    de  = min( de, sdSegment( p, v4, v1) );
    de  = min( de, sdSegment( p, v1, v3) );
    de  = min( de, sdSegment( p, v3, v0) );
    return de;
}

vec3 hsv(float h, float s, float v)
{
  return mix( vec3( 1.0 ), clamp( ( abs( fract(
    h + vec3( 3.0, 2.0, 1.0 ) / 3.0 ) * 6.0 - 3.0 ) - 1.0 ), 0.0, 1.0 ), s ) * v;
}

float castRay( in vec3 ro, in vec3 rd, in Quad q, out vec3 p)
{
    float z = 10.0;
    vec3 n = cross( q.c - q.a, q.b - q.a );
    float t = dot( q.a - ro, n ) / dot( rd, n );
    if ( t > 0.0 )
	{
    	p = ro + rd * t;
        if ( dot( cross( q.b - q.a, n ), p - q.a ) > 0.0 ) 
        if ( dot( cross( q.c - q.b, n ), p - q.b ) > 0.0 ) 
        if ( dot( cross( q.d - q.c, n ), p - q.c ) > 0.0 )
        if ( dot( cross( q.a - q.d, n ), p - q.d ) > 0.0 ) z = t;
    }
    return z;
}

vec2 getUV(in Quad q, in vec3 p)    
{
	vec3 ap = p - q.a;
    vec3 bp = p - q.b;
    vec3 dp = p - q.d;
    vec3 ab = q.b - q.a;
    vec3 ad = q.d - q.a;
    float x0 = dot( bp, ab );
    float x1 = dot( ap, ab );
	float y0 = dot( dp, ad );
    float y1 = dot( ap, ad );
    return vec2( x0 / ( x0 - x1 ), y0 / ( y0 - y1 ) );
}

Quad genQuad( in vec3 center)
{
 	return Quad(
        center + vec3(  1.0,  1.0, 0.0) * 0.5,
        center + vec3( -1.0,  1.0, 0.0) * 0.5,
        center + vec3( -1.0, -1.0, 0.0) * 0.5,
        center + vec3(  1.0, -1.0, 0.0) * 0.5);
}

Quad quads( in int i )
{
    float time = 0.3 * iTime ;
    vec3 theta = vec3( 0.2, 0.2, 0.6 * iTime );
	if ( i < 15)
    {
        time *= -1.0;
        theta *= -1.0;
    }
    Quad s = genQuad(
    	vec3( 3.0 * sin( float( i ) * PI2 / 15.0 + time ),
    		  0.1, 
    		  3.0 * cos( float( i ) * PI2 / 15.0 + time ) - 3.0
    	) );
	return  rotate( s, theta );  
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = ( 2.0 * fragCoord.xy - iResolution.xy ) / iResolution.y;
    vec3 rd = normalize( vec3( p, -1.8 ) );
	vec3 ro = vec3( 0.0, 0.0, 1.8 );
    vec3 col =  vec3( 0.2 + 0.2 * p.y );
    float z = 10.0;
    for (int i = 0; i < 30; i++ )
    {
    	Quad s = quads( i );
    	vec3 pos;
    	float d = castRay(ro, rd, s, pos);
    	if ( z > d )
    	{
        	vec2 uv = getUV( s, pos );
            float dd = deStar(uv);
            if ( dd < 0.025)
            {
                z = d;
                col = hsv(mod(float(i), 15.0)/15.0, 0.8, 0.8) ;
            }
    	}
    } 
    fragColor = vec4( col, 1.0 );
}

float roundBox(vec2 coord, vec2 pos, vec2 b, float c ){
  return 1.-floor(length(max(abs(coord-pos)-b,c)));
}
float circle(vec2 coord, vec2 pos, float size){
    return min(floor(distance(coord,pos)-size),0.);
}
float sdCapsule( vec2 p, vec2 a, vec2 b, float r ){
    vec2 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return min(floor(length( pa - ba*h ) - r),0.);
}
mat2 rotate(float Angle){
    mat2 rotation = mat2(
        vec2( cos(Angle),  sin(Angle)),
        vec2(-sin(Angle),  cos(Angle))
    );
	return rotation;
}

float triangle( vec2 coord, vec2 pos, float angle, float thick, float size){
    vec2 original_coord = coord;
    coord += pos;
    coord *= rotate(angle);
    float collision = 0.0;
    collision += sdCapsule( coord, vec2( 0.0, 0.333)*size, vec2( 0.3,-0.2)*size, thick );
    collision += sdCapsule( coord, vec2( 0.0, 0.333)*size, vec2(-0.3,-0.2)*size, thick );
    collision += sdCapsule( coord, vec2( 0.3,-0.2)*size, vec2(-0.3,-0.2)*size, thick );
    collision += 3.0*texture( iChannel0, original_coord * 0.6 + vec2(iTime*0.02) ).b * 0.6;
    collision += 2.5*texture( iChannel0, original_coord * 0.1 + vec2(iTime*0.001) ).b * 0.4;
    return -min(max(-collision,0.0),1.0);
}
vec3 overlay(){
	vec2 p = gl_FragCoord.xy / iResolution.xy;

	float aspectCorrection = (iResolution.x/iResolution.y);
	vec2 coordinate_entered = 2.0 * p - 1.0;
	vec2 coord = vec2(aspectCorrection,1.0) *coordinate_entered;

    vec3 COLOR = vec3(0.0);
    float collision = 0.0;
    for( float i = 0.0; i < 14.0; i++){
        vec2 triangle_position = vec2(-mod(iTime+i*0.2+i*0.55,4.0)+2.0,sin(iTime+i));
        triangle_position.y /= triangle_position.x + 2.3;
        collision += triangle( coord, triangle_position, iTime+i, 0.03, 0.9 + sin(i*5235.1112313+iTime) * 0.2);
    }
    ;
    if(collision<0.0){
        COLOR = vec3(0.5);
    }
    return COLOR;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 p = fragCoord.xy / iResolution.xy;

	float aspectCorrection = (iResolution.x/iResolution.y);
	vec2 coordinate_entered = 2.0 * p - 1.0;
	vec2 coord = vec2(aspectCorrection,1.0) *coordinate_entered;

	float vignette = 1.0 / max(0.25 + 0.3*dot(coord,coord),1.);
    coord.x += 0.6;
    coord /= 1.6+sin(iTime*2.0)*0.1;
    coord *= rotate(0.2 + 0.1*sin(iTime));
	vec3 COLOR =(vec3(0.9,0.5,0.4)
        		+vec3(floor(mod(coord.y*2.,1.)*3.+.015))
        		*vec3(1.0,0.4,0.9) );


    vec2 position = vec2(0.);
    vec2 size = vec2(11.,0.3);
    if(coord.x<0.){
    size.y += (.631*sin(coord.x*4.)+1.)*texture(iChannel1,.4*coord+vec2(iTime*.17,0.)).r*1.;
    size.y += (.341*sin(coord.x*2.)+1.)*texture(iChannel1,.4*coord+vec2(iTime*.127,0.)).r*.3;
    size.y /= -.5+pow(-coord.x*.017-1.2,3.)+.1*sin(iTime+-coord.x*11.);
    } else {
    size.y += (.631*sin(coord.x*4.)+1.)*texture(iChannel1,.4*coord+vec2(-iTime*.17,0.)).r*1.;
    size.y += (.341*sin(coord.x*2.)+1.)*texture(iChannel1,.4*coord+vec2(-iTime*.127,0.)).r*.3;
    size.y /= -.5+pow(-coord.x*.017-1.2,3.)+.1*sin(iTime+-coord.x*11.);

    }
    float corner = 0.1;
    COLOR -= max(roundBox(coord*5., position, size, corner )*2.,0.);
    COLOR += overlay();
    float head_size = sin(iTime)*.06+mod(iTime*2.,1.)*.03;
    COLOR += 4.*circle(coord, vec2(0.), .5+head_size);
    for(float i=0.; i<3.141*2.; i+=3.141*.25){
    	COLOR += 4.*sdCapsule(coord, vec2(0.0), vec2(cos(i+iTime),sin(i+iTime))*.7, 0.1);
    }


    if(length(coord*vec2(.6,1.-sin(coord.x*5.-3.141*.5)))-.1<.15)
    COLOR = vec3(1.);
    if(length(coord)-.1<.1)
    COLOR = vec3(0.);
    if(length(coord)-.1<.01)
    COLOR = vec3(1.);


	fragColor = vec4( COLOR*vignette
         				,1.0);
}

uniform float iOvertoneVolume;

vec4 blue_lines(vec2 fragCoord) {
  float v = texture(iChannel0, vec2(0.5)).x;
  float t = iTime;

  vec2 uv = 2.0*(fragCoord.xy/iResolution.xy) - 1.0;

  float r, g, b = 0.0;
  float i, j, k = 0.0;

  float z = 6.0 + 4.0 * sin(t);

  i = sin(pow(uv.x*z,2.0)+ abs(sin(t*70.0)*pow(uv.y*z,2.0)));
  j = pow(i, 50.0*abs(sin(t*4.0*v)));
  b = j;

  return vec4(r, g, b, 1.0);
}

vec4 name_tbd(vec2 fragCoord) {
  float v = iOvertoneVolume;
  float t = iTime;

  vec2 uv = 2.0*(fragCoord.xy/iResolution.xy) - 1.0;

  float r, g, b = 0.0;
  float h, i, j, k = 0.0;

  float vv = 1.0;
  float z = 2.0;
  float w = 1000.0 + (1000.0 * pow(sin(t*3.0),3.0));

  h = (w * pow(uv.x*z,2.0)) + pow(uv.y*z,2.0);
  i = sin(h);
  if (h > 4.0) {
    i = 0.0;
  }
  j = pow(i, 50.0*abs(sin(vv*4.0)));
  b = j;

  return vec4(r, g, b, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  float t = iTime;

  fragColor = mix(blue_lines(fragCoord), name_tbd(fragCoord), sin(t));
}

// "Seascape" by Alexander Alekseev aka TDM - 2014
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

const int NUM_STEPS = 38;
const float PI	 	= 3.14159265;
const float EPSILON	= 1e-4;
float EPSILON_NRM	= 0.1 / iResolution.x;

// sea
const int ITER_FRAGMENT = 5;
const float SEA_HEIGHT = 1.6;
const float SEA_CHOPPY = 2.0;
const float SEA_SPEED = 1.8;
const float SEA_FREQ = 0.16;
const vec3 SEA_BASE = vec3(0.1,0.19,0.22);
const vec3 SEA_WATER_COLOR = vec3(0.8,0.9,0.6);
const float SKY_INTENSITY = 1.0;
float SEA_TIME = iTime * SEA_SPEED;

// math
mat4 fromEuler(vec3 ang) {
	vec2 a1 = vec2(sin(ang.x),cos(ang.x));
    vec2 a2 = vec2(sin(ang.y),cos(ang.y));
    vec2 a3 = vec2(sin(ang.z),cos(ang.z));
    mat4 m;
    m[0] = vec4(a1.y*a3.y+a1.x*a2.x*a3.x,a1.y*a2.x*a3.x+a3.y*a1.x,-a2.y*a3.x,0.0);
	m[1] = vec4(-a2.y*a1.x,a1.y*a2.y,a2.x,0.0);
	m[2] = vec4(a3.y*a1.x*a2.x+a1.y*a3.x,a1.x*a3.x-a1.y*a3.y*a2.x,a2.y*a3.y,0.0);
	m[3] = vec4(0.0,0.0,0.0,1.0);
	return m;
}
vec3 rotate(vec3 v, mat4 m) {
    return vec3(dot(v,m[0].xyz),dot(v,m[1].xyz),dot(v,m[2].xyz));
}
float hash( vec2 p ) {
	float h = dot(p,vec2(127.1,311.7));	
    return fract(sin(h)*43758.5453123);
}
float noise( in vec2 p ) {
    vec2 i = floor( p );
    vec2 f = fract( p );	
	vec2 u = f*f*(3.0-2.0*f);
    return -1.0+2.0*mix( mix( hash( i + vec2(0.0,0.0) ), 
                     hash( i + vec2(1.0,0.0) ), u.x),
                mix( hash( i + vec2(0.0,1.0) ), 
                     hash( i + vec2(1.0,1.0) ), u.x), u.y);
}


// lighting
float diffuse(vec3 n,vec3 l,float p) { return pow(dot(n,l) * 0.4 + 0.6,p); }
float specular(vec3 n,vec3 l,vec3 e,float s) {    
    float nrm = (s + 8.0) / (3.1415 * 8.0);
    return pow(max(dot(reflect(e,n),l),0.0),s) * nrm;
}

// sky
vec3 sky_color(vec3 e) {
    e.y = max(e.y,0.0);
    vec3 ret;
    ret.x = pow(1.0-e.y,2.0);
    ret.y = 1.0-e.y;
    ret.z = 0.6+(1.0-e.y)*0.4;
    return ret * SKY_INTENSITY;
}

// sea
float sea_octave(vec2 uv, float choppy) {
    uv += noise(uv);        
    vec2 wv = 1.0-abs(sin(uv));
    vec2 swv = abs(cos(uv));    
    wv = mix(wv,swv,wv);
    return pow(1.0-pow(wv.x * wv.y,0.65),choppy);
}

float map_detailed(vec3 p) {
    float freq = SEA_FREQ;
    float amp = SEA_HEIGHT;
    float choppy = SEA_CHOPPY;
    vec2 uv = p.xz; uv.x *= 0.75;
    mat2 m = mat2(1.6,1.2,-1.2,1.6);

    float d, h = 0.0;    
    for(int i = 0; i < ITER_FRAGMENT; i++) {        
    	d = sea_octave((uv+SEA_TIME)*freq,choppy);
    	d += sea_octave((uv-SEA_TIME)*freq,choppy);
        h += d * amp;        
    	uv *= m; freq *= 1.9; amp *= 0.22;
        choppy = mix(choppy,1.0,0.2);
    }
    return p.y - h;
}

float map(vec3 p) {
    return map_detailed(p);
}
vec3 sea_color(in vec3 p, in vec3 n, in vec3 eye, in vec3 dist) {  
    float fresnel_o = 1.0 - max(dot(n,-eye),0.0);
    float fresnel = pow(fresnel_o,3.0) * 0.65;

    // reflection
    vec3 refl = sky_color(reflect(eye,n));

    // color
    vec3 ret = SEA_BASE;    
    ret = mix(ret,refl,fresnel);

    // wave peaks    
    float atten = max(1.0 - dot(dist,dist) * 0.001, 0.0);
    ret += SEA_WATER_COLOR * (p.y - SEA_HEIGHT) * 0.18 * atten;

    return ret;
}

// tracing
vec3 getNormal(vec3 p, float eps) {
    vec3 n;
    n.y = map_detailed(p);    
    n.x = map_detailed(vec3(p.x+eps,p.y,p.z)) - n.y;
    n.z = map_detailed(vec3(p.x,p.y,p.z+eps)) - n.y;
    n.y = eps;
    return normalize(n);
}
float hftracing(vec3 ori, vec3 dir, out vec3 p) {  
    float tm = 0.0;
    float tx = 1000.0;    
    float hx = 1.0;
    float hm = 0.0;    
    float tmid = 1.0;
    for(int i = 0; i < NUM_STEPS; i++) {
        //tmid = mix(tm,tx, hm/(hm-hx));                   
        p = ori + dir * tmid;                   
    	tmid += map(p);

    }
    return tmid;
}

// main
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;    
    float time = iTime * 0.3;

    // ray
    vec3 ang = vec3(sin(time*3.0)*0.1,sin(time)*0.2+0.4,time);
    if(iMouse.z > 0.0) ang = vec3(0.0,clamp(2.0-iMouse.y*0.01,-0.3,PI),iMouse.x*0.01);
	mat4 rot = fromEuler(ang);

    vec3 ori = vec3(0.0,0.2,time*1.0);
    ori.y += abs(map_detailed(-ori));
    vec3 dir = normalize(vec3(uv.xy,-1.0));
    dir = rotate(normalize(dir),rot);

    // tracing
    vec3 p;
    float dens = hftracing(ori,dir,p);
    vec3 dist = p - ori;
    vec3 n = getNormal(p, dot(dist,dist)*EPSILON_NRM);

    // color
    vec3 color = sea_color(p,n,dir,dist);
    vec3 light = normalize(vec3(0.0,1.0,0.8));  
    color += vec3(diffuse(n,light,80.0) * SEA_WATER_COLOR) * 0.12; 
    color += vec3(specular(n,light,dir,60.0));  

    // post
    color = mix(sky_color(dir),color, clamp(1.0-length(dist)/100.0,0.0,1.0)); 
    color = pow(color,vec3(0.75));
	fragColor = vec4(color,1.0);
}

