struct Sphere
{
    vec3 position;
    float radius;
    vec4 color;

};

struct Ray
{
    vec3 from;
    vec3 direction;
};

const int SPHERES_COUNT = 9;

Sphere spheres[SPHERES_COUNT];


const float distort = 1.0;
float d = 4.0 / distort;
vec3 lightPos = vec3(1000, 1000, 2000.0);
vec4 ambient = vec4(0.15, 0.15, 0.15, 0.15);

vec4 ColorCircle(in Sphere sp, Ray ray, float t, vec3 temp, out vec3 norm, out vec3 hit)
{
    vec3 hitp = ray.from + t * ray.direction;
    vec3 normal = hitp - sp.position;
    normal = normalize(normal);

    vec3 lightDir = normalize(lightPos - sp.position );
    vec3 ldiff = lightPos - sp.position;

    float d = dot(normal, normalize(lightDir));  
    d = max(d, 0.0);

    vec3 ldiff2 = lightPos - hitp;
    vec3 R = reflect(normalize(-ldiff2), normal);    
    R = normalize(R);

    float spec = dot(R, normalize(-ray.direction));    

    spec = max(0.0, spec);
    spec = pow(spec, 10.0);

    vec4 specColor = vec4(spec, spec, spec, spec);
    norm = normal;
    hit = hitp;
    return vec4(d, d, d, d) * sp.color + ambient + spec;
}



vec4 traceSphere(in Sphere sp, Ray ray, out vec3 hitp, out vec3 normal, out float hitdistance)
{

    float d;
    vec3 temp = ray.from - sp.position;
    float a = dot(ray.direction, ray.direction);
    float b = 2.0 * dot(temp, ray.direction);
    float c = dot(temp, temp) - sp.radius * sp.radius;
    float disc = b * b - 4.0 * a * c;

    if (disc >= 0.0)
    {
        float e = sqrt(disc);
        float denom = 2.0 * a;
        float t = (-b - e) / denom;

        if ( t > 0.0)
        {
	        hitdistance = t;
            return ColorCircle(sp, ray, t, temp, normal, hitp);
        }

        t = (-b + e) / denom;

        if (t > 0.0)
        {
	        hitdistance = t;
            return ColorCircle(sp, ray, t, temp, normal, hitp);
        }       
    }
    return vec4(0.0, 0.0, 0.0, 0.0);
}


vec4 traceSpheres(Ray ray, int bounceIndex, out vec3 hitp, out vec3 normal)
{


    float closestHit = 99999.0;
    vec4 col = vec4(0.0, 0.0, 0.0, 0.0);
    for (int i = 0; i < SPHERES_COUNT; ++i)
    {
	    float hitDistance = 0.0;
        vec4 result = traceSphere(spheres[i], ray, hitp, normal, hitDistance);

        if (hitDistance > 0.0)
        {
            if (hitDistance < closestHit)
            {
                closestHit = hitDistance;
                col = result;
            }

        }
    }


    return col;
}


vec2 getNormalizedPosition(vec2 screenCoord)
{
    float midx = iResolution.x * 0.5;
    float midy = iResolution.y * 0.5;

    float mx = screenCoord.x - midx;
    float my = screenCoord.y - midy;
    return vec2(mx / midx, my / midx);
}


Ray getRay(vec2 screenCoord)
{
    vec3 p = vec3(getNormalizedPosition(screenCoord), 0.0);

    Ray r;
    r.from = lookFrom;

    vec3 look = lookAt - lookFrom;

    p.z = look.z;
    p.z /= length(look);
    p.z *= d;
    p = normalize(p);
    r.direction = p;
    r.direction *= rot;

    return r;
}


void lookat(vec3 to)
{
    lookAt = to;
    vec3 fwd = normalize(lookFrom - to);
    vec3 up = vec3(0.0, 1.0, 0.0);

    vec3 v = normalize(cross(up, fwd));
    vec3 w = normalize(cross(fwd, v));

    rot[0] = v;
    rot[1] = w;
    rot[2] = fwd;


}

vec4 draw(vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 totalCol = vec4(3.0, 1.0 + sin(iTime * 0.25), uv.y * 4.0 + 1.0 - sin(iTime * 0.5), 0.0);    
	const int kernel = 3;
    const float filterOffset = 1.0 / float(kernel);


    for (int x = -kernel; x < kernel; x+=2)
    {
        for (int y= -kernel; y < kernel; y+=2)
        {
            vec3 hitp = vec3(0.0, 0.0, 0.0);
            vec3 normal = vec3(0.0, 0.0, 0.0);
            vec2 offset = vec2(x, y) * filterOffset;

            vec2 pos = fragCoord.xy + offset;
		    Ray r = getRay(pos);
		    totalCol += traceSpheres(r, 0, hitp, normal);

            if (hitp != vec3(0.0, 0.0, 0.0))
            {
	            vec3 ref = reflect(r.direction, normal);
                Ray r2;
                r2.from = hitp;
                r2.direction = normalize(ref);
                r2.from += r2.direction * 1.0;

                vec4 reflectCol = traceSpheres(r2, 0, hitp, normal);
                totalCol += reflectCol * 0.95;

            }
        }
    }

    return totalCol / float (kernel * kernel);
}

vec3 rotatey(float angle, vec3 point)
{
    mat3 mat;
    mat[0] = vec3(cos(angle), 0.0, sin(angle));
    mat[1] = vec3(0.0, 1.0, 0.0);
    mat[2] = vec3(-sin(angle), 0.0, cos(angle));
    return mat * point;
}

vec3 rotatez(float angle, vec3 point)
{
    mat3 mat;
    mat[0] = vec3(cos(angle), -sin(angle), 0.0);
    mat[1] = vec3(sin(angle), cos(angle), 0.0);
    mat[2] = vec3(0.0, 0.0, 1.0);
    return mat * point;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float r = mod(iTime, 4.0);

    d = 4.0 / distort;


    lookFrom = vec3(0.0, -0.0, 800.0);

    vec3 at = vec3(0.0, 0.0, -1.0);

    lookat(lookFrom + at);
    float yp = -1.0;

    vec2 uv = fragCoord.xy / iResolution.xy;
    float w = texture( iChannel0, vec2(uv.y,1.0) ).x;
    float wave2 = texture( iChannel0, vec2(uv.x,1.0) ).x;


    float radius = 30.0 * (1.0 + w * 0.05);
	float toRotate = iTime;

    for (int i = 0; i < SPHERES_COUNT; ++i)
    {

        Sphere s;
        float fi = float(i);
        float xp = fi;

        xp = mod(xp, 3.0);
        yp += mod(fi, 3.0) == 0.0 ? 1.0 : 0.0;
        xp -= 1.0;
        float y = yp - 1.0;

        vec3 spos = vec3(xp * radius * 2.5, y * radius * 2.5, 0.0);
        spos = rotatez(toRotate, spos);    
        spos = rotatey(sin(toRotate), spos);    
        s.position = spos;
        s.radius = radius;

        float r = (sin(iTime + 2.0) + 1.0) * 0.5;
        float g = (cos(iTime) + 1.0) * 0.5;
        g *= wave2;
        float b = mod(fi, 2.0);
        vec4 color = vec4(r, g, b, 0.0);
        s.color = color;
        spheres[i] = s;
    }

    fragColor = draw(fragCoord);
}

// On/Off Spikes, fragment shader by movAX13h, oct 2014

