mat3 xrotate(float t) {
	return mat3(1.0, 0.0, 0.0,
                0.0, cos(t), -sin(t),
                0.0, sin(t), cos(t));
}

mat3 yrotate(float t) {
	return mat3(cos(t), 0.0, -sin(t),
                0.0, 1.0, 0.0,
                sin(t), 0.0, cos(t));
}

mat3 zrotate(float t) {
    return mat3(cos(t), -sin(t), 0.0,
                sin(t), cos(t), 0.0,
                0.0, 0.0, 1.0);
}

float sphereDistance(vec3 pos) {
	return length(pos) - 0.6;   
}

float cubeDistance(vec3 pos) {
	vec3 clamped = clamp(pos, -0.5, 0.5);
    return length(pos - clamped);
}

float planeDistance(vec3 pos) {
	vec3 origin = vec3(0.0, -0.5, 0.0);
    vec3 normal = vec3(0.0, 1.0, 0.0);
    vec3 delta = pos - origin;
    float prod = dot(delta, normal);
    return prod;
}

float combinedDistance(vec3 pos) {
    pos *= xrotate(0.5) * yrotate(iTime);

    float cube = cubeDistance(pos);

    float sphere = sphereDistance(pos);

	float x = max(cube - sphere, cube);

	return min(x, planeDistance(pos));
}

float aoc(vec3 origin, vec3 ray) {
    float delta = 0.1;
    const int samples = 6;
    float r = 0.0;
    for (int i = 1; i <= samples; ++i) {
        float t = delta * float(i);
     	vec3 pos = origin + ray * t;
        float dist = combinedDistance(pos);
        float len = abs(t - dist);
        r += len * pow(2.0, -float(i));
    }
    return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 origin = vec3(0.0, 0.0, -2.0);
    vec3 ray = normalize(vec3(uv, 1.0));

    float t = 0.0;

    for (int i = 0; i < 64; ++i) {
        vec3 pos = origin + ray * t;
        float d = combinedDistance(pos);
        if (d < 0.0) {
         	break;   
        }
        t += d;
    }

    vec3 worldPos = origin + ray * t;

    float shade = 1.0 - aoc(worldPos, -ray);

    shade = pow(max(shade, 0.0), 8.0);

    float fog = 1.0 / (1.0 + t);

    float col = shade * fog;

	fragColor = vec4(col, col, col, 1.0);
}

