#define AA_SIZE 4

float meta(vec2 uv) {
    uv /= 0.8;
    float l1 = length(uv - vec2(sin(iTime), cos(iTime))*0.5 - vec2(-0.1))-0.3;
    float l2 = length(uv - vec2(cos(1.4*iTime), sin(iTime))*0.2 - vec2(0.35))-0.4+0.1*sin(1.4*iTime);
    float l3 = length(uv - vec2(sin(1.5*iTime), cos(1.9*iTime))*0.6 - vec2(-0.1))-0.2+0.1*sin(1.9*iTime);
    float l4 = length(uv);
    l4 = max(l4-1.051, -(l4-1.05));
    return min(l4, min(l3, min(min(l1, l2), l1*l2*l3)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 rand = texture(iChannel0, mod(fragCoord.xy/64.0, 1.0));
    vec2 pixelAspect = vec2(iResolution.x / iResolution.y, 1.0);
	vec2 pixelSize = 2.0*pixelAspect / iResolution.xy;
    vec2 uv = -pixelAspect + fragCoord.xy * pixelSize;

    vec3 accum = vec3(0.0);

    vec3 bg = sqrt(vec3(0.95,0.95, 0.92));

    vec3 color = vec3(1.0-abs(uv*mat2(cos(iTime), sin(iTime), -sin(iTime), cos(iTime))),0.5+0.5*sin(iTime));
    color = 1.0-exp(-2.5*(color+pow(color, vec3(16.0))));
    color += 4.0/256.0*rand.rgb;
    color = sqrt(color);

    for (int x=0; x<AA_SIZE; x++) {
        for (int y=0; y<AA_SIZE; y++) {
		    float f = meta(uv + vec2(float(x),float(y)) * pixelSize/float(AA_SIZE));
            accum += mix(color, bg, step(0.0, f-0.005));
        }
    }

    fragColor = vec4(accum*accum/float(AA_SIZE*AA_SIZE*AA_SIZE*AA_SIZE), 1.0);
}

