float iPlane( in vec3 ro, in vec3 rd )
{
    return (-1.0 - ro.y)/rd.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord.xy-iResolution.xy) / iResolution.y;
    float s = (2.0*iMouse.x-iResolution.x) / iResolution.y;
    if( iMouse.z<0.001 ) s=0.0;

	vec3 ro = vec3(0.0, 0.0, 4.0 );
	vec3 rd = normalize( vec3(p,-2.0) );

    // box animation
	mat4 rot = rotationAxisAngle( normalize(vec3(1.0,0.9,0.5)), 0.5*iTime );
	mat4 tra = translate( 0.0, 0.0, 0.0 );
	mat4 txi = tra * rot; 
	mat4 txx = inverse( txi );
	vec3 box = vec3(0.2,0.7,2.0) ;

    vec4 rrr = textureLod( iChannel0, (fragCoord.xy)/iChannelResolution[0].xy, 0.0  ).xzyw;

    vec3 col = vec3(0.0);

    float tmin = 1e10;

    float t1 = iPlane( ro, rd );
    if( t1>0.0 )
    {
        tmin = t1;
        vec3 pos = ro + tmin*rd;
        vec3 nor = vec3(0.0,1.0,0.0);
        float occ = 0.0;

        if( p.x > s )
        {
            occ = boxOcclusion( pos, nor, txx, txi, box );
        }
        else
        {
   		    vec3  ru  = normalize( cross( nor, vec3(0.0,1.0,1.0) ) );
		    vec3  rv  = normalize( cross( ru, nor ) );

            occ = 0.0;
            for( int i=0; i<256; i++ )
            {
                vec2  aa = hash2( rrr.x + float(i)*203.1 );
                float ra = sqrt(aa.y);
                float rx = ra*cos(6.2831*aa.x); 
                float ry = ra*sin(6.2831*aa.x);
                float rz = sqrt( 1.0-aa.y );
                vec3  dir = vec3( rx*ru + ry*rv + rz*nor );
                vec4 res = boxIntersect( pos, dir, txx, txi, box );
                occ += step(0.0,res.x);
            }
            occ /= 256.0;
        }

        col = vec3(1.2);
        col *= 1.0 - occ;
    }

    vec4 res = boxIntersect( ro, rd, txx, txi, box );
    float t2 = res.x;
    if( t2>0.0 && t2<tmin )
    {
        tmin = t2;
        float t = t2;
        vec3 pos = ro + t*rd;
        vec3 nor = res.yzw;
		col = vec3(1.4);//vec3(1.0,0.85,0.6);
        col *= 0.6 + 0.4*nor.y;
	}

	col *= exp( -0.05*tmin );

    float e = 2.0/iResolution.y;
    col *= smoothstep( 0.0, 2.0*e, abs(p.x-s) );

    fragColor = vec4( col, 1.0 );
}

//Implementation of Multiple Importance Sampling technique(E.Veach 1995)
//example shows MIS for direct light calculation
//Idea behind technique is that every monte-carlo sampling technique has 
//variance and this variance comes from low probability of success.
//So if you have 2 or more unbiased monte-carlo sampling techniques 
//which gives you different response on same situation you can predict 
//which of those responses contains more variance and weight them
//correspondingly to decrease overal variance and stay unbiased.
