// BSDF functions *************************************************************
float evaluateBlinn( in  vec3 N, in vec3 E, in vec3 L, in float roughness ) {
    vec3 H = normalize(E + L);
    float cosTheta = dot(N,H);
    return (roughness + 2.0) / (8.0 * PI) * pow(cosTheta, roughness);
}

float pdfBlinn(in vec3 N, in vec3 E, in vec3 L, in float roughness ) {
    vec3 H = normalize(E + L);
    float cosTheta = dot(N,H);
	float normalizationFactor = (roughness + 1.0) / TWO_PI;
    return pow( cosTheta, roughness ) * normalizationFactor / (4.0 * dot(E, H));
}

vec3 sampleBlinn( in vec3 N, in vec3 E, in float roughness, in float r1, in float r2, out float pdf ) {
    float cosTheta = pow( r1, 1.0/( roughness ) );
    float phi = r2*TWO_PI;
    float theta = acos( cosTheta );
    vec3 H = localToWorld( sphericalToCartesian( 1.0, phi, theta ), N );
    float dotNH = dot(H,N);
    vec3 L = reflect( E*(-1.0), H );

    pdf = pdfBlinn(N, E, L, roughness );

    return L;
}

float evaluateLambertian( in vec3 N, in vec3 L ) {
    return INV_PI;
}

float pdfLambertian( in vec3 N, in vec3 L ) {
    return max(.0, dot( N, L )) * INV_PI;
}

vec3 sampleLambertian( in vec3 N, in float r1, in float r2, out float pdf ){
    vec3 L = sampleHemisphereCosWeighted( N, r1, r2 );
    pdf = pdfLambertian(N, L);
    return L;
}
