	//fragColor = vec4(vec3(sin(iTime)),1.0);

    float x = fragCoord.x;
    float y = fragCoord.y;
    float t = iTime*1000.0;

    float adx2 = (sin(t/1000.0+3.0) + 1.0) * 200.0;
    float ady2 = (sin(t/1000.0+.5) + 1.0) * 200.0;
    float adx3 = (sin(t/1000.0+.8) + 1.0) * 200.0;
    float ady3 = (sin(t/1000.0+1.5) + 1.0) * 200.0;

    float d = sqrt(x*x+y*y)+t/12.0;
    float v = sin(d/4.0);

    d = sqrt((x-adx2) * (x-adx2) + (y-ady2) * (y-ady2))+t/16.0;
    float v2 = sin(d / 5.0);
    d = sqrt((x-adx3) * (x-adx3) + (y-ady3) * (y-ady3))+t/34.0;
    float v3 = sin(d / 4.0);
    v = ((v + 1.0) * 2.0 + (v2 + 1.0) * 3.0 + (v3 + 1.0) * 2.0) / 14.0; 
    fragColor = vec4(vec3(v),1.0);
