	vec2 uv = fragCoord.xy / iResolution.xy;
    float aspect = iResolution.x / iResolution.y;

    uv -= .5;
    uv.x = aspect * uv.x;

    vec3 camPos = vec3(2.0, 1.5, -2.) * iTime;
    vec3 camTarget = vec3(0.);
    vec3 camUp = vec3(0., 1., 0.);
    vec3 camDir = normalize(camTarget - camPos);
    vec3 camRight = normalize(cross(camUp, camDir));
    camUp = normalize(cross(camDir, camRight));
    vec3 rayPos = camPos;
    vec3 rayDir = normalize(camDir + uv.x * camRight + uv.y * camUp);

    float zNear = 10.0;
    rayPos += zNear * rayDir;

    vec3 c;
    vec3 n;
    bool hit = intersect(rayPos, rayDir, c, n);

	fragColor = vec4(c, 1.0);
}

