float checker(vec2 p){ return mod(floor(p.x)+floor(p.y), 2.);}
float stripe(float p, float h){return (abs(1.-mod(p,2.)) < h) ? 1. : 0.;}
float gradient(float p){return abs(1.-mod(p,2.));}

float IsGridLine(vec2 fragCoord, float rows)
{
    float sizeOfSquares = iResolution.y / rows;
	vec2 vPixelsPerGridSquare = vec2(sizeOfSquares, sizeOfSquares);
	vec2 vScreenPixelCoordinate = fragCoord.xy;
	vec2 vGridSquareCoords = fract(vScreenPixelCoordinate / vPixelsPerGridSquare);
	vec2 vGridSquarePixelCoords = vGridSquareCoords * vPixelsPerGridSquare;
	vec2 vIsGridLine = step(vGridSquarePixelCoords, vec2(1.0));
	float fIsGridLine = max(vIsGridLine.x, vIsGridLine.y);
	return fIsGridLine;
}




