/**
	Vizszintes eltolas + HSV eltolas random
	v1
	@param uv texturakoordinata
	@param shift szegmens y-eltolasa
	@param seed random seed
	@param speed random valtas sebessege
	@param segment segmens meret
	@return HSV transform (xyz) es vizszintes eltolas (w)
*/
vec4 distort(vec2 uv, float shift, float seed, float speed, float segment){
	vec4 transform = vec4(0.,2.,1.,0.);
	if (seed>0.){
		float 	f = hash(sin(uv.y)), dir, s, p, v, t = speed * iTime;

		v = hash(ceil(segment*uv.y));
		dir = hash(seed*v)>.5?-1.:1.;
		p = hash(fract(v + .0005*floor(t) ) );
		s = fract(seed*uv.y+dir*shift); 

		if(s-v>0.1)
			transform = vec4(
				360. *hash(v*v*p*p),				// H
				.1 + 2.* hash(seed*sin(v*v*p*p)),	// S
				.1 + 2.* hash(seed*cos(v*v*p*p)),	// V
				dir*(.1 * v * p));					// X-shift
		else if (s-v<-0.175)
			transform = vec4(
				180. *v*v*p*p,						// H
				.1 + 2.* hash(sin(v*v*p*p)),		// S
				.1 + 2.* hash(cos(v*v*p*p)),		// V
				dir*(.1 * v * p) + p*uv.x);			// X-shift
	}
	return transform;
}

