/**
************************************************
MEGBASZ TV CSIK
************************************************
*/

vec4 megbasz(vec2 uv, vec4 color) 
{ 
	//fragColor = texture(texture, gl_TexCoord[0].st); 
    vec4 res = color;
	float x      = uv.s * iResolution.x; 
	float y      = uv.t * iResolution.y; 
	float modx2  = mod(x,       2.0); 
	float mody3  = mod(y,       3.0); 
	float mody13 = mod(y - 1.0, 3.0); 
	float mody23 = mod(y - 2.0, 3.0); 
	if (modx2 < 1.0 && mody3  < 1.0 ) res *= vec4(1.0,0.0,0.0,1.0);  
	if (modx2 > 1.0 && mody3  < 1.0 ) res *= vec4(0.0,1.0,0.0,1.0); 
	if (modx2 < 1.0 && mody13 < 1.0 ) res *= vec4(0.0,0.0,1.0,1.0);  
	if (modx2 > 1.0 && mody13 < 1.0 ) res *= vec4(1.0,0.0,0.0,1.0); 
	if (modx2 < 1.0 && mody23 < 1.0 ) res *= vec4(0.0,1.0,0.0,1.0);  
	if (modx2 > 1.0 && mody23 < 1.0 ) res *= vec4(0.0,0.0,1.0,1.0);

    return res;
} 


