    float s = 0.5 + 8.0 * zp * ((0.8 + uvCos.x * 0.9) + (0.9 + (1.0 - -cos(uvCos.y + iTime)) * 1.3) + (0.9 + (1.0 - -cos(uvCos.x + iTime)) * 1.3) );
    uv.xy *= s;
    uv.x -= s;
    uv.y -= s * 0.5;


    float x = 0.5 + 0.5 * -cos(uv.x * Pi2 + iTime * 2.0)  * (uvCos.y + 1.0);
    x += 0.25 + 0.25 * -cos((uv.x + iTime * 0.2) * Pi2 * 2.5);
    float y = 0.5 + 0.5 * -cos(uv.y * Pi2);
    float blah = (0.5 + 0.5 * -cos(iTime * 0.134)) * (uv.x + iTime * 0.1);
    y += 0.25 + 1.0 * -cos(blah + uv.y * Pi2 * 0.1);

	fragColor = vec4(lookup(x * y * (1.1 - pow(zp, 0.125)) * 4.1 + iTime * 0.001), 1.0);
}

