void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ar = iResolution.x/iResolution.y;
	vec2 uv = (fragCoord.xy / iResolution.xy) * vec2(ar, 1);

    float zoom = 1.4;
    vec2  pos  = vec2(.5,.25);

    uv = uv * zoom - pos; // Position the flag
    vec2 pv = uv;

#ifdef ANIMATE // Wave animation    
    vec2 cv = uv;    
	pv.y = uv.y + (.3 + cv.x) * pow(sin(cv.x * 6. - iTime * 6.0), 2.) * .032;
    pv.x = uv.x + cv.y * cos(cv.x - cv.y * 2. - iTime * .5) * .05;
#endif

    vec3 col = vec3(0);

#ifdef SUPERSAMPLING
    // Antialiasing via supersampling
    float e = 1. / min(iResolution.y , iResolution.x) / zoom;
    for (float i = -SUPERSAMPLING; i < SUPERSAMPLING; ++i) {
        for (float j = -SUPERSAMPLING; j < SUPERSAMPLING; ++j) {
    		col += flag_jp(pv + vec2(i, j) * (e/SUPERSAMPLING)) 
                / (4.*SUPERSAMPLING*SUPERSAMPLING);
        }
    }
#else
     col = flag_jp(pv);
#endif

    float s = 1.;

#ifdef ANIMATE
    s = pow(dot(normalize(vec3(pv - uv, 1)), normalize(vec3(0,25, 4))),.4);
    #endif

	fragColor = vec4(col * s, 1);
}

vec3 hsv2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	return c.z * mix( vec3(1.0), rgb, c.y);
}

float uniformRand(vec2 p)
{ 
     return fract(975.121*sin(dot (vec2(p.x,p.y),vec2(321.654,654.987))))*0.5+0.5;
}

float noise1D(float p)
{
    float a = uniformRand( floor( vec2(p,0. )));
    float b = uniformRand( floor( vec2(p + 1.,0. ) ));
    float t = mix(a,b,fract(p));
    return t;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= vec2(0.5,0.5);
    vec2 oriuv = uv;
    uv.y *= 2.5;    
    uv += sin(uv.x * 10. * (uv.y * 1.11)  + iTime) * 0.15;

    oriuv = mix(oriuv,uv,0.1);
    float m = clamp((.37 - abs( uv.y )) * 3.,0.,1.);    
    vec3 V = hsv2rgb( vec3((uv.x * 0.1) + iTime * 0.25 ,1.,1.));
    V *=  m;    
    V *= 1. - (sin( uv.y * uv.y * 30. ) * .26);

    fragColor = vec4(V,1.0) * noise1D(oriuv.x * 50.+iTime*5.)* noise1D(oriuv.x * 40.+iTime);
}

vec3 F1(in float x, in float y, in float Scale, in float Speed)
{   
    x *= Scale*0.5+(0.5*sin(iTime*Speed));
    y *= Scale*0.5+(0.5*sin(iTime*Speed));

	float f = pow(x*x+y*y,3.0) - 27.0*x*x*y*y;
    float dx = 6.0*x*pow(x*x+y*y,2.0) - 54.0*x*y*y;
    float dy = 6.0*y*pow(x*x+y*y,2.0) - 54.0*x*x*y;
    return vec3(f,dx,dy);
}

vec3 F2(in float x, in float y, in float Scale, in float Speed)
{   
    x *= Scale*0.5+(0.5*sin(iTime*Speed));
    y *= Scale*0.5+(0.5*sin(iTime*Speed));

	float f = x*x+y*y-1.0;
    float dx = 2.0*x;
    float dy = 2.0*y;
    return vec3(f,dx,dy);
}

vec3 F3(in float x, in float y, in float Scale, in float Speed)
{   
    x *= Scale*0.5+(0.5*sin(iTime*Speed));
    y *= Scale*0.5+(0.5*sin(iTime*Speed));

	float f = pow(x*x-y,2.0) - pow(x,5.0);
    float dx = 4.0*x*(x*x-y) - 5.0*pow(x,4.0);
    float dy = 2.0*(x*x-y);
    return vec3(f,dx,dy);
}

vec3 F4(in float x, in float y, in float Scale, in float Speed)
{   
    x *= Scale*0.5+(0.5*sin(iTime*Speed));
    y *= Scale*0.5+(0.5*sin(iTime*Speed));

	float f = x*x*x-2.0*x*x-y*y+x;
    float dx = 3.0*x*x-4.0*x+1.0;
    float dy = -2.0*y;
    return vec3(f,dx,dy);
}

vec3 F5(in float x, in float y, in float Scale, in float Speed)
{   
    x *= Scale+(3.0*sin(iTime*Speed));
    y *= Scale+(3.0*sin(iTime*Speed));

	float f = pow(x*x+y*y-6.0*x,2.0)-x*x-y*y;
    float dx = (4.0*x-12.0)*(x*x+y*y-6.0*x)-2.0*x;
    float dy = 4.0*y*(x*x+y*y-6.0*x)-2.0*y;
    return vec3(f,dx,dy);
}

vec3 F6(in float x, in float y, in float Scale, in float Speed)
{   
    x *= Scale+(1.5*sin(iTime*Speed));
    y *= Scale+(1.5*sin(iTime*Speed));

	float f = x*x*x*x+y*y*y*y-8.0*x*x-10.0*y*y+16.0;
    float dx = 4.0*x*x*x-16.0*x;
    float dy = 4.0*y*y*y-20.0*y;
    return vec3(f,dx,dy);
}

vec3 F7(in float x, in float y, in float Scale, in float Speed)
{   
    x *= Scale*0.5+(0.5*sin(iTime*Speed));
    y *= Scale*0.5+(0.5*sin(iTime*Speed));

    float a = 0.75;
	float f = pow(x*x-y*y+a,2.0)-x*x/(y*y);
    float dx = 4.0*x*(x*x-y*y+a)-2.0*x/(y*y);
    float dy = -4.0*y*(x*x-y*y+a)+2.0*x*x/(y*y*y);
    return vec3(f,dx,dy);
}

vec3 F8(in float x, in float y, in float Scale, in float Speed)
{   
    x *= Scale*0.5+(0.5*sin(iTime*Speed));
    y *= Scale*0.5+(0.5*sin(iTime*Speed));

    float a = 1.0;
	float f = pow(x,2.0/3.0)+pow(y,2.0/3.0)-a;
    float dx = pow(x,-1.0/3.0);
    float dy = pow(y,-1.0/3.0);
    return vec3(f,dx,dy);
}

vec3 F9(in float x, in float y, in float Scale, in float Speed)
{   
    x *= Scale*0.75+(0.25*sin(iTime*Speed));
    y *= Scale*0.75+(0.25*sin(iTime*Speed));    

	float f = pow(x*x+y*y,2.0)-x*x+y*y;
    float dx = 4.0*x*(x*x+y*y)-2.0*x;
    float dy = 4.0*y*(x*x+y*y)+2.0*y;
    return vec3(f,dx,dy);
}

float Graph(in vec3 f, in float s)
{
    float Distance = abs(f.x)/length(s*f.yz);
    return smoothstep(0.1,0.15,Distance);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = (fragCoord.xy/iResolution.yy-vec2(0.5*iResolution.x/iResolution.y,0.5));

	vec2 grid = min(vec2(1.0,1.0),floor(abs(uv*min(iResolution.y,512.0))));

    float a = Graph(F1(uv.x,uv.y,8.0,2.0),0.16);

	float b = Graph(F2(uv.x,uv.y,5.0,2.0),0.09);

    float c = Graph(F3(uv.x,uv.y,5.0,2.0),0.08);

    float d = Graph(F4(uv.x,uv.y,5.0,2.0),0.07);

    float e = Graph(F5(uv.x,uv.y,10.0,2.0),0.3);

    float f = Graph(F6(uv.x,uv.y,8.0,2.0),0.2);      

    float g = Graph(F7(uv.x,uv.y,6.0,2.0),0.08);

    float h = Graph(F8(uv.x,uv.y,4.5,2.0),0.05);      

    float i = Graph(F9(uv.x,uv.y,2.0,2.0),0.05);    

    float m = floor(fract((iTime-9.0)/24.0)*9.0);

    float graph = a;

    if (m == 0.0)
    {graph = a;}

    else if (m == 1.0)
    {graph = b;}

    else if (m == 2.0)
    {graph = c;}

    else if (m == 3.0)
    {graph = d;}

    else if (m == 4.0)
    {graph = e;}

    else if (m == 5.0)
    {graph = f;}

    else if (m == 6.0)
    {graph = g;}

    else if (m == 7.0)
    {graph = h;}

    else
     graph = i;

    fragColor = vec4( mix(vec4(0.0,0.0,1.0,0.0),vec4(1.0),graph)*grid.x*grid.y);
}

// A "screen-saver" based on barycentric coordinates
// written 26.11.2014 by Jakob Thomsen
// (jakobthomsen@gmx.de)

// mirror/bounce inside -1,+1
vec2 mirror(vec2 pos)
{
    return (2.0 * abs(2.0 * fract(pos) - 1.0) - 1.0);
}

float light(vec2 pos, float size, vec2 uv)
{
    return exp(-pow(distance(uv, pos) / size, 2.0));
}

vec3 barycentric(vec2 a, vec2 b, vec2 c, vec2 p)
{
    float d = (b.y - c.y) * (a.x - c.x) + (c.x - b.x) * (a.y - c.y);
    float alpha = ((b.y - c.y) * (p.x - c.x)+(c.x - b.x) * (p.y - c.y)) / d;
    float beta = ((c.y - a.y) * (p.x - c.x) + (a.x - c.x) * (p.y - c.y)) / d;
    float gamma = 1.0 - alpha - beta;
    return vec3(alpha, beta, gamma);
}

vec3 inRange3(vec3 p)
{
    return step(p, vec3(1.0)) * step(vec3(0.0), p);
}

float inRangeAll(vec3 p)
{
    vec3 r = inRange3(p);

    return r.x * r.y * r.z;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 speed0 = vec2(0.0432, 0.0123);
    vec2 speed1 = vec2(0.0257, 0.0332);
    vec2 speed2 = vec2(0.0835, 0.0674);
    float size = 0.1;
	vec2 uv = (fragCoord.xy - iResolution.xy / 2.0) / iResolution.xy;
    uv *= 2.0;
    float val = 0.0;
    vec2 pos0 = mirror(iTime * speed0);
    vec2 pos1 = mirror(iTime * speed1);
    vec2 pos2 = mirror(iTime * speed2);
    vec3 bc = barycentric(pos0, pos1, pos2, uv);
    val = inRangeAll(bc);
    //val = mix(val, light(vec2(0.0), 1.0, uv), max(step(1.0, abs(uv.x)),step(1.0, abs(uv.y))));
    vec3 c = bc * val;
    c = c + vec3(1.0, 0.0, 0.0) * light(pos0, size, uv);
    c = c + vec3(0.0, 1.0, 0.0) * light(pos1, size, uv);
    c = c + vec3(0.0, 0.0, 1.0) * light(pos2, size, uv);

    fragColor = vec4(c, 1.0);
}

