/*sphere*/
float sphere(vec3 p, vec3 c, float r) {
    return distance(c, p) - r;
}

/*cylinder with spherical caps at ends*/
/* a, b - centres of the caps, r - radius */
float cylinder_caps(vec3 p, vec3 a, vec3 b, float r) {
    vec3 n = normalize(b - a);
    vec3 p1 = p - a;
    float d = dot(n, p1);
    vec3 c = d * n;
    if (dot(n, c) < 0.0) {
        return sphere(p, a, r);
    }
    if (dot(n, c) > distance(a, b)) {
        return sphere(p, b, r);
    }
    float daxis = length(p1 - d * n);
    return daxis - r;
}

float dist_object(vec3 p) {
    float t = smoothstep(-0.1, 0.6, p.y);
    float c = cylinder_caps(p, vec3(0, -0.9, 0), vec3(0, 0.9, 0), 0.3);
    float s = sphere(p, vec3(0, 0.3, 0), 0.9);
    float cutplane = 0.1-p.y;
    //s = max(s, cutplane);
    return mixfix(c, s, t);
}


/* gradient */
vec3 grad(vec3 p) {
    float eps = 0.0001;
    return normalize(vec3(
                dist_object(p - vec3(eps, 0.0, 0.0)) - dist_object(p + vec3(eps, 0.0, 0.0)),
                dist_object(p - vec3(0.0, eps, 0.0)) - dist_object(p + vec3(0.0, eps, 0.0)),
                dist_object(p - vec3(0.0, 0.0, eps)) - dist_object(p + vec3(0.0, 0.0, eps))));
}


const float epsilon = 2.0e-4;
#define MAXITERS 128
/* trace from point p along ray r */
vec4 trace(vec3 p, vec3 r) {
    vec3 p1 = p;
    float d = dist_object(p);
    float d1;
    for (int i = 0; i < MAXITERS; i++) {
        // escape if too long
        if (dot(p1 - p, p1 - p) > 16.0 * 16.0) {
            return vec4(p1, 0.0);
        }
        if (abs(d) < epsilon) {
            return vec4(p1, 1.0);
        }
        d1 = distance(p1, p);
        p1 = (d + d1) * r + p;
        d = dist_object(p1);
    }
    return vec4(p1, 0.0);
}

/* Get the minimal distance along a segment a to b
Returns < epsilon if an object is hit, > 0 otherwise
*/
float min_distance_along_segment(vec3 a, vec3 b) {
    vec3 p = a;
    vec3 r = normalize(b - a);
    float mindist = -1.0;
    float maxlength2 = dot(b - a, b - a);
    float d = dist_object(a);
    float d1;
    vec3 n;
    for (int i = 0; i < MAXITERS; i++) {
        n = grad(p);
        if (abs(d) < mindist || mindist < 0.0) {
            if (dot(n, r) < 0.0) {
                mindist = abs(d);
            }
        }
        if (dot(p - a, p - a) >= maxlength2) {
            break;
        }
        if (abs(d) < epsilon) {
            if (dot(n, r) < 0.0) {
            	break;
            }
        }
        d1 = distance(p, a);
        p = (d + d1) * r + a;
        d = dist_object(p);
    }
    return mindist;
}

float manhattan_distance(vec2 a, vec2 b) {
    vec2 c = a - b;
    return abs(c.x) + abs(c.y);
