vec3 texgen(vec3 p, vec3 n, vec3 ray) {
    // glTexGen GL_SPHERE_MAP
    return abs(n);
    vec3 u = normalize(p);
    vec3 f = u - 2.0 * dot(n, u) * n;
    f += vec3(0, 0, 1);
    float m = 2.0 * sqrt(dot(f, f));
    vec2 t = f.xy / m + vec2(0.5, 0.5);

    vec2 rep = fract(t * 4.0);
    float d = distance(vec2(0.5, 0.5), rep);
    float c = smoothstep(-0.4, -0.35, -d) * 0.5 + 0.5;
    return vec3(c, c, c);
}

