vec4 go(vec3 p, vec3 ray) {
    vec4 q = trace(p, ray);
    vec3 result;
    if (q.w < 1.0) {
        return vec4(0.0, 0.0, 0.0, 0.0);
    }
    p = q.xyz;
    return vec4(shade(p, ray), 1.0);
}

mat4 mkrotationm4(vec3 axis, float angle) {
    float c = cos(angle);
    float nc = 1.0 - c;
    float s = sin(angle);
    float v0 = axis.x;
    float v1 = axis.y;
    float v2 = axis.z;
    /* Formula copied from wikipedia
     * matrices are column-major so this looks transposed
     * with regards to what you'd find there */
    return mat4(
        vec4(v0 * v0 * nc + c, v1 * v0 * nc + v2 * s, v2 * v0 * nc - v1 * s, 0),
        vec4(v0 * v1 * nc - v2 * s, v1 * v1 * nc + c, v2 * v1 * nc + v0 * s, 0),
        vec4(v0 * v2 * nc + v1 * s, v1 * v2 * nc - v0 * s, v2 * v2 * nc + c, 0),
        vec4(0, 0, 0, 1));
}

vec3 srgb_from_rgb(vec3 rgb) {
    vec3 a = vec3(0.055, 0.055, 0.055);
    vec3 ap1 = vec3(1.0, 1.0, 1.0) + a;
    vec3 g = vec3(2.4, 2.4, 2.4);
    vec3 ginv = 1.0 / g;
    vec3 select = vec3(greaterThan(rgb, vec3(0.0031308, 0.0031308, 0.0031308)));
    vec3 lo = rgb * 12.92;
    vec3 hi = ap1 * pow(rgb, ginv) - a;
    return select * hi + (vec3(1.0, 1.0, 1.0) - select) * lo;
}

vec3 rgb_from_srgb(vec3 srgb) {
    vec3 a = vec3(0.055, 0.055, 0.055);
    vec3 ap1 = vec3(1.0, 1.0, 1.0) + a;
    vec3 g = vec3(2.4, 2.4, 2.4);
	vec3 select = vec3(greaterThan(srgb, vec3(0.04045, 0.04045, 0.04045)));
    vec3 lo = srgb / 12.92;
    vec3 hi = pow((srgb + a) / ap1, g);
    return select * hi + (vec3(1, 1, 1) - select) * lo;
}

