    uv = floor(uv*40.+80.*vec2(sin(iTime/2.),cos(iTime/2.)));
    float x = hash(fract(hash(.213*uv.x+.5*uv.y)+hash(.73*uv.y+7.)));

	fragColor = vec4(x,x,x,1.0);
}

