vec4 mv4D;

// From 4D to 3D 
vec3 line4D(in vec3 p, vec4 p1, vec4 dp) {
    p1+=mv4D;
    //dp+=mv4D;
 	vec3 pa = p-p1.xyz, ba = dp.xyz;
    float k = clamp(dot(pa,ba)/dot(ba,ba),0., 1.);
    return vec3(length(pa - ba*k) - RAYON ,k, p1.w +k*dp.w);
}

