/* Compiled with Shaderforth: https://github.com/daeken/Shaderforth
import[ noise ]

:globals
	@vec3 uniform =iResolution
	@float uniform =iTime
;

:m time iTime ;

iResolution frag->position !=p =op

:m light [ 0.81 0.80 0.70 ] ;
:m dark [ 0.19 0.15 0.06 ] ;

p time .37 * sin 2 ** 3 * abs .5 + * time 2 / sin 2 * rotate-2d [ time .17 * time .37 * ] 5 * + =p
&snoise-2d p gradient =v
1 op [ .1 .2 ] - length - abs v 2 ** * 0 v 0 < select =spec
light dark v 1 + 2 / mix spec abs .3 * + ->fragcolor
*/

vec2 rotate_2d(vec2 c, float a) {
	float ca = cos(a);
	float sa = sin(a);
	return vec2(c.x * ca - c.y * sa, c.y * ca + c.x * sa);
}
vec3 permute(vec3 x) {
	return mod((x * 34. + 1.) * x, 289.);
}
float snoise_2d(vec2 v) {
	vec4 C = vec4(.211324865405187, .366025403784439, -.577350269189626, .024390243902439);
	vec2 i = floor(dot(v, C.yy) + v);
	vec2 x0 = v - i + dot(i, C.xx);
	vec2 i1 = x0.x > x0.y ? vec2(1., 0.) : vec2(0., 1.);
	vec4 x12 = x0.xyxy + C.xxzz - vec4(i1, 0., 0.);
	i = mod(i, 289.);
	vec3 p = permute(permute(vec3(0., i1.y, 1.) + i.y) + i.x + vec3(0., i1.x, 1.));
	vec3 m = max(.5 - vec3(dot(x0, x0), dot(x12.xy, x12.xy), dot(x12.zw, x12.zw)), 0.);
	m = m * m * m * m;
	vec3 x = 2. * fract(p * C.www) - 1.;
	vec3 h = abs(x) - .5;
	vec3 ox = floor(x + .5);
	vec3 a0 = x - ox;
	m = (1.79284291400159 - .85373472095314 * (a0 * a0 + h * h)) * m;
	return dot(m, vec3(a0.x * x0.x + h.x * x0.y, a0.yz * x12.xz + h.yz * x12.yw)) * 130.;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 var_0 = (fragCoord.xy / iResolution.xy * 2. - 1.) * vec2(iResolution.x / iResolution.y, 1.);
	vec2 p = var_0;
	vec2 op = var_0;
	p = rotate_2d(p * (abs(pow(sin(iTime * .37), 2.) * 3.) + .5), sin(iTime / 2.) * 2.) + vec2(iTime * .17, iTime * .37) * 5.;
	vec2 h = vec2(.00001, 0.);
	float v = snoise_2d(p) / abs(length(vec2(snoise_2d(p + h) - snoise_2d(p - h), snoise_2d(p + h.yx) - snoise_2d(p - h.yx)) / (2. * h.x)));
	float spec = v < 0. ? abs(1. - length(op - vec2(.1, .2))) * pow(v, 2.) : 0.;
	fragColor = vec4(mix(vec3(.81, .80, .70), vec3(.19, .15, .06), (v + 1.) / 2.) + abs(spec) * .3, 1.);
}

float roundBox(vec2 coord, vec2 pos, vec2 b, float c ){
  return 1.-floor(length(max(abs(coord-pos)-b,c)));
}
float circle(vec2 coord, vec2 pos, float size){
    return min(floor(distance(coord,pos)-size),0.);
}
float sdCapsule( vec2 p, vec2 a, vec2 b, float r ){
    vec2 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return min(floor(length( pa - ba*h ) - r),0.);
}
mat2 rotate(float Angle)
{
    mat2 rotation = mat2(
        vec2( cos(Angle),  sin(Angle)),
        vec2(-sin(Angle),  cos(Angle))
    );
	return rotation;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 p = fragCoord.xy / iResolution.xy;
	float aspectCorrection = (iResolution.x/iResolution.y);
	vec2 coordinate_entered = 2.0 * p - 1.0;
	vec2 coord = vec2(aspectCorrection,1.0) *coordinate_entered;
    vec2 s = coord;
	float vignette = 1.0 / max(0.25 + 0.3*dot(coord,coord),1.);

	vec3 COLOR =(vec3(0.9,0.4,0.5)
        		+vec3(0.2,0.5,0.2) );
    if(mod(coord.y*200.+coord.x*200.,8.)<1.1){
        COLOR -= vec3(0.05);
    }else if(mod(coord.y*200.-coord.x*200.,8.)<1.1){
        COLOR -= vec3(0.05);
    }

    vec2 Xpos = vec2(1.1,-0.35);
    COLOR += 3.*vec3(0.0,-2.0,-0.0)*sdCapsule(coord, vec2(-0.15,-0.15)+Xpos, vec2( 0.15, 0.15)+Xpos, 0.025);
    COLOR += 3.*vec3(0.0,-2.0,-0.0)*sdCapsule(coord, vec2( 0.15,-0.15)+Xpos, vec2(-0.15, 0.15)+Xpos, 0.025);

    COLOR += 3.*vec3(0.1,-0.3,-0.3)*sdCapsule(coord, vec2(-1.7,-0.5), vec2( -0.7,-0.5), 0.025);
    COLOR += 3.*vec3(1.)*sdCapsule(coord, vec2( -1.7,0.3), vec2( -0.7,0.3), 0.025);
    COLOR -= 2.*vec3(1.)*sdCapsule(coord, vec2( -1.7,0.5), vec2( -0.7, 0.5), 0.025);

    float surface = texture(iChannel0, coord*length(coord)+vec2(sin(iTime),cos(iTime))).r;
    COLOR -= vec3(1.)*circle(coord, vec2(0.0), surface);
