    }
    if(total_circle<0.)
    	COLOR = vec3(1.9,0.97,0.4);
    vec2 position = vec2(0.0);
    vec2 size = vec2(6.,2.);
    float corner = 0.2;
    if( roundBox(coord*5., position, size, corner ) < 1.)
