    coord = vec2(mod(s.x,.999)-.333,mod(s.y,.75)-0.21);

	fragColor = vec4( texture(iChannel0,s).rgb*.04+.7*COLOR*vignette
         				,1.0);
}

float WardAnisotropy (in vec3 vNormal, in vec3 vDirection, in vec3 vEye, in vec3 vLight,in vec2 Roughness) 
{    
    vec3 A = normalize(vEye+vLight);

    float X = dot(A,normalize(cross(vNormal,vDirection)))/Roughness.x;

    float Y = dot(A,normalize(cross(vNormal,A)))/Roughness.y;

    float B = 1.0+dot(A,vNormal);

    float XY = -2.0*(pow(X,2.0)+pow(Y,2.0)) / B;

    float C = exp(XY) * (1.0/12.5664*Roughness.x*Roughness.y);

    return C * (1.0/sqrt(max(dot(vEye,vNormal),0.0)/max(dot(vLight,vNormal),0.0)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.yy;

    vec2 uvl = fragCoord.xy / iResolution.xy-0.5;

    //Cones  
    float s1 = 1.0-clamp(length(uv*4.0-vec2(1.5,1.2)),0.0,1.0);

    float s2 = 1.0-clamp(length(uv*4.0-vec2(5.67,1.2)),0.0,1.0);

    float s3 = 1.0-clamp(length(uv*4.0-vec2(3.67,2.8)),0.0,1.0);

    float sph = s1+s2+s3;

    float spm = clamp((s1+s2+s3)*96.0,0.0,1.0);


    //Normals
    float dx = dFdx(sph)*iResolution.x/15.0*spm;

    float dy = dFdy(sph)*iResolution.x/15.0*spm;

    vec3 vNormal = normalize(vec3(dx,dy,sqrt(clamp(1.0-dx*dx-dy*dy,0.0,1.0))));


    //Shading
    vec2 Roughness = vec2(0.2,0.8);

	vec3 Dir1 = normalize(vec3(fract(uv.x*4.0)-0.5,fract(uv.y*4.0)-0.5,0.0));

	vec3 Dir2 = normalize(vec3(0.0,1.0,0.0));

    vec3 Dir3 = normalize(vec3(uv.x*4.0-5.67,uv.y*4.0-1.2,0.0));

    vec3 vLight = normalize(vec3(uvl.x+(0.5*sin(iTime)),uvl.y+(0.5*cos(iTime)),0.5));

    vec3 vEye = vec3(0.0,0.0,1.0);

    float sh = clamp(dot(vNormal,vLight),0.0,1.0);

    vec3 Dir = mix(mix(Dir1,Dir2,spm),Dir3,min(s1*48.0,1.0));

    vec3 sp = WardAnisotropy(vNormal,Dir,vEye,vLight,Roughness)*vec3(24.0);

    vec3 Color = vec3(0.15)+vec3(0.45)*sh+sp;

    fragColor = vec4(Color,1.0);
}

#define M_PI_1_2 1.5707963267948966
#define M_PI_2_2 3.141592653589793
#define M_PI_3_2 4.71238898038469
#define M_PI_4_2 6.283185307179586

#define M_CORNER_1 2.356194490192345
#define M_CORNER_2 3.9269908169872414
#define M_CORNER_3 5.497787143782138

vec4 pal1 = vec4(0.7, 1.0, 1.0, 1.0);
vec4 pal2 = vec4(1.0, 0.7, 0.7, 1.0);

vec4 palette(float v)
{
    float at = mod(v, 1.0) * 2.0;
    float x = mod(at, 1.0);
    float y = floor(at);
    vec4 c1, c2;

    if (y < 1.0) {
        c1 = pal1; c2 = pal2;
    } else {
        c1 = pal2; c2 = pal1;
    }

    x *= M_PI_1_2;
    x = sin(x) * sin(x);

    return c1 * x + c2 * (1.0 - x);
}

vec4 background(float t, vec2 uv)
{
    return palette(
        sin(sin(3.0 * t) * sin((2.2 + sin(2.0 * t)) * (2.0 * uv.x + sin(t + uv.y))                  
			+ sin(1.2 * t)) + cos(4.0 * uv.y + sin(t)))
    );
}

float rotation(float t, float y)
{
    return 1.3 * sin(1.3 * t + 0.6 * y) + t;
}

vec2 decode(float rot)
{
    return vec2(
        mod(rot, M_PI_1_2),
        mod(rot / M_PI_1_2, 4.0)
    );
}

vec3 parameters(float rot, float ctr, float sz)
{
    return vec3(
        ctr + sz * cos(rot + M_CORNER_1),
        ctr + sz * cos(rot + M_CORNER_2),
        ctr + sz * cos(rot + M_CORNER_3)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float center = iResolution.x / 2.0;
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 rotface = decode(rotation(iTime, uv.y));
    float size = iResolution.x / 4.0;
    vec3 params = parameters(rotface.x, center, size);
    float face = 0.0;
    float facepos = 0.0;
    float norm = 0.0;
    if (fragCoord.x < params.x || fragCoord.x > params.z) {
        fragColor = background(iTime, uv);
        return;
    } else if (fragCoord.x < params.y) {
        face = mod(rotface.y + 1.0, 4.0);
        norm = (params.y - params.x) / size;
        facepos = (fragCoord.x - params.x) / (params.y - params.x);
    } else {
        face = rotface.y;
        norm = (params.z - params.y) / size;
        facepos = (fragCoord.x - params.y) / (params.z - params.y);
    }

    float vpos = sin(iTime + 0.2) + uv.y;

    vec4 color = texture(iChannel0, vec2(facepos, vpos));;
    if (face < 1.0) {
        color *= vec4(1.0, 0.4, 0.0, 1.0);
    } else if (face < 2.0) {
        color *= vec4(0.0, 1.0, 0.4, 1.0);
    } else if (face < 3.0) {
        color *= vec4(0.4, 0.0, 1.0, 1.0);
    } else {
        color *= vec4(1.0, 0.0, 0.4, 1.0);
    }

    vec4 ones = vec4(1.0,1.0,1.0,1.0);
    color = ones - (ones - color) * (ones - color);

    fragColor = (color + vec4(0.2,0.2,0.2,0.2)) * (0.1 + 0.6 * norm);

}

// Folding. Created by Reinder Nijhoff 2014
