#define MARCHSTEPS 250
#define PAPERHEIGHT 0.002
#define PI 3.1415926

float time;

//----------------------------------------------------------------------

vec3 RotateY( const in vec3 vPos, const in float fAngle ) {
    float s = sin(fAngle);
    float c = cos(fAngle);

    vec3 vResult = vec3( c * vPos.x + s * vPos.z, vPos.y, -s * vPos.x + c * vPos.z);

    return vResult;
}

vec3 RotateZ( const in vec3 vPos, const in float fAngle ) {
    float s = sin(fAngle);
    float c = cos(fAngle);

    vec3 vResult = vec3( c * vPos.x + s * vPos.y, -s * vPos.x + c * vPos.y, vPos.z);

    return vResult;
}

//----------------------------------------------------------------------
// distance primitives

float opS( float d2, float d1 ) { return max(-d1,d2); }

float sdBox( in vec3 p, in vec3 b ) {
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

//----------------------------------------------------------------------
// Map functions

vec3 fold( const in vec3 p, in float offset, const in float rot, in float a, const bool left ) {    
    a = clamp( a, -PI, PI );
    float b = PI-a;   
    vec3 rp = p;

    if( !left ) offset = -offset;

	rp.x -= offset;
    rp = RotateY( rp, rot );

	float angle = atan( rp.y, rp.x * (left?-1.:1.) );

    if( angle < 0. ) {
        if(  angle >  - b * 0.5 ) {
            rp = RotateZ( rp, a * (left?-1.:1.) );
        }
    } else {
        if( angle - a < b * 0.5 ) {
	        rp = RotateZ( rp, a * (left?-1.:1.) );
        }
    }

    rp = RotateY( rp, -rot );      
    rp += vec3(offset,0.,0.);


    return rp;
}

float timedAngle( const in float starttime, const in float totaltime, const in float angle ) {
	float i = clamp( time - starttime, 0., totaltime );
    return 3.1415926 * angle * i / totaltime;
}

float map( in vec3 p ) {

	// folding input domain
    p = fold( p, 0.,    0.,  		   timedAngle( 6., 2., 0.25), false );
    p = fold( p, 0.,    0.,  		   timedAngle(10., 2., 0.25), true );
    p = fold( p, -0.25,  0., 		   timedAngle( 8., 2.,-0.25), false  );
    p = fold( p, -0.25,  0., 		   timedAngle(12., 2.,-0.25), true  );

    if( time < 6.  ) 
    	p = fold( p, -1.4, PI*0.25,  timedAngle( 4., 2., -0.8 ) , true );
    if( time < 4.  ) 
		p = fold( p, -1.4, -PI*0.25, timedAngle( 2., 2., -0.8 ) , false );

    // just one paper plane
    float d = sdBox( p, vec3( 1., PAPERHEIGHT, 1.4) );

    if( time >= 6.  ) { // clip the plane hack :(
        vec3 po = p + vec3( 1.53, 0., 2.707 ); po = RotateY( po, PI*0.25 );
        d = opS( d, sdBox( po, vec3( 2., 1., 2. ) ) );
    } 

    if( time >= 4.  ) { // clip the plane hack :(
        vec3 po = p + vec3( -1.53, 0., 2.707 ); po = RotateY( po, PI*0.25 );
        d = opS( d, sdBox( po, vec3( 2., 1., 2. ) ) );
    } 

	return d;
}

//----------------------------------------------------------------------

vec3 calcNormal( in vec3 pos ) {
    const vec2 e = vec2(1.0,-1.0)*0.0025;

    vec3 n = normalize( e.xyy*map( pos + e.xyy ) + 
					    e.yyx*map( pos + e.yyx )   + 
					    e.yxy*map( pos + e.yxy )   + 
					    e.xxx*map( pos + e.xxx )   );  
    return n;
}

vec2 intersect( in vec3 ro, in vec3 rd ) {
	const float maxd = 60.0;
	const float precis = 0.001;
    float d = precis*2.0;
    float t = 0.;
    float m = 1.;

    for( int i=0; i<MARCHSTEPS; i++ ) {
	    d = 0.2 * map( ro+rd*t );
		t+=d;
        if( d<precis||t>maxd ) break;

    }

    if( t>maxd ) m=-1.0;
    return vec2( t, m );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {    
    time = mod( iTime + 8., 32. );
    vec2 q = fragCoord.xy / iResolution.xy;
	vec2 p = -1.0 + 2.0*q;
	p.x *= iResolution.x / iResolution.y;

    if (q.y < .12 || q.y >= .88) {
		fragColor=vec4(vec4(0.0));
		return;
	}

    if ( time > 16. ) { time = 32.-time; }

    //-----------------------------------------------------
    // camera
    //-----------------------------------------------------

	vec3 ro = vec3(0.,1.75 + 0.25*sin( iTime * 0.42 ), 3.);
    ro = RotateY( ro, iTime*0.05 );
    vec3 ta = vec3( 0. ,0., 0. );

    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
	vec3 rd = normalize( -p.x*uu + p.y*vv + 2.2*ww );


     vec3 col = vec3(0.01);

    // raymarch
    vec2 ints = intersect(ro ,rd );
    if(  ints.y > -0.5 ) {
        vec3 i = ro + ints.x * rd;
        vec3 nor =  calcNormal( i );
    	col = vec3(1.) * (0.1+0.9 * clamp(dot( nor, normalize(vec3(0.5, 0.8, 0.2))),0.,1.));
	}

    // gamma
	col = pow( clamp(col,0.0,1.0), vec3(0.4545) );


    fragColor = vec4( col, 1.0 );
}

// LED watch: the two bars on the left represent hours, the two on the
// right minutes. LEDs in the first bar count for 6, LEDs in the third
// bar count for 10; LEDs in the 2 other bars count for 1.

#define M_PI 3.1415926535897932384626433832795
#define VERTICAL
#define HSV2RGB_FAST

float boxDist (in vec2 p, in vec2 b) {
	return length (max (abs (p) - b, 0.0));
}

float boxDist (in vec2 p, in vec3 b) {
	return length (max (abs (p) - b.xy + b.z, 0.0)) - b.z;
}

vec3 hsv2rgb (in vec3 hsv) {
	#ifdef HSV2RGB_SAFE
	hsv.yz = clamp (hsv.yz, 0.0, 1.0);
	#endif
	#ifdef HSV2RGB_FAST
	return hsv.z * (1.0 + 0.5 * hsv.y * (cos (2.0 * M_PI * (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0))) - 1.0));
	#else
	return hsv.z * (1.0 + hsv.y * clamp (abs (fract (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0)) * 6.0 - 3.0) - 2.0, -1.0, 0.0));
	#endif
}

float rand (in vec2 seed) {
	return fract (sin (dot (seed, vec2 (12.9898, 78.233))) * 137.5453);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Get the fragment's position in the watch space
	#ifndef VERTICAL
	vec2 frag = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.x;
	#else
	vec2 frag = (2.0 * fragCoord.yx - iResolution.yx) / iResolution.y;
	#endif
	frag *= 1.0 - vec2 (0.02, 0.04) * cos (frag.yx * 2.0) * max (0.0, 50.0 * sin (iTime * M_PI * 2.0 / 5.0) - 49.0);
	#ifndef VERTICAL
	frag *= 8.0;
	#else
	frag *= vec2 (6.0, -6.0);
	#endif

	// Define the panel and the border
	vec3 panelSize = vec3 (5.0, 2.2, 1.0);
	float panelDist = -boxDist (frag, panelSize);
	float borderDist = boxDist (frag, panelSize + 0.1);

	// Define the LEDs
	vec2 led = frag;
	led.x -= 0.5;
	vec2 ledId = floor (led);
	float random = rand (ledId);
	float ledThresholdBar2 = step (-1.5, ledId.y);
	float ledThresholdBar4 = step (0.5, ledId.y);
	float ledTimeModulo = 10.0 - 4.0 * ledThresholdBar2 - 2.0 * ledThresholdBar4;
	float ledMode = max (0.0, cos (iTime * 0.5));
	float ledIntensity = mix (0.8 * step (ledId.x + 6.0, mod (iDate.w / (60.0 + 540.0 * ledThresholdBar2 + 3000.0 * step (-0.5, ledId.y) + 18000.0 * ledThresholdBar4), ledTimeModulo)), 0.5 + 0.5 * cos (random + random * iTime + iTime), ledMode);
	float ledDist = boxDist (fract (led) - 0.5, vec2 (0.2, 0.03));

	// Create everything (panel, casing, small indicator and LEDs)
	vec3 color = vec3 (0.2, 0.2, 0.2) * (0.7 + 0.3 * cos (frag.x * M_PI / panelSize.x)) * smoothstep (0.1, 0.15, panelDist) * smoothstep (0.2, 0.25, abs (fract (frag.y) - 0.5));
	color += (0.4 + 0.2 * cos (frag.x + frag.y + iTime)) * smoothstep (0.1, 0.0, max (borderDist, panelDist));
	color += 0.3 * smoothstep (0.05, 0.0, boxDist (frag + vec2 (0.0, 1.9), vec3 (0.04, 0.1, 0.04)));
	color += mix (vec3 (0.1) * smoothstep (0.025, 0.0, ledDist), hsv2rgb (vec3 (random + iTime * 0.1, ledMode, 1.0)) * smoothstep (0.25, 0.0, ledDist), ledIntensity) * step (-4.5, frag.x) * step (5.5 - ledTimeModulo, -frag.x) * step (-2.0, -abs (frag.y));

	// Get the fragment's position in the background tile space
	#ifndef VERTICAL
	frag = 15.0 * fragCoord.xy / iResolution.x;
	#else
	frag = 15.0 * fragCoord.yx / iResolution.y;
	#endif
	frag -= vec2 (iTime, sin (iTime));

	// Create the background tiles
	random = rand (floor (frag));
	color += hsv2rgb (vec3 (random, 1.0, 1.0)) * smoothstep (0.6, 0.0, length (fract (frag) - 0.5)) * (0.2 + 0.2 * cos (random + random * iTime + iTime)) * smoothstep (0.0, 0.2, borderDist);

	// Set the fragment color
	fragColor = vec4 (color, 1.0);
}

#define PI 3.141592

float hash(float n) {return fract(sin(n * 0.021) * 43758.5453123);}//from iq

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    vec2 mo = iMouse.xy / iResolution.xy;
    mo -= 0.5;
    mo.x *= iResolution.x / iResolution.y;

    float time = iTime * 0.5;

    float subd = 15.0;
    float radius = 0.5;
    vec3  color = vec3(0.0);

    float hsub = subd*2.0;
    vec2 grid = uv * subd;
    grid = vec2(floor(grid.x)/subd, floor(grid.y)/subd);
    vec2 gridC = vec2(grid.x + 1.0/hsub, grid.y+1.0/hsub);

    vec2 mDist = vec2(sin(time)*0.5, cos(time)*0.3) - grid;

    float weight = 0.0;
    if(length(mDist) < radius) {
        weight = smoothstep(0.0, 0.3, max(0.0, radius - length(mDist)));
    }

    float qbRadius = clamp(0.5*smoothstep(0.5, 0.1, length(mDist)), 0.2, 0.5);
    float qbWeight = 0.85*smoothstep(0.5, 0.1, length(mDist));   

    vec2 cellD = uv - gridC;
	float finalRadius = qbRadius/subd;
    float rings = 0.0;
    if(length(cellD) < qbRadius/subd) {
        float col = 0.0;
        col = smoothstep(1.0,0.9,length(cellD)/(qbRadius/subd));
        col -= 1.0*smoothstep(1.0,0.9,length(cellD)/(qbRadius/subd*qbWeight));

        rings += col;
    }

    color = 
        vec3(0.03) 
        + rings*vec3(grid.x+0.7, grid.y+0.7, 0.7)*0.1
        + rings*weight*vec3(grid.x+0.7, grid.y+0.7, 0.7)*2.0
        + weight*vec3(grid.x+0.5, grid.y+0.5, 0.5)*0.3;

    fragColor = vec4(color.rgb, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{        
    float t = iTime*.5;
    vec2 p = -1.0+2.0*fragCoord.xy/iResolution.xy;
    p *= .8;

    float x = p.x*cos(t)-p.y*sin(t);
    float y = p.x*sin(t)+p.y*cos(t);

    vec2 uv = 0.2*vec2(x,y)/abs(y*8.);
    uv.y += .01*iTime;
    uv.x += .1*iTime;

	float w = max(-0.3, 0.3-abs(y) );
    float w2 = max(-0.6, 0.4-abs(x) );
    vec4 tex1 = texture(iChannel0,uv*3.);
    vec4 tex2 = texture(iChannel2,uv*4.);

	fragColor = vec4( mix(tex1,tex2,sin(t)).xyz+w+w2, 1.0);

}

