void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 pos =   ( 2.0 * fragCoord.xy - iResolution.xy ) / iResolution.y;
  pos.y = 1.0 - pos.y - 1.0;

  vec3 camPos =  vec3(0.0, 0.0, 9.0);
  vec3 camDir = vec3(0.0, 0.0, -1.0);
  vec3 camUp = vec3(0.0, 1.0, 0.0);  
  vec3 camSide = cross(camDir, camUp);
  float focus = 1.8;//1.8

  vec3 rayDir = normalize(camSide*pos.x + camUp*pos.y + camDir/* *focus*/);

  float t = 0.0;
  vec2 object = vec2(1., 1.);
//------------------------------

  vec3 posOnRay = camPos;
//------------------------------ 
  for(int i=0; i<MAX_ITER; ++i)
  {

    object = renderFunction(posOnRay);
    t += object.x;
    posOnRay = camPos + t*rayDir;    
  }
//------------------------------ 

  if(abs(object.x) < 0.01)
  {
    vec3 materialColor = getmaterial(posOnRay.xyz, object.y); 
    vec3 normal = getNormal(posOnRay);                        
  //  vec3 lightDir = -rayDir;
  //  vec3 color = getlighting(posOnRay.xyz, normal, lightDir, materialColor);  

  vec3 lightDir =  vec3(5.0, 0.0, 4.0);
  vec3 color = getlightingPhong(posOnRay, camPos, normal, lightDir, materialColor); // ÐŸÐ¾ Ð¤Ð¾Ð½Ð³Ñƒ   

  fragColor = vec4(color, 1.0);
  }else
  {
   fragColor = vec4(0.5);// 
  }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;
	vec2 uv = (2.0*fragCoord.xy-iResolution.xy) / iResolution.x;

    vec2 c = uv;

    c.x += sin(time/5.0)/4.0;
    c.y += cos(time/3.0)/4.0;

    vec2 p = vec2(0,0);
    p.y = 1.0/length(c*4.0);
    float angle = atan(c.x,c.y);
    p.x = angle/4.0;
    p.y += time/8.0;
    vec4 color = mix(texture(iChannel0,p),texture(iChannel1,p),0.3);

  	vec4 water = vec4(1.0,0.5,0.0,1.0)*0.1;
    color = mix(color,water,smoothstep(0.0,0.08,sin(p.y*8.0+time*3.0)*0.02+(abs(angle)-2.0)));
    color *= min(1.0,(c.x*c.x+c.y*c.y));
	fragColor = color;
}

