vec3 light2 = normalize(vec3(.5, 0.2, 0.2));


const float pi = 3.14159265359;


vec3 trans(vec3 p)
{
  return mod(p, 8.0)-4.0;
}

mat3 rx, ry, rz;

float lengthN(vec3 v, float n)
{
  v*=rx*ry*rz;
  vec3 tmp = pow(abs(v), vec3(n));
  return pow(tmp.x+tmp.y+tmp.z, 1.0/n);
}

float distanceFunction(vec3 pos)
{
  //pos*=rz;  
  return lengthN(trans(pos), 4.0) - 1.0;
}

vec3 getNormal(vec3 p)
{
  const float d = 0.0001;
  return
    normalize
    (
      vec3
      (
        distanceFunction(p+vec3(d,0.0,0.0))-distanceFunction(p+vec3(-d,0.0,0.0)),
        distanceFunction(p+vec3(0.0,d,0.0))-distanceFunction(p+vec3(0.0,-d,0.0)),
        distanceFunction(p+vec3(0.0,0.0,d))-distanceFunction(p+vec3(0.0,0.0,-d))
      )
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    float movX = -100.+100.*sin(iTime*.02);
    float movY = -155.+155.*sin(iTime*.06);
    float movZ = -115.+115.*sin(iTime*.04);

  vec2 pos = (fragCoord.xy*2.0 -iResolution.xy) / iResolution.y;
  vec3 camPos = vec3(movX,movY,movZ);
  vec3 camDir = vec3(0.0, 0.0, -1.0);
  vec3 camUp = vec3(0.0, 1.0, 0.0);

  float radian = (iTime*pi)/180.0; 

  ry = mat3(cos(radian*12.), 0.0,-sin(radian*12.),
            0.0, 1.0, 0.0, 
            sin(radian*12.), 0.0, cos(radian*12.)  );

  rz = mat3(cos(radian*150.),-sin(radian*150.), 0.0,
            sin(radian*150.), cos(radian*150.), 0.0,
            0.0, 0.0, 1.0 );

  rx = mat3(1.0, 0.0, 0.0,
            0.0, cos(radian*13.), sin(radian*13.), 
            0.0,-sin(radian*13.), cos(radian*13.) );


  camDir *= rx*ry;//*ry;//*rx;
  camUp  *= rx*ry;//*ry;//*rx;

  vec3 camSide = cross(camDir, camUp);
  float focus = 1.8;

  vec3 rayDir = normalize(camSide*pos.x + camUp*pos.y + camDir*focus);

  float t = 0.0, d;
  vec3 posOnRay = camPos;

  for(int i=0; i<48; ++i)
  {
    d = distanceFunction(posOnRay);
    t += d;
    posOnRay = camPos + t*rayDir;
  }

  vec3 normal = getNormal(posOnRay);

  if(abs(d) < 0.001)
  {  
    //https://www.shadertoy.com/view/4s2XRd
    float diffuse = clamp( dot(normal, light), 0., 1.);
    float specular = 1.5*pow(clamp(dot(reflect(rayDir, normal), light), 0., 1.), 180.);
    float specular2 = 1.5*pow(clamp(dot(reflect(rayDir, normal), light2), 0., 1.), 200.);
	vec3 brdf = 1.5*vec3(.10, .11, .11);
	float fre = 0.1*pow(clamp(1. + dot(normal, rayDir), 0., 1.), 2.);
    brdf += 1.30*diffuse*vec3(1., .9, .75);

	fragColor = vec4(normal+specular+specular2+fre+brdf, 1.0);
  }else
  {
    fragColor = vec4(0.0);
  }
}

// Created by Vinicius Graciano Santos - vgs/2014
// This shader renders an approximation of a cubic Bezier using the de Casteljau's algorithm.
// It splits the bezier into multiple smaller ones and renders their control points connected with line segments.

struct Bezier {
    vec2 a, b, c, d;
};

// splits a bezier B (t = 0.5) into beziers L and R.
void decasteljau(in Bezier B, out Bezier L, out Bezier R) {
	L.a = B.a;          R.d = B.d;
    L.b = .5*(B.a+B.b); R.c = .5*(B.c+B.d); 
    vec2 tmp = .5*(B.b+B.c);
    L.c = .5*(L.b+tmp); R.b = .5*(R.c+tmp);
    L.d = R.a = .5*(L.c+R.b);
}

// distance to the control points.
float distCP(vec2 p, Bezier B) {
    return min(min(length(p - B.a), length(p - B.b)),
               min(length(p - B.c), length(p - B.d)));
}

// distance to a line segment.
float segment(vec2 p, vec2 a, vec2 b) {
    vec2 ap = p - a, ab = b - a;
    float k = clamp(dot(ap, ab)/dot(ab,ab), 0.0, 1.0);
    return length(ap - k*ab);
}

// distance to the linear approximation.
float distLinear(vec2 p, Bezier B) {
    return min(min(segment(p, B.a, B.b), segment(p, B.b, B.c)), segment(p, B.c, B.d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (-iResolution.xy + 2.0*fragCoord.xy) / iResolution.y;
    vec2 st = fragCoord.xy / iResolution.xy;
    float c = cos(iTime), s = sin(iTime);

    // Control points (change these to play with the curve).
	vec2 PA = vec2(-1.5*c, .8*s);
	vec2 PB = iMouse.z > 0.0 ? (-iResolution.xy + 2.0*iMouse.xy) / iResolution.y : vec2(.5);
	vec2 PC = vec2(-c*s, -.8*c);
	vec2 PD = vec2(1.5*s, 0.);

    Bezier bezier[16]; 
    bezier[0] = Bezier(PA, PB, PC, PD);
    float dCP  = distCP(uv, bezier[0]), dLCP = distLinear(uv, bezier[0]);

    // just an unrolled recursion, yey! :D
    decasteljau(bezier[0], bezier[0], bezier[8]);    decasteljau(bezier[0], bezier[0], bezier[4]);
    decasteljau(bezier[8], bezier[8], bezier[12]);   decasteljau(bezier[0], bezier[0], bezier[2]);
    decasteljau(bezier[4], bezier[4], bezier[6]);    decasteljau(bezier[8], bezier[8], bezier[10]);
    decasteljau(bezier[12], bezier[12], bezier[14]); decasteljau(bezier[0], bezier[0], bezier[1]);
    decasteljau(bezier[2], bezier[2], bezier[3]);    decasteljau(bezier[4], bezier[4], bezier[5]);
    decasteljau(bezier[6], bezier[6], bezier[7]);    decasteljau(bezier[8], bezier[8], bezier[9]);
    decasteljau(bezier[10], bezier[10], bezier[11]); decasteljau(bezier[12], bezier[12], bezier[13]);
    decasteljau(bezier[14], bezier[14], bezier[15]);

	float dLA = 100.0;
    for (int i = 0; i < 16; ++i)
        dLA = min(dLA, distLinear(uv, bezier[i]));

    // shading.
    vec3 blue   = pow(vec3(189., 199., 255.)/255., vec3(2.2));
    vec3 green  = pow(vec3(194., 255., 148.)/255., vec3(2.2));
    vec3 dgreen = pow(vec3(125., 178., 86. )/255., vec3(2.2));

    vec3 col = mix(green, dgreen, smoothstep(0.005, 0.01, dLCP));
    col = mix(blue, col, smoothstep(0.01, 0.015, dLA));
    col = mix(green, col, smoothstep(0.05, 0.06, dCP));
    col = mix(col, green, pow(st.x*st.y*(1.0-st.x)*(1.0-st.y), .3));
    col = smoothstep(0.0, 1.0, col);

	fragColor = vec4(pow(col, vec3(1.0/2.2)),1.0);
}

