// Analytical ambient occlusion of a triangle. Left side of screen, stochastically 
// sampled occlusion. Right side of the screen, analytical solution (no rays casted).
//
// If the polygons was intersecting the ground plane, we'd need to perform clipping
// and use the resulting triangles for the analytic formula instead.


// Other shaders with analytical occlusion or approximations:
// 
// Box:                        https://www.shadertoy.com/view/4djXDy
// Box with horizon clipping:  https://www.shadertoy.com/view/4sSXDV
// Triangle:                   https://www.shadertoy.com/view/XdjSDy
// Sphere:                     https://www.shadertoy.com/view/4djSDy
// Ellipsoid (approximation):  https://www.shadertoy.com/view/MlsSzn
// Capsule (approximation):    https://www.shadertoy.com/view/llGyzG


// Other triangle functions:
//
// Occlusion:    https://www.shadertoy.com/view/XdjSDy
// Distance:     https://www.shadertoy.com/view/4sXXRN
// Intersection: https://www.shadertoy.com/view/MlGcDz
// Closest:      https://www.shadertoy.com/view/ttfGWl

//=====================================================

// Triangle intersection. Returns { t, u, v }
vec3 triIntersect( in vec3 ro, in vec3 rd, in vec3 v0, in vec3 v1, in vec3 v2 )
{
    vec3 a = v0 - v1;
    vec3 b = v2 - v0;
    vec3 p = v0 - ro;
    vec3 n = cross( b, a );
    vec3 q = cross( p, rd );

    float idet = 1.0/dot( rd, n );

    float u = dot( q, b )*idet;
    float v = dot( q, a )*idet;
    float t = dot( n, p )*idet;

    if( u<0.0 || u>1.0 || v<0.0 || (u+v)>1.0 ) t = -1.0;

    return vec3( t, u, v );
}

// Triangle occlusion (if fully visible)
float triOcclusion( in vec3 pos, in vec3 nor, in vec3 v0, in vec3 v1, in vec3 v2 )
{
    vec3 a = normalize( v0 - pos );
    vec3 b = normalize( v1 - pos );
    vec3 c = normalize( v2 - pos );

    float s = sign(dot(v0-pos,cross(v1-v0,v2-v1))); // side of the triangle

    return s*(dot( nor, normalize( cross(a,b)) ) * acos( dot(a,b) ) +
              dot( nor, normalize( cross(b,c)) ) * acos( dot(b,c) ) +
              dot( nor, normalize( cross(c,a)) ) * acos( dot(c,a) ) ) / 6.2831;
}

//=====================================================

vec2 hash2( float n ) { return fract(sin(vec2(n,n+1.0))*vec2(43758.5453123,22578.1459123)); }

float iPlane( in vec3 ro, in vec3 rd )
{
    return (-1.0 - ro.y)/rd.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord.xy-iResolution.xy) / iResolution.y;
    float s = (2.0*iMouse.x-iResolution.x) / iResolution.y;
    if( iMouse.z<0.001 ) s=0.0;

	vec3 ro = vec3(0.0, 0.0, 4.0 );
	vec3 rd = normalize( vec3(p,-2.0) );

    // triangle animation
    vec3 v1 = cos( iTime + vec3(2.0,1.0,1.0) + 0.0 )*vec3(1.5,1.0,1.0);
	vec3 v2 = cos( iTime + vec3(5.0,2.0,3.0) + 2.0 )*vec3(1.5,1.0,1.0);
	vec3 v3 = cos( iTime + vec3(1.0,3.0,5.0) + 4.0 )*vec3(1.5,1.0,1.0);

    vec4 rrr = texture( iChannel0, (fragCoord.xy)/iChannelResolution[0].xy, -99.0  ).xzyw;


    vec3 col = vec3(0.0);

    float tmin = 1e10;

    float t1 = iPlane( ro, rd );
    if( t1>0.0 )
    {
        tmin = t1;
        vec3 pos = ro + tmin*rd;
        vec3 nor = vec3(0.0,1.0,0.0);
        float occ = 0.0;

        if( p.x > s )
        {
            occ = triOcclusion( pos, nor, v1, v2, v3 );
        }
        else
        {
   		    vec3  ru  = normalize( cross( nor, vec3(0.0,1.0,1.0) ) );
		    vec3  rv  = normalize( cross( ru, nor ) );

            occ = 0.0;
            for( int i=0; i<256; i++ )
            {
                vec2  aa = hash2( rrr.x + float(i)*203.1 );
                float ra = sqrt(aa.y);
                float rx = ra*cos(6.2831*aa.x); 
                float ry = ra*sin(6.2831*aa.x);
                float rz = sqrt( 1.0-aa.y );
                vec3  dir = vec3( rx*ru + ry*rv + rz*nor );
                vec3 res = triIntersect( pos+nor*0.001, dir, v1, v2, v3 );
                occ += step(0.0,res.x);
            }
            occ /= 256.0;
        }

        col = vec3(1.0);
        col *= 1.0 - occ;
    }

    vec3 res = triIntersect( ro, rd, v1, v2, v3 );
    float t2 = res.x;
    if( t2>0.0 && t2<tmin )
    {
        tmin = t2;
        float t = t2;
        vec3 pos = ro + t*rd;
        vec3 nor = normalize( cross( v2-v1, v3-v1 ) );
		col = vec3(1.0,0.8,0.5);
        col *= 1.5*texture( iChannel1, res.yz ).xyz;
        col *= 0.6 + 0.4*nor.y;
	}

	col *= exp( -0.05*tmin );

    float e = 2.0/iResolution.y;
    col *= smoothstep( 0.0, 2.0*e, abs(p.x-s) );

    fragColor = vec4( col, 1.0 );
}

#define BIG_FLOAT 1e10
#define SMALL_FLOAT 1e-5
#define PI 3.14159

#define STATIC_TEXTURE 1
#define ANIMATED_NOISE 2
#define SLIDING_PROJECTION 3

#define PROJECTION_TYPE ANIMATED_NOISE

//----------------------------------------------------------------------------------------
// Thanks to Dave Hoskins
vec3 hash32(vec2 p)
{
	p  = fract(p * vec2(5.3983, 5.4427));
    p += dot(p.yx, p.xy +  vec2(21.5351, 14.3137));
	return fract(vec3(p.x * p.y * 95.4337, p.x * p.y * 97.597, p.x * p.y * 93.8365));
}

// XXX: To get around a case where a number very close to zero can result in 
// eradic behavior with sign, we assume a positive sign when a value is 
// close to 0.
float ztsign(float value)
{
    float s = 1.;
    if (abs(value) > SMALL_FLOAT) {
        s = sign(value);
    }
    return s;   
}

// intersection for an enclosing sphere with a ray.
// Returns a vec3 where:
//  result.x = number of intersections taking place
//  result.y = tmin
//  result.z = tmax

vec3 intersect_sphere(vec3 ro,                 
                             vec3 rd, 
                             float r,
                             vec3 sc)
{
    // Calculate the ray origin in object space of the sphere
    vec3 oro = ro - sc;

    // We don't consider an intersection if the ray is inside the sphere

    // DEBRANCH
    // Equivalent to:
    // if (dot(ospaceRayOrigin, ospaceRayOrigin) < radius*radius) {
    //     return vec3(0.);
    // }
    float inside = step(dot(oro, oro), r*r);

    float a = dot(rd, rd);
    float b = dot(oro, rd);
    float c = dot(oro, oro) - r*r;
    float discr = b*b - a*c; // discriminant

    // DEBRANCH
    // Equivalent to:
    // if (discr > 0.) {
    //     ...
    // }

    float isdiscrgtZero = step(0., discr);

    // Real root of disc, so intersection
    float sdisc = sqrt(abs(discr));
    float tmin = (-b - sdisc)/a;
    float tmax = (-b + sdisc)/a; 

    float hit = step(0., tmin) + step(0., tmax);

    return step(0., isdiscrgtZero) * vec3(hit, tmin, tmax);
}


// Returns a vec2 where:
//   result.x = 1. or 0. if there is a hit
//   result.y = t such that origin + t*dir = hit point
vec2 intersect_plane(vec3 ro,
                     vec3 rd,
                     vec3 pn,
                     vec3 po)
{
    float rddotpn = dot(rd, pn);
    // if the ray direction is parallel to the plane, let's just treat the 
    // ray as intersecting *really* far off, which will get culled as a
    // possible intersection.

    float denom = ztsign(rddotpn) * max(abs(rddotpn), SMALL_FLOAT);
    float t = min(BIG_FLOAT, -dot(pn, (ro - po)) / denom);    
    return vec2(step(SMALL_FLOAT, t), t);
}

vec3 rotate_look(vec3 l)
{
    float y = .05 * iTime;
    if (iMouse.y/iResolution.y > .1)
    {
        y = -PI * iMouse.x/iResolution.x;
    }
    return l * mat3(cos(y), 0., sin(y),
                    0.,     1., 0.,
                   -sin(y), 0., cos(y));
}

vec3 texture_cube(vec3 usphp, float scale)
{
    // cheap way of turning a 2D texture into a cube
    // map lookup by ray casting sphere intersection direction 
    // to the appropriate plane of the surrounding cube and then
    // using the uv of that plane intersection as a 2d vector.

    vec3 p = usphp;
#if PROJECTION_TYPE == SLIDING_PROJECTION
    p.y -= sin(.1 * iTime);
#endif

    float ml = max(abs(p.x), max(abs(p.y), abs(p.z)));
    vec3 ansphp = abs(p/ml); 

    // select the plane offset of a unit cube
    vec3 upo = sign(p) * step(vec3(1. - SMALL_FLOAT), ansphp);

    // scale the plane we are intersecting by the offset
    vec2 pr = intersect_plane(vec3(0.), p, -upo, scale * upo);
    vec3 pp = pr.y * p;

    // get the uv lookup of the plane intersection.
    vec2 uv = step(1. - SMALL_FLOAT, ansphp.x) * pp.yz;
    uv += step(1. - SMALL_FLOAT, ansphp.y) * pp.xz;
    uv += step(1. - SMALL_FLOAT, ansphp.z) * pp.xy;

#if PROJECTION_TYPE == STATIC_TEXTURE | PROJECTION_TYPE == SLIDING_PROJECTION

    // filter texture lookup more when unit cube is closer to
    // unit sphere - cheap hack to compensate for the fact
    // that the texture lookup is more stretched near the
    // corners.
    float f = 1.2 * (1. - length(pp/scale - p));

    return texture(iChannel0, .5 * uv + .5, f).rgb;
#elif PROJECTION_TYPE == ANIMATED_NOISE

    return hash32(.5 * uv + .5);

#endif

}

// **************************************************************************
// SCENE MARCHING

float spheredf( vec3 p, float r )
{
    return length(p) - r;    
}

float yplanedf( vec3 p, float offset )
{
    return abs(p.y - offset);
}

float cyldf( vec3 p, float r)
{
    return - (length(p.xz) - r);
}

float scenedf( vec3 pos )
{
    float dist = BIG_FLOAT;

    for (float i = 0.; i < 12.; i += 1.)
    {
        float s = PI * i/6.;
        float r = 5. + 5. * (.5 * cos(PI * i/2. + .2 * iTime) + .5);
        vec3 sphc = vec3(r * cos(s), .7, r * sin(s));
        dist = min(spheredf(pos - sphc, 1.), dist);
    }

    dist = min(yplanedf(pos, -1.3), dist);
	dist = min(cyldf(pos, 15.), dist);
    return dist;
}

#define DISTMARCH_STEPS 50
#define DISTMARCH_MAXDIST 20.

vec3 surface_normal( vec3 p )
{
    vec3 epsilon = vec3( 0.001, 0.0, 0.0 );
    vec3 n = vec3(
        scenedf(p + epsilon.xyy) - scenedf(p - epsilon.xyy),
        scenedf(p + epsilon.yxy) - scenedf(p - epsilon.yxy),
        scenedf(p + epsilon.yyx) - scenedf(p - epsilon.yyx) );
    return normalize( n );
}


#define SOFTSHADOW_STEPS 30
#define SOFTSHADOW_STEPSIZE .3

float soft_shadow( vec3 ro, 
                   vec3 rd, 
                   float mint, 
                   float maxt, 
                   float k )
{
    float shadow = 1.0;
    float t = mint;

    for( int i=0; i < SOFTSHADOW_STEPS; i++ )
    {
        if( t < maxt )
        {
            float h = scenedf( ro + rd * t );
            shadow = min( shadow, k * h / t );
            t += SOFTSHADOW_STEPSIZE;
        }
    }
    return clamp( shadow, 0.0, 1.0 );

}

#define AO_NUMSAMPLES 5
#define AO_STEPSIZE .3
#define AO_STEPSCALE .7

float ambient_occ( vec3 p, 
		          vec3 n )
{
    float ao = 0.0;
    float aoscale = 1.0;

    for( int aoi=0; aoi < AO_NUMSAMPLES ; aoi++ )
    {
        float step = 0.01 + AO_STEPSIZE * float(aoi);
        vec3 aop =  n * step + p;

        float d = scenedf( aop );
        ao += -(d-step)*aoscale;
        aoscale *= AO_STEPSCALE;
    }

    return clamp( ao, 0.0, 1.0 );
}


vec2 dist_march( vec3 ro, vec3 rd, float maxd )
{

    float epsilon = 0.001;
    float dist = 10. * epsilon;
    float t = 0.;
    for (int i=0; i < DISTMARCH_STEPS; i++) 
    {
        if ( abs(dist) < epsilon || t > maxd ) break;
        t += dist;
        dist = scenedf( ro + t * rd );
    }


    float objhit = 0.;
    if( t < maxd ) 
    {
        objhit = 1.;
    }

    return vec2(objhit, t);
}

vec3 shade_scene( vec3 ip, vec3 ro, vec3 rd )
{
    vec3 lpos = 4. * vec3(0., 1., 0.);
    vec3 n = surface_normal( ip );
    vec3 ldir = normalize(lpos - ip);

    float s = soft_shadow( ip, ldir, .02, 10., 10.);
    float ao = ambient_occ( ip, n );

    float mody = mod(4. * ip.y, 1.);
    float stripes = step(length(ip.xz), 14.) * (smoothstep(0., .05, mody) - smoothstep(.5, .55, mody));
    vec3 scol =  mix(vec3(.5, .6, 1.), vec3(.2, .2, .4), stripes);

    float falloff = pow(max(0., 1. - length(lpos - ip)/19.), 2.);

    vec3 diff = 5. * scol * max(0., dot(ldir, n));
    vec3 spec = vec3(2.) * pow(max(0., dot(ldir, reflect(rd, n))), 8.);
    vec3 amb = scol * .8 * (1. - ao);
    vec3 bnc = scol * .6 * pow(ip.y + 1.3,2.) * max(0., dot(n, normalize(ro - ip + vec3(0., -8., 0.))));

    return falloff * (s * (diff + spec) + 
           		      (amb + bnc));
}

vec3 renderEye( in vec3 fragRayOri, in vec3 fragRayDir, in vec2 uv )
{
    vec3 scol = vec3(0.);

    vec3 ro = fragRayOri;
    vec3 rd = fragRayDir;
    vec2 dr = dist_march(ro, rd, DISTMARCH_MAXDIST);

    // shade
    if (dr.x > 0.5)
    {
	    vec3 ip = ro + rd * dr.y;
        scol = shade_scene(ip, ro, rd);

        // post effect projection
        vec3 sphc = vec3(0.);
        vec3 sphr = intersect_sphere(ro, rd, 1. * length(ip), sphc);     
        vec3 sphp = ro + sphr.z * rd;

        scol *= .5 + .5 * texture_cube(normalize(sphp - sphc), 5.);
		//scol = pow(scol, vec3(1.) + .5 * texture_cube(normalize(sphp - sphc), 5.));

        // gamma
	    scol = pow(scol, vec3(.5));
    }    

    // spherical vignette
    scol *= pow(max(1. - length(uv),0.), 1.);

    return scol;   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy / iResolution.xy - .5;
    uv.y *= iResolution.y / iResolution.x;

    // camera
    vec3 f = rotate_look(vec3(0., 0., 1.));
    vec3 u = vec3(0., 1., 0.);

    vec3 cx = normalize(cross(f, u));
    vec3 cy = cross(cx, f);
    vec3 cz = f;

    vec3 ro = vec3(0.);
    vec3 rd = normalize(uv.x * cx + uv.y * cy + 1. * cz);

    vec3 scol = vec3(0.);

    ro = 1. * vec3(2. * pow((.5 * sin(.5 * iTime) + .5), 4.) - 1., 
                   1. * pow((.5 * cos(.8 * iTime + 1.) + .5), 4.), 
			        0.);

    vec3 scene_color = renderEye(ro, rd, uv);

    fragColor.rgb = scene_color;
    fragColor.a = 1.;
}

void mainVR( out vec4 fragColor, in vec2 fragCoord, in vec3 fragRayOri, in vec3 fragRayDir )
{
    vec2 uv = fragCoord.xy / iResolution.xy - .5;
    vec3 col = renderEye( fragRayOri + vec3(0.,1.,0.), fragRayDir, uv );

    fragColor = vec4( col, 1.0 );
}

