vec3 mod289(vec3 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec2 mod289(vec2 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec3 permute(vec3 x) {
  return mod289(((x*34.0)+1.0)*x);
}

float snoise(vec2 v)
  {
  const vec4 C = vec4(0.211324865405187,  // (3.0-sqrt(3.0))/6.0
                      0.366025403784439,  // 0.5*(sqrt(3.0)-1.0)
                     -0.577350269189626,  // -1.0 + 2.0 * C.x
                      0.024390243902439); // 1.0 / 41.0
// First corner
  vec2 i  = floor(v + dot(v, C.yy) );
  vec2 x0 = v -   i + dot(i, C.xx);

// Other corners
  vec2 i1;
  //i1.x = step( x0.y, x0.x ); // x0.x > x0.y ? 1.0 : 0.0
  //i1.y = 1.0 - i1.x;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  // x0 = x0 - 0.0 + 0.0 * C.xx ;
  // x1 = x0 - i1 + 1.0 * C.xx ;
  // x2 = x0 - 1.0 + 2.0 * C.xx ;
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;

// Permutations
  i = mod289(i); // Avoid truncation effects in permutation
  vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
		+ i.x + vec3(0.0, i1.x, 1.0 ));

  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy), dot(x12.zw,x12.zw)), 0.0);
  m = m*m ;
  m = m*m ;

// Gradients: 41 points uniformly over a line, mapped onto a diamond.
// The ring size 17*17 = 289 is close to a multiple of 41 (41*7 = 287)

  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;

// Normalise gradients implicitly by scaling m
// Approximation of: m *= inversesqrt( a0*a0 + h*h );
  m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );

// Compute final noise value at P
  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}

float fancyStep(vec2 position, float x, float width, float offset) {
    float stepCenter = snoise(position.xy*0.01) * (width*(sin(iTime *0.5)*1.5+1.)) + 0.5 + offset;
    float stepWidth = snoise(position.xy*0.01 + 289.) * 0.3 + 0.4;
    return smoothstep( stepCenter - stepWidth, stepCenter + stepWidth, x);
}

float striation(vec2 position, float time) {
    return sin(position.y*0.17 + 
        0.2*sin(position.x*0.1 - time*10.) +
        1.*snoise(position.xy*0.01 + time*0.1) +
        2.*snoise(position.x*0.007 - position.y*0.003 + vec2(289.,1232.) ) +
        8.*snoise(position.xx*0.001 + position.yy * 0.001 - time * 0.1)
        );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    float t = iTime * 0.3;
    float scale = 1.0 / (iResolution.x * 0.001);
    vec2 position = scale * (fragCoord.xy - iResolution.xy/2. + vec2(t *100.,0.) + 50.*vec2(sin(t )*0.4, cos(t *0.4)*3.2));

    float red = fancyStep(position, striation(position, t -0.0), -1.2, -0.6);
    float green = fancyStep(position, striation(position, t ), 1.1, 0.0);
    float blue = fancyStep(position, striation(position, t +0.0), 2.8, 0.5);
    fragColor = vec4( red, green, blue, 1.0 );

}

float cube_width = 20.0;
float wave_width = 2.5;
float PI = 3.1415926;
vec4 rainbow_color(int i)
{
    vec4 c;

	if (i == 0) c=vec4(0,0,0,0); else
	if (i == 1) c= vec4(255,43,14,255)/255.0; else
	if (i == 2) c= vec4(255,168,6,255)/255.0; else
	if (i == 3) c= vec4(255,244,0,255)/255.0; else
	if (i == 4) c= vec4(51,234,5,255)/255.0; else
	if (i == 5) c= vec4(8,163,255,255)/255.0; else
	if (i == 6) c= vec4(122,85,255,255)/255.0; else
		c=vec4(0,0,0,0);
	return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = 1.0 - 2.0 * uv;

    //wave
    vec4 wave_color = vec4(0.);
    for(int i = 0;i<7;++i)
    {
    	float y = 0.1 * sin(uv.x*PI + float(i)/3. + iTime*PI ) + uv.y+float(i)/10.0 - 0.5;
    	float wave = abs(1.0 / (y * iResolution.y /wave_width));
    	wave_color += rainbow_color(i)*wave;
    }

    //back ground
    float c1 = mod(fragCoord.x,2.0 * cube_width);
    c1 = smoothstep(cube_width*0.7,cube_width*1.3,c1);
    float c2 = mod(fragCoord.y,2.0 * cube_width);
    c2 = smoothstep(cube_width*0.7,cube_width*1.3,c2);
    vec4 bcolor = mix(vec4(0.0,0.2,0.3,1.0),vec4(0.5,0.0,0.,1.0),c1*c2);
	fragColor = bcolor + wave_color;
}

