float hatching(vec2 p) {
	p = sin(p * 1000.) * .0015 + p;
	vec2 var_1 = p * (1. - (abs(sin(iTime * 1000.)) * .025 + .025));
	vec2 var_2 = p * vec2(1., -1.);
	vec2 var_3 = p * (1. - (abs(sin(iTime * 1000.)) * .025 + .025)) * vec2(-1.4, 1.);
	return ((clamp(sin((var_1.x + var_1.y) * 100.), .9, 1.) - .9) * 10. * (1. - (abs(sin(iTime * 1000.)) * .025 + .025)) - (clamp(sin((var_2.x + var_2.y) * 100.) + sin(p.x + sin(p.y * 137.) * 5.), .9, 1.) - .9) * 10. + (clamp(sin((var_3.x + var_3.y) * 50.), .95, 1.) - .95) * 20. * .4) / 2.;
}
vec3 fill(float d, vec2 p) {
	vec2 var_2 = p * vec2((.5 + .05 * abs(sin(p.x))) * (1. - (abs(sin(iTime * 1000.)) * .025 + .025)), 1. * (1. - (abs(sin(iTime * 1000.)) * .025 + .025)));
	return vec3(.44, .38, .37) - hatching(p) * .05 - (clamp(sin((var_2.x + var_2.y) * 200.), .9, 1.) - .9) * 10. * .2;
}
vec2 polar_cart(vec2 p) {
	return vec2(cos(p.x), sin(p.x)) * p.y;
}
vec3 texturize(float d, vec2 p) {
	vec3 temp_30;
	d = d + sin(iTime * 100. * d * p.y * 10.) * .4 * d;
	if(abs(d) <= .01 || (abs(d) - hatching(p) * .04) <= .01) {
		temp_30 = vec3(.23, .20, .19);
	} else if(d < 0.) {
		temp_30 = fill(d, p);
	} else {
		temp_30 = vec3(.83, .83, .83) - hatching(p) * .2;
	}
	return temp_30;
}
vec2 cart_polar(vec2 p) {
	return vec2(atan(p.y, p.x), length(p));
}
vec3 cwarp(vec3 c) {
	float t = 1. - pow(clamp((abs(mod(iTime, 8.) - 4.) - 1.) / 1.5, 0., 1.), 3.);
	return vec3(polar_cart(cart_polar(c.xy * (1. - t)) + vec2(t * 3.14159 * -1., 0.)), c.z * (t * 2.5 + 1.));
}
float p_circle(vec2 p, vec3 c) {
	c = cwarp(c);
	return length(c.xy - p) - c.z;
}
vec2 rotate_2d(vec2 c, float a) {
	float ca = cos(a);
	float sa = sin(a);
	return vec2(c.x * ca - c.y * sa, c.y * ca + c.x * sa);
}
vec3 zrotate(vec3 v, float a) {
	return vec3(rotate_2d(v.xy, a * .017453), v.z);
}
float distance_field(vec2 p) {
	p = rotate_2d(p, -iTime);
	return max(p_circle(p, vec3(0., 0., .6)), -max(max(max(max(max(p_circle(p, vec3(0., 0., .4)), -max(max(p_circle(p, zrotate(vec3(.14, -.04, .52), 0.)), p_circle(p, zrotate(vec3(-.25, .135, .46), 0.))), -p_circle(p, zrotate(vec3(-.3, -.101, .46), 0.)))), -max(max(p_circle(p, zrotate(vec3(.14, -.04, .52), 120.)), p_circle(p, zrotate(vec3(-.25, .135, .46), 120.))), -p_circle(p, zrotate(vec3(-.3, -.101, .46), 120.)))), -max(max(p_circle(p, zrotate(vec3(.14, -.04, .52), 240.)), p_circle(p, zrotate(vec3(-.25, .135, .46), 240.))), -p_circle(p, zrotate(vec3(-.3, -.101, .46), 240.)))), -max(max(p_circle(p, zrotate(vec3(.14, -.04, .52), 360.)), p_circle(p, zrotate(vec3(-.25, .135, .46), 360.))), -p_circle(p, zrotate(vec3(-.3, -.101, .46), 360.)))), -max(max(max(p_circle(p, vec3(0., .184, .295)), p_circle(p, zrotate(vec3(0., .184, .295), 120.))), p_circle(p, zrotate(vec3(0., .184, .295), 240.))), -p_circle(p, vec3(0., 0., .0434)))));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 p = (fragCoord.xy / iResolution.xy * 2. - 1.) * vec2(iResolution.x / iResolution.y, 1.) * vec2(1.2, -1.2);
	vec2 wp = polar_cart(cart_polar(p) + vec2(0., sin(p.y * 100. + iTime * 10.) * .003)) + vec2(sin(p.y * 10. + iTime * 10. * sin(iTime / 17.)) * .005, 0.);
	vec2 h = vec2(.00001, 0.);
	fragColor = vec4(texturize(distance_field(wp) / abs(length(vec2(distance_field(wp + h) - distance_field(wp - h), distance_field(wp + h.yx) - distance_field(wp - h.yx)) / (2. * h.x))), p), 1.);
}

