// The Logistic Map in complex numbers. Since it's a quadratic funcion,
// it's isomorphic to the Mandelbrot Set.
//
// The bifurcation diagram for the Logistic Map in real numbers is
// superimposed to better see the overlap in the dynamics across the
// x axis. More info here: https://www.shadertoy.com/view/4dSXWt
//
// Since f(z) = h·z·(1-z), as |Zn| approaches infinity we have that
// Z = h^(2^n-1)·Zo^(2^n)
//
// Hence the normalization map phi = (Zn·h)^(1/2^n). 
//
// The Green function is therefore G = log|phi| = (log|Zn|+log|h|)/(2^n)


// supersampling factor (1 for slow machines, 5 for monsters!)
#define AA 2

// complex number operations
vec2 cadd( float s, vec2 a ) { return vec2( a.x+s, a.y ); }
vec2 cmul( vec2 a, vec2 b )  { return vec2( a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x ); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 tot = vec3(0.0);
    #if AA>1
    for( int jj=0; jj<AA; jj++ )
    for( int ii=0; ii<AA; ii++ )
    #else
    int ii = 1, jj = 1;
    #endif
    {
        vec2 q = fragCoord.xy+vec2(float(ii),float(jj))/float(AA);
        vec2 p = (-iResolution.xy + 2.0*q)/iResolution.y;

        // zoom
        float zoo = 0.62 + 0.38*cos(.02*iTime);
        float coa = cos( 0.1*(1.0-zoo)*iTime );
        float sia = sin( 0.1*(1.0-zoo)*iTime );
        zoo = pow( zoo,8.0);
        vec2 xy = vec2( p.x*coa-p.y*sia, p.x*sia+p.y*coa);
        vec2 cc = vec2(1.0,0.0)+smoothstep(1.0,0.5,zoo)*vec2(0.24814,0.7369) + xy*zoo*2.0;

        vec3 col = vec3( 0.0 );

        //---------------------------------
        // logistic map in complex numbers
        //---------------------------------

        vec2 sc = vec2( abs(cc.x-1.0)-1.0,cc.y);
        if( dot(sc,sc)<1.0 )
        {
            // trick: in order to accelerate the rendering, we can detect if we
            // are inside the convergent part of the set (any of the two bulbs of period 1).
            //col = vec3(0.2);
        }
        else
        {
            float co = 0.0;
            vec2 z  = vec2(0.5,0.0);
            for( int i=0; i<256; i++ )
            {
                if( dot(z,z)>1024.0 ) break;
                z = cmul(cc, cmul( z, cadd(1.0,-z) ) );
                co += 1.0;
            }

            // smooth interation count = n + 1 - log2( log2|h| + log2|z| );
            float sco = co + 1.0 - log2( 0.5*(log2(dot(cc,cc)) + log2(dot(z,z))) );

            col = 0.5 + 0.5*cos( 3.0 + sco*0.1 + vec3(0.0,0.5,1.0));
            if( co>255.5 ) col = vec3(0.0);
        }

        // Hubbard-Douady potential, |G|
        //float d = (log(length(z)) + log(length(cc)))/pow(2.0,co);


        //---------------------------------
        // logic map in real numbers    
        //---------------------------------
        if( abs(cc.x-1.0)<3.0 )
        {
            float al = smoothstep( 17.0, 12.0, iTime );
            col = clamp(col,0.0,1.0);
            float x = 0.5;
            for( int i=0; i<200; i++ )
            x = cc.x*x*(1.0-x);
            for( int i=0; i<200; i++ )
            {
                x = cc.x*x*(1.0-x);
                col = mix( col, vec3(1.0,1.0,0.0), 
                           (0.15+0.85*pow(clamp(abs(sc.x+1.0)*0.4,0.0,1.0),4.0))*al*
                           0.06*exp(-15000.0*(cc.y-x)*(cc.y-x)) );
            }
        }

        tot += col;
    }

    tot = tot/float(AA*AA);

	fragColor = vec4( tot, 1.0 );
}

#define WARP_F 0.5
#define SWIRLYNESS 1.4

/* from iq */
float hash(float n){
	return fract(sin(n)*43758.5453);
}
vec3 noise(in vec2 x){
    vec2 p = floor(x);
    vec2 f = fract(x);
    f = f*f*(3.0-2.0*f);
    float n = p.x + p.y*57.0;
    float res = mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                    mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y);
    return vec3(res);
}
/* --- */

vec2 rotate(vec2 p, float a) {
    float i = cos(a) * p.x + sin(a) * p.y;
    float j = -sin(a) * p.x + cos(a) * p.y;

    return vec2(i,j);
}

vec3 nice(vec2 p) {
    vec3 warp = SWIRLYNESS*noise(p + vec2(WARP_F * iTime));

    float s, A, F;

    s = 0.1;
    A = 1.0;
    F = 1.0;

    for(int i = 0; i < 10; i++){
        p =  rotate(p, float(i)*SWIRLYNESS);
 		p += warp.y + s;
        s += A * noise(p * F).x;
        F = F * 2.0;
        A = A / 2.0;
    }

    float a = 1.0 / sqrt(3.0), b = a, c = a;

    return vec3(
    	a * smoothstep(0.6, 0.9, s*s),
    	b * smoothstep(1.1, 1.3, s*s),
    	c * smoothstep(1.5, 1.51, s*s));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = 2.0 * uv - 1.0;
    vec3 col, n;

    p.x *= iResolution.x / iResolution.y;  
    p += iTime * 0.01;

    float A = 1.0, F = 4.0;

    // two or more? use a for!
    for(int i = 0; i < 1; ++i) {   
    	n += A * nice(p * F);
        A *= 0.5;
        F *= 2.0;
    }


    col += (n.x) * vec3(0.5, 0.3, 0.4);
    col += (n.y) * vec3(0.4, 0.3, 0.2);
    col += (n.z) * vec3(0.2, 0.2, 0.2);


	fragColor = vec4(col * sqrt(length(col)), 1.0);
}

