    float shapeRadius = 0.2 + 0.1*cos( atan(distanceToShape.x, distanceToShape.y)*10.00
                           - 20.00*distanceToShape.x*iTime + 10000000.0  );
    //shape sharpness (smaller is sharper)
    float sharpnessMod = 0.008;

    col *=  1.00 - smoothstep( shapeRadius, shapeRadius+sharpnessMod, length(distanceToShape));
    //col = vec3( 1.0, 1.0, 1.0) - col;

    //finally changes the color
	fragColor = vec4(col,1.0);
}

const int rays_hits=9;
const float epsilon=0.001;
vec3 vertical;

vec3 sphere1;
vec3 sphere2;
vec3 sphere3=vec3(1.0,1.0,1.0);

vec3 back(vec3 rd)
{
	float s=pow(dot(rd,vertical),0.68);//Sky
	float f=dot(rd,-vertical);//floor
	vec3 sc=s*vec3(66.0/255.0,111.0/255.0,169.0/255.0);
	vec3 fc=f*vec3(185./255.,122./255.,87./255.);
	return max(sc,fc);
}

float sphere(vec3 ray, vec3 dir, vec3 center, float radius, float closed_hit, out vec3 normal)
{
  vec3 rc = ray-center;
  float c = dot(rc, rc) - (radius*radius);
  float b = dot(dir, rc);
  float d = b*b - c;
  float t = -b - sqrt(abs(d));
  if (d < 0.0 || t < 0.0 || t > closed_hit) 
	return closed_hit;
  else {
    normal = center-(ray+dir*t);
	return t;
 }

}

float scene(vec3 ro, vec3 rd, out vec3 normal, float max_dist)
{
	float dist=max_dist;
	dist=sphere(ro,rd,sphere1,0.7,dist,normal);
	dist=sphere(ro,rd,sphere2,0.5,dist,normal);
	dist=sphere(ro,rd,sphere3,0.88,dist,normal);
	return dist;
}


mat2 rotate2D(float a)
{
	return mat2(cos(a), -sin(a),
				sin(a),  cos(a));
}

vec2 rotateY(vec2 v, float a)
{
	return v*rotate2D(a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vertical = vec3(sin(0.8*iTime),1.0,0.0);
    sphere1=vec3(0.0,0.0,-2.0);
	sphere2=vec3(0.0,sin(iTime),0.0);
    vec2 v = (-1.0 + 2.0*fragCoord.xy / iResolution.xy);
	vec3 light = vec3(0.0);    
	vec3 transmit = vec3(1.0); 
	vec3 ro=vec3(3.0*cos(iTime),0.0,3.0*sin(iTime))+vertical;   
	vec2 vd=normalize(vec3(0.0,0.0,0.0) - ro).xz; //View direction
	float va=atan(vd.y,vd.x);
	vec2 r2d=rotateY(vec2(1.0,v.x),va);
	vec3 rd=normalize(vec3(r2d.x,v.y,r2d.y));    //direction 

	for (int r=0;r < rays_hits; r++)
	{
		vec3 normal;
		float max_dist=1000.0;
		float dist=scene(ro,rd,normal,max_dist);
		if (dist < max_dist)
		{

			transmit*=0.9;
			ro += rd*dist;
			rd =reflect(rd,normalize(normal));
			ro += rd*epsilon; 
		}
		else { //Hit infinite (or default object)
			light += transmit*back(rd); //here background fonction
			break;
		}
	}
	fragColor = vec4(light, 1.0); // Set pixel color to the amount of light seen.
}
