vec2 getPos(vec2 p){
    float ratio = iResolution.x/iResolution.y;
    return (p/iResolution.xy*2.-1.)*vec2(ratio, 1.);//centering -1->0->1 x & y
}
float metahole(vec2 p, vec2 o, float radius, float thick){
    vec2 po = p-o;
	return thick / dot(po+radius, po-radius);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = getPos(fragCoord.xy);
    float t = sin(iTime*0.3);
    vec3 tex = texture(iChannel0, uv).xyz;
    float h1 = metahole(uv, vec2(t,0.), 0.5, 0.4);
	float h2 = metahole(uv, vec2(-t,0.), 0.6, -0.2);
	float o = h1+h2;
	fragColor.rgb = smoothstep(tex, vec3(o), vec3(t)); ;
}

#define PI 3.141592653589793238
#define SPHERES 4.0
#define SPHERE_SHINE 30.0

float iSphere (vec3 ro, vec3 rd, vec3 c, float r, out float t){

    float B = 2.0* dot(ro - c,rd);
    float C = pow(length(ro -c),2.0) -pow(r,2.0);
    float d = pow(B,2.0) - 4.0*C;
    if (d < 0.0){
    	return -1.0;
    }

    t = (-B - sqrt(d))/2.0;    
    return 1.0;

}

vec3 nSphere(vec3 pos, vec3 c){

    return normalize(pos-c);

}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = vec2(-1.0 + 2.0*fragCoord.xy / iResolution.xy);
    float ar = iResolution.x / iResolution.y;
    vec2 r =  vec2(uv.x * ar,uv.y);

    vec3 ro = vec3(0.0,0.0,3.0);
	vec3 rd = normalize(vec3(r.xy,-3.0));


    vec3 l_1pos = vec3(0.0,sin(iTime),0.0);
    float l_1kd = 0.6;
    float l_1ks = 0.3;

    vec3 l_2pos = vec3(2.0*ar*sin(iTime*1.523),0.0,2.0*ar*cos(iTime*1.523));
    float l_2kd = 0.6;
    float l_2ks = 0.3;

    float ka = 0.02;

    float shading1 = 0.0;
    float shading2 = 0.0;
    float t = -1.0;
    float t_temp = -1.0;

    for (float i = 0.0;i<SPHERES;i+=1.0){
        vec3 s_c = vec3((ar/2.0)*cos(iTime+i*2.0*PI/SPHERES),
                        0.0,(ar/2.0)*sin(iTime+i*2.0*PI/SPHERES));
        float s_r = 0.3 + 0.1*i;

        if ((iSphere(ro,rd,s_c,s_r,t_temp) > 0.5) &&
        (((t_temp < t) && (t_temp > 0.0)) || (t < 0.0))){
        	t = t_temp;
        	vec3 pos = ro + t*rd;
            vec3 Lm = normalize(l_1pos-pos);
            vec3 N = nSphere(pos,s_c);
            vec3 Rm = 2.0*dot(Lm,N)*N - Lm;
            vec3 Lm_2 = normalize(l_2pos-pos);
			vec3 Rm_2 = 2.0*dot(Lm_2,N)*N - Lm_2;

        	shading1 = ka + max(0.0,l_1kd*dot(Lm,N)) + max(0.0,l_1ks *pow(dot(Rm,-rd),SPHERE_SHINE));
            shading2 = l_2kd*dot(Lm_2,N) + max(0.0,l_2ks *pow(dot(Rm_2,-rd),SPHERE_SHINE));

    		}

        }



    vec3 col = vec3(shading1,shading2,0.0);

	fragColor = vec4(col,1.0); 
}

#define PI 3.14159
#define SMALL_FLOAT 0.0001
#define BIG_FLOAT 1000000.
#define DISTMARCH_STEPS 60
#define DISTMARCH_MAXDIST 400.
#define REP_XZ 45.
#define REP_Y 25.
#define CLOCK_R 15.
#define CLOCK_H 1.

// Based on IÃ±igo Quilez articles:
// https://www.shadertoy.com/view/Xds3zN

const float fov = PI / 4.0;
vec3 LigthPos = vec3(0.0,0.0,0.0);

float clock( vec3 p, float r, float h )
{
	vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(r,h);
  	return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}



float map( vec3 pos)
{
    pos.y += REP_Y;
	vec3 c = vec3(REP_XZ);
    vec3 p = mod(pos,c)-0.5*c;    
    pos.xz = p.xz;
    float da = clock(pos,CLOCK_R,CLOCK_H);
    pos.y -= REP_Y*2.0;
    float db = clock(pos,CLOCK_R,CLOCK_H);

    return min(da,db);
}

float distmarch( vec3 ro, vec3 rd)
{   
    float dist = 10. * SMALL_FLOAT;
    float t = 0.;
    for (int i=0; i < DISTMARCH_STEPS; i++) 
    {
        if ( abs(dist) < SMALL_FLOAT || t > DISTMARCH_MAXDIST ) break;
        // advance the distance of the last lookup
        t += dist;
        dist = map( ro + t * rd);
    }

    return t;
}

vec3 texture2d( in vec3 pos )
{
    //return vec3(1.0);

	vec3 c = vec3(REP_XZ);
    vec3 p = mod(pos,c)-0.5*c;    
    pos.xz = p.xz;


    float len = length(pos.xz);
    if(len<1.0)
        return vec3(0.0);

    if(len>CLOCK_R-1.0)
        return vec3(0.7,0.7,0.2);


    float ep = 0.05;
    float alfa =atan(pos.z,pos.x);
    float an = abs(alfa);
    if((abs(an)<ep || abs(an-PI)<ep || abs(an-PI/2.0)<ep || abs(an-PI/4.0)<ep
       || abs(an-3.0*PI/4.0)<ep )
       && len>CLOCK_R-5.0)
        return vec3(0.2,0.2,0.6);

    float min = mod(iTime*15.0,2.0*PI) - PI;
    if(abs(alfa-min)<ep)
        return vec3(0.0);

    if(len<4.0)
    {
    	float hour = mod(iTime*3.0,2.0*PI) - PI;
        if(abs(alfa-hour)<ep)
            return vec3(0.0);

    }




    return vec3(0.5 + len / CLOCK_R);
}

vec3 calcNormal( in vec3 pos )
{
	vec3 eps = vec3( 0.001, 0.0, 0.0 );
	vec3 nor = vec3(
	    map(pos+eps.xyy) - map(pos-eps.xyy),
	    map(pos+eps.yxy) - map(pos-eps.yxy),
	    map(pos+eps.yyx) - map(pos-eps.yyx) );
	return normalize(nor);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	float x = 2.0*uv.x-1.0;
    float y = 2.0*uv.y-1.0;
	float time = iTime*2.5;


 	vec3 vLookFrom = vec3(30.0*time,2.0,21.0);
 	vec3 vLookAt = vec3(30.0*time+10.0,0.0,21.0);
	vec3 LigthPos = vec3(30.0*time,0.0,12.0);

    vec3 N = normalize(vLookAt-vLookFrom);
    vec3 V = normalize(cross( N , vec3(0.0,1.0,0.0)));
    vec3 U = cross(V , N);
    float k = 2.0*tan(fov/2.0);
	vec3 Dy = U*(k*iResolution.y/iResolution.x);
	vec3 Dx = V*k;
	// direccion de cada rayo
	vec3 D = normalize(N + Dx*x + Dy*y);

	float dist = distmarch( vLookFrom,D);
    vec3 rgb;
    if(dist < DISTMARCH_MAXDIST)
    {
        // computo n dot l 
        vec3 Ip = vLookFrom + D*dist;
        vec3 Ld = normalize(LigthPos - Ip);
        vec3 Normal = calcNormal(Ip);
        float Kd = 0.2 + clamp(dot(Normal , Ld) , 0.0 , 1.0);
        rgb = texture2d(Ip) *Kd;
    }
    else
    {
        rgb = texture( iChannel0, ((uv+ 0.5)+iTime*0.15)/16.0).rgb;

    }

    vec3 final_color = mix(vec3(0.85,0.85,1.0),rgb, 
		smoothstep(0.0,0.65,abs(y-0.4)));
	fragColor = vec4(final_color, 1.0);    

}

const float PI = 3.14159265359;
const float DBL_PI = PI * 2.0;
const float TIME_MOD = 10.0;
const float AMP_SCALE = 0.03;
const float THICKNESS = 0.2;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    uv.y = -((1.0 - uv.y) - 0.5);

    float ampMod = ((sin(iTime) + 0.25) + 1.0) / 2.0;
    float thickMod = ((sin(iTime * (TIME_MOD * 0.1)) + 0.1) + 1.0) / 2.0;    
    vec2 ptOnWave = vec2(uv.x, sin((uv.x * DBL_PI) + iTime * TIME_MOD) * (AMP_SCALE * ampMod));    
    float distToPt = length(uv - ptOnWave);    
    float col = floor(((1.0 + (THICKNESS * thickMod)) - distToPt));

    fragColor = vec4(col, col, col, 1.0);
}

/*
 * # Raymarched distance-field renderer tutorial
 * # Part 2: Lighting
 *
 * ```
 * Author:  SÃ©bastien Pierre   
 * Website: http://sebastienpierre.ca
 * Twitter: @ssebastien
 * License: BSD License
 * ```
 *
 * This is the second part of the Raymarched Distance-Field Renderer tutorial.
 * In the first part <https://www.shadertoy.com/view/XtXGzM>, 
 * we've seen the `map`, `interesect` and `main` functions that constitute the base
 * of the renderer. We've created a sphere
 * with a very simple lighting model. 
 *
 * In this part of the tutorial, we're going going to add light
 * sources of different colors and "shade" our sphere so that it does not
 * look grey anymore.
 *
 * Have a look at [CFT], and espectially the `GetObjectLighting` if you'd like
 * to see how to implement more visual properties on top of this model.
 *
 * References:
 *
 * - [PART1] https://www.shadertoy.com/view/XtXGzM
 * - [LIGT] http://www1.cs.columbia.edu/~cs4162/slides/lecture16.pdf
 * - [CFT] https://www.shadertoy.com/view/lssGzn
*/

/**
  * Distance field function for a sphere at [0,0,0] of radius `s`
*/
float sdSphere( vec3 p, float s )
{
	return length(p)-s;
}

/**
  * Returns the distance to the closest object in the scene to the given point.
*/
float map( in vec3 p )
{
	return sdSphere(p, 1.0);	
}


/**
  * Returns the distance on the ray with origin `ro` and delta `rd`
  * to the closest object in the scene.
*/
float intersect( in vec3 ro, in vec3 rd )
{
    const float maxd = 20.0;
	float h          = 1.0;
    float t          = 0.0;
    for( int i=0; i<50; i++ )
    {
        if( h<0.001 || t>maxd ) break;
	    h = map( ro+rd*t );
        t += h;
    }

    if( t>maxd ) t=-1.0;

    return t;
}

/**
  * `pointlight` is the basic function to evaluate the effect of
  * a light source at a given point. It takes the point position `pos`,
  * the light position `lgt`, the light color `col` and the light
  * `intensity`, and returns the corresponding color.
  *
  * Note that this does not do any shadow casting, so it does not take
  * into account any object that might be between the light and the
  * point.
*/
vec3 pointlight( in vec3 pos, in vec3 lgt, in vec3 col, float intensity ){
    // We start by calculating the distance between the current point
    // and the light source. As light's intensity decreases (ie. is
    // attenuated according to distance), we need this as the primary
    // value.
    float distance    = length(lgt - pos);
    // What we want to calculate next is how the intensity decreases with
    // the distance. In that case, we use a quadratic attenuation, meaning
    // that we divide the intensity by the distance^2. This shading model
    // is super smiple but already gives good results.
    float attenuation = intensity / (distance * distance);
    // We return the corresponding color.
    vec3 color        = col * attenuation;
    return color;
}

/**
  * `lighting` creates three point lights, one red, one green and one blue
  * and return the composite value. The blue light follows the mouse pointer.
*/
vec3 lighting( in vec3 pos ) {
    // In order to make the lighting interesting, we'll make the lights revolve
    // aroud the Y axis at different speeds. This will allow to show how the
    // emitted light blends together. You can adjust the speed of the revolution
    // by changing `s`. Set it to 0.0 to have the lights still.
    float s  = 1.0;
    float a1 = 0.0 * 3.1416 / 3.0 + s*0.5*iTime;
    float a2 = 1.0 * 3.1416 / 3.0 + s*0.3*iTime;
    float a3 = 1.0 * 3.1416 / 3.0 + s*0.1*iTime;
    float r1 = 1.0;
    float r2 = 1.0;
    float r3 = 1.0;
    return pointlight(pos, vec3(r1 * cos(a1), 1.0, r1 * sin(a1)), vec3( 1.0, 0.0, 0.0), 2.0)
    +      pointlight(pos, vec3(r2 * cos(a2), 1.0, r2 * sin(a2)), vec3( 0.0, 1.0, 0.0), 2.0)
    +      pointlight(pos, vec3(r3 * cos(a3), 1.0, r3 * sin(a3)), vec3( 0.0, 0.0, 1.0), 2.0)
    ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // `q` is the normalized position of the current shaded pixel
	vec2 q = fragCoord.xy / iResolution.xy;
    // `p` is positioned so that [0,0] is at the center of the screen
    vec2 p = 2.0 * q - 1.0;
    // sets aspect ration to `p 
    p.x *= iResolution.x/iResolution.y;  

    // `ro` is the camera origin where the rays are cast
	vec3 ro = vec3(0.0, 0.0, 2.0 );

    // `rd` is the ray delta, ie. it's normalized direction
    vec3 rd = normalize( vec3(p,-1.0) );

    // col is the color of the pixel, black by default.
	vec3 col = vec3(0.0);

    // `t` will hold the distance to the intersection with the scene
    float t = intersect(ro,rd);
    if( t>0.0 )
    {
        // `pos` is the point found by interesect, ie. the point of 
        // where the ray interseted the scene.
        vec3 pos = ro + t*rd;
        // We call the basic `lighting` function to see the point lights
        // in action.
        col  = lighting(pos);
    }
	// We assign the color we've just computed.
    fragColor = vec4( col, 1.0 );
}

