{
    float t = 5.5;
    float dt = .039;
    vec3 col= vec3(0.);
    for( int i=0; i<64; i++ )
	{                         
        t-=dt;      
        col = (.72+.25*ti)*col+ .08*map(ro+t*rd); 

    }

    return col;
}


