  // result is x=scene distance y=material or object id; zw are material specific parameters (maybe uv co-ordinates)
  vec4 GetDistanceScene( const in vec3 vPos )
  {
    vec4 vResult = vec4(10000.0, -1.0, 0.0, 0.0);
    float tt=iTime*(2.0)+10.0;
    vec4 vDistFloor1 = vec4(vPos.y + sin(vPos.x*0.2) * sin(vPos.z*0.2+tt), 1.0, vPos.xz);
    vec4 vDistFloor2 = vec4(vPos.y + sin(vPos.x*0.4) * sin(vPos.x+tt), 1.0, vPos.xz);
    vResult = DistCombineUnion(vResult, vDistFloor1);
    vResult += DistCombineUnion(vResult, vDistFloor2);
    return vResult;
  }

