	if(tsk>1.0 && tsk<8.0)rez=maxRez;
	if(tsk==4.0)meshy=1.0;
	ms=mstsk(tsk);
	if(tm<1.0){//moving mouse to task
		ms=mix(mstsk(tsk-1.0)+vec2(-0.3,1.0)*tm,ms,tm);	
		if(tsk==5.0)meshy=1.0;
	}else{//perform task
		tm-=1.0;tm*=0.25;
		if(ms.y<0.03){ms.x+=sin(tm*6.283)*0.3;slider.x=ms.x;}
		else if(ms.x==0.77){ms.y+=sin(tm*6.283)*0.4;slider.y=ms.y;}
		//if(tm<0.04)clk=0.0;
		if(tsk==1.0)rez=minRez+tm*(maxRez-minRez);
		else if(tsk==7.0)rez=maxRez-tm*(maxRez-minRez);
		if(tsk==3.0)meshy=tm;
		if(tsk==5.0)meshy=1.0-tm;
		clk=sin(time*50.0);
	}
	vec2 uv=fragCoord.xy/size.xy;
	vec3 color;
	if(uv.x<0.75 && uv.y>0.05){

		hrez=0.5*rez;
		float a=-slider.x*3.0,b=slider.y*3.0;
		vec3 ro=vec3(0.0,0.0,1.5),rd=vec3((vec2(2.5)*fragCoord.xy-size.xy)/size.y,1.5);
		ro.xz=rotate(ro.xz,a);
		ro.xy=rotate(ro.xy,b);
		rd=lookat(vec3(0.0,-1.0,0.0)-ro,vec3(0.0,1.0,0.0))*normalize(rd);
		color=scene(ro,rd,fragCoord);
	}else{
		color=vec3(0.4);
	  	if(uv.x>0.79 && uv.y<0.95 && uv.y>0.45){
			float sz=30.0;
			ch_d=100.0;
            const int _A=126, _C=29,_D=125,_E=31,_F=30,_H=122,_I=128,_L=25,_M=252,_P=94, _S=55,_T=132,_U=121;
			ch_p=(uv-vec2(0.86,0.9))*sz;
			ch(_M);ch(_E);ch(_S);ch(_H);
			ch_p=(uv-vec2(0.83,0.8))*sz;
			ch(_D);ch(_I);ch(_F);ch(_F);ch(_U);ch(_S);ch(_E);
			ch_p=(uv-vec2(0.82,0.7))*sz;
			ch(_S);ch(_P);ch(_E);ch(_C);ch(_U);ch(_L);ch(_A);ch(_A);
			ch_p=(uv-vec2(0.81,0.6))*sz;
			ch(_T);ch(_E);ch(_S);ch(_E);ch(_L);ch(_L);ch(_A);ch(_T);ch(_E);
			ch_p=(uv-vec2(0.84,0.5))*sz;
			ch(_S);ch(_M);ch(_D);ch(_D);ch(_T);ch(_H);
			float y=mod(uv.y+0.04,0.1);
			if(abs(uv.x-0.89)<0.1 && y<0.08)color=vec3(0.5+y*4.0);
			color=mix(vec3(0.0),color,smoothstep(0.0,0.1,ch_d));
	  	}else if(uv.y<0.045 && uv.x<0.75){
			color=vec3(0.4+uv.y*5.0+max(0.0,0.4-abs(uv.x-slider.x)*60.0));
		}else if(uv.y>0.05 && uv.x>0.755 && uv.x<0.785){
			color=vec3(0.6+(0.755-uv.x)*5.0+max(0.0,0.4-abs(uv.y-slider.y)*40.0));
		}		
	}
	color=mix(vec3(0.0),color,cursor(uv,ms));
	uv-=vec2(0.004);
	if(clk>0.5)color=mix(vec3(1.0),color,cursor(uv,ms));
	fragColor = vec4(color,1.0);	
}

// Created by Marc Lepage in Dec 2014
// by hacking up Inigo Quilez's raymarching demo:
// https://www.shadertoy.com/view/Xds3zN

#define M_PI 3.1415926535897932384626433832795

float sdPlane( vec3 p )
{
    return p.y;
}

float udRoundBox( vec3 p, vec3 b, float r )
{
    return length(max(abs(p)-b, 0.0)) - r;
}

// h.x is radius, h.y is length
float sdDrupelet( vec3 p, vec2 h)
{
    vec2 d = abs(vec2(length(p.xy),p.z)) - h;
    return min(length(p-vec3(0.0, 0.0, h.y))-h.x, min(max(d.x,d.y),0.0) + length(max(d,0.0)));
}

vec2 opU( vec2 d1, vec2 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}

// A B
//     C
// D E
//     F
//   G
float sdLogo( vec3 p, float r)
{
    // TODO need to figure out the exact skew
    // TODO need to figure out the exact vertical position of third column
    // TODO need to center it better (it's off by half a drupelet sphere)

    // Rotate by r radians
    float c = cos(r);
    float s = sin(r);
	float x = -(p.x*c + p.z*s);
	float z = -(p.x*s - p.z*c);
    p.x = x;
    p.z = z;

    // Skew
    p.z -= p.y/8.0;

    float d = sdDrupelet( p, vec2(0.1,0.1) ); // E
    d = min(d, sdDrupelet( p-vec3(0.0,  0.3,  0.0), vec2(0.1,0.1) )); // B
    d = min(d, sdDrupelet( p-vec3(0.0, -0.3,  0.0), vec2(0.1,0.1) )); // G
    d = min(d, sdDrupelet( p-vec3(0.0,  0.3, -0.4), vec2(0.1,0.1) )); // A
    d = min(d, sdDrupelet( p-vec3(0.0,  0.0, -0.4), vec2(0.1,0.1) )); // D
    d = min(d, sdDrupelet( p-vec3(0.0,  0.125, 0.4), vec2(0.1,0.1) )); // C
    d = min(d, sdDrupelet( p-vec3(0.0, -0.175, 0.4), vec2(0.1,0.1) )); // F
    return d;
}

vec2 map( in vec3 pos )
{
    vec2 res = opU( vec2( sdPlane(     pos), 1.0 ),
	                vec2( udRoundBox(  pos-vec3( 0.0,2.0,0.0), vec3(1.0), 0.1 ), 2.0 ) );

   	res = opU( res, vec2( sdLogo(  pos-vec3(-1.1, 2.0, 0.0), 0.0), 4.0 ) );

   	res = opU( res, vec2( sdLogo(  pos-vec3( 1.1, 2.0, 0.0), M_PI), 4.0 ) );

    res = opU( res, vec2( sdLogo(  pos-vec3( 0.0, 2.0, -1.1), M_PI/2.0), 4.0 ) );

    res = opU( res, vec2( sdLogo(  pos-vec3( 0.0, 2.0, 1.1), -M_PI/2.0), 4.0 ) );

    return res;
}

vec2 castRay( in vec3 ro, in vec3 rd )
{
    float tmin = 1.0;
    float tmax = 20.0;

	float precis = 0.0001;
    float t = tmin;
    float m = -1.0;
    for( int i=0; i<50; i++ )
    {
	    vec2 res = map( ro+rd*t );
        if( res.x<precis || t>tmax ) break;
        t += res.x;
	    m = res.y;
    }

    if( t>tmax ) m=-1.0;
    return vec2( t, m );
}

vec3 calcNormal( in vec3 pos )
{
	vec3 eps = vec3( 0.001, 0.0, 0.0 );
	vec3 nor = vec3(
	    map(pos+eps.xyy).x - map(pos-eps.xyy).x,
	    map(pos+eps.yxy).x - map(pos-eps.yxy).x,
	    map(pos+eps.yyx).x - map(pos-eps.yyx).x );
	return normalize(nor);
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}

float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<16; i++ )
    {
		float h = map( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 render( in vec3 ro, in vec3 rd )
{        
    vec3 col = vec3(0.9, 0.9, 0.9);
    vec2 res = castRay(ro,rd);
    float t = res.x;
	float m = res.y;
    if( m>-0.5 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos );
        vec3 ref = reflect( rd, nor );

        // material        
		col = 0.45 + 0.3*sin( vec3(0.5,0.08,0.10)*(m-1.0) );

        if( m<1.5 )
        {

            float f = mod( floor(5.0*pos.z) + floor(5.0*pos.x), 2.0);
            col = 0.4 + 0.1*f*vec3(1.0);
        }
        else if (m == 2.0)
        {
            // box
            col = vec3(0.05, 0.05, 0.05);
        }
        else if (m == 4.0)
        {
            // drupelet
            col = vec3(0.75, 0.75, 0.75);
        }

        // lighting        
        float occ = calcAO( pos, nor );
		vec3  lig = normalize( vec3(-0.6, 0.7, -0.5) );
		float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
        float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
        float bac = clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);
        float dom = smoothstep( -0.1, 0.1, ref.y );
        float fre = pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );
		float spe = pow(clamp( dot( ref, lig ), 0.0, 1.0 ),16.0);

        dif *= softshadow( pos, lig, 0.02, 2.5 );
        dom *= softshadow( pos, ref, 0.02, 2.5 );

		vec3 brdf = vec3(0.0);
        brdf += 1.20*dif*vec3(1.00,0.90,0.60);
		brdf += 1.20*spe*vec3(1.00,0.90,0.60)*dif;
        brdf += 0.30*amb*vec3(0.50,0.70,1.00)*occ;
        brdf += 0.40*dom*vec3(0.50,0.70,1.00)*occ;
        brdf += 0.30*bac*vec3(0.25,0.25,0.25)*occ;
        brdf += 0.40*fre*vec3(1.00,1.00,1.00)*occ;
		brdf += 0.02;
		col = col*brdf;

    	col = mix( col, vec3(0.8,0.9,1.0), 1.0-exp( -0.0005*t*t ) );
    }

	return vec3( clamp(col,0.0,1.0) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy/iResolution.xy;
    vec2 p = -1.0+2.0*q;
	p.x *= iResolution.x/iResolution.y;
    vec2 mo = iMouse.xy/iResolution.xy;

	float time = 40.0 + iTime;

	// camera	
	vec3 ro = vec3( -0.4+3.2*cos(0.1*time + 6.0*mo.x), 2.0 + 2.0*mo.y, 0.5 + 3.2*sin(0.1*time + 6.0*mo.x) );
	vec3 ta = vec3( 0.0, 1.5, 0.0 );

	// camera tx
	vec3 cw = normalize( ta-ro );
	vec3 cp = vec3( 0.0, 1.0, 0.0 );
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
	vec3 rd = normalize( p.x*cu + p.y*cv + 2.5*cw );

    vec3 col = render( ro, rd );

    // gamma
	col = pow( col, vec3(0.4545) );

    fragColor=vec4( col, 1.0 );
}

// distance field ray caster
// simon green 06/01/2011
// alex dunn - lollipop
// 
// based on Inigo Quilezles's:
// http://www.iquilezles.org/www/material/nvscene2008/rwwtt.pdf
// 
// Google Android robot:
// http://www.android.com/branding.html

precision mediump float;

#define SKY_COLOR vec3(0.1,0.4,0.9)
#define HORIZON_COLOR vec3(0.8, 0.8, 0.8)
#define SUN_COLOR vec3(1.0,1.0,0.8)

#define MIN_DISTANCE 0.01

#define SKIP_PRIMITIVES 0

#if SKIP_PRIMITIVES
#define EARLY_OUT if(d<MIN_DISTANCE) return d;
#else 
#define EARLY_OUT
#endif

// CSG operations
float _union(float a, float b)
{
    return min(a, b);
}

float intersect(float a, float b)
{
    return max(a, b);
}

float difference(float a, float b)
{
//    return max(a, -b);
	return max(a, 0.0-b); // work around PowerVR bug
}

// primitive functions
// these all return the distance to the surface from a given point

float plane(vec3 p, vec3 planeN, vec3 planePos)
{
    return dot(p - planePos, planeN);
}

float box(vec3 p, vec3 abc )
{
    vec3 di=max(abs(p)-abc, 0.0);
    return dot(di,di);
    //return length(di);
}

float sphere(vec3 p, float r)
{
    return length(p) - r;
}

// capsule in Y axis
float capsuleY(vec3 p, float r, float h)
{
    p.y -= clamp(p.y, 0.0, h);
    return length(p) - r;
}

// given segment ab and point c, computes closest point d on ab
// also returns t for the position of d, d(t) = a + t(b-a)
vec3 closestPtPointSegment(vec3 c, vec3 a, vec3 b, out float t)
{
    vec3 ab = b - a;
    // project c onto ab, computing parameterized position d(t) = a + t(b-a)
    t = dot(c - a, ab) / dot(ab, ab);
    // clamp to closest endpoint
    t = clamp(t, 0.0, 1.0);
    // compute projected position
    return a + t * ab;
}

// generic capsule
float capsule(vec3 p, vec3 a, vec3 b, float r)
{
    float t;
    vec3 c = closestPtPointSegment(p, a, b, t);
    return length(c - p) - r;
}

float cylinderY(vec3 p, float r, float h)
{
     float d = length(vec2(p.x, p.z)) - r;
     d = intersect(d, plane(p, vec3(0.0, 1.0, 0.0), vec3(0.0, h, 0.0)));
     d = intersect(d, plane(p, vec3(0.0, -1.0, 0.0), vec3(0.0)));
     return d;
}

// transforms
vec3 rotateX(vec3 p, float a)
{
    float sa = sin(a);
    float ca = cos(a);
    vec3 r;
    r.x = p.x;
    r.y = ca*p.y - sa*p.z;
    r.z = sa*p.y + ca*p.z;
    return r;
}

vec3 rotateY(vec3 p, float a)
{
    float sa = sin(a);
    float ca = cos(a);

    vec3 r;
    r.x = ca*p.x + sa*p.z;
    r.y = p.y;
    r.z = -sa*p.x + ca*p.z;
    return r;
}

float halfSphere(vec3 p, float r)
{
    return difference( 
               sphere(p, r),
               plane(p, vec3(0.0, 1.0, 0.0), vec3(0.0)) );
}

float sceneInterp() 
{
    const float speed=0.5;

    return clamp(sin(iTime*speed)*1.75,-1.0,1.0) *0.5+0.5;
}

float droid(vec3 p)
{
    float d;

    p.x = abs(p.x);  // mirror in X to reduce no. of primitives

    vec3 hp = p;

    // head
    d = halfSphere(hp, 1.0);

    EARLY_OUT

    // eyes
    d = _union(d, sphere(hp - vec3(0.3, 0.3, 0.9), 0.1));

    EARLY_OUT

    // antenna
    d = _union(d, capsule(hp, vec3(0.4, 0.7, 0.0), vec3(0.75, 1.2, 0.0), 0.05));

    EARLY_OUT

    // body
    d = _union(d, capsuleY((p*vec3(1.0, 4.0, 1.0) - vec3(0.0, -4.6, 0.0)), 1.0, 4.0));

    EARLY_OUT

    // arm
    d = _union(d, capsuleY(p - vec3(1.2, -0.9, 0.0), 0.2, 0.7));

    EARLY_OUT

    // legs
    d = _union(d, capsuleY(p - vec3(0.4, -1.8, 0.0), 0.2, 0.5));

    return d;
}

float lollipop(vec3 p)
{
    float d = capsuleY(p - vec3(0.0, -2.0, 0.0), 0.02, 2.0);

    EARLY_OUT

    d = _union(d, cylinderY(vec3(p.x,-p.z,p.y) - vec3(0.0, -0.1, 0.0), 0.9, 0.2)); // hand rotate by 90 in X

    return d;
}


// distance to scene
float scene(vec3 p)
{
    return mix(droid(p),lollipop(p),sceneInterp());
}

// calculate scene normal
vec3 sceneNormal( in vec3 pos, in float d )
{
    float eps = 0.14;
    vec3 n;
#if 1
    // central difference
    n.x = scene( vec3(pos.x+eps, pos.y, pos.z) ) - scene( vec3(pos.x-eps, pos.y, pos.z) );
    n.y = scene( vec3(pos.x, pos.y+eps, pos.z) ) - d;//scene( vec3(pos.x, pos.y-eps, pos.z) );
    n.z = scene( vec3(pos.x, pos.y, pos.z+eps) ) - scene( vec3(pos.x, pos.y, pos.z-eps) );
#else
    // forward difference
   // float d = scene( vec3(pos.x, pos.y, pos.z) );
    n.x = scene( vec3(pos.x+eps, pos.y, pos.z) ) - d;
    n.y = scene( vec3(pos.x, pos.y+eps, pos.z) ) - d;
    n.z = scene( vec3(pos.x, pos.y, pos.z+eps) ) - d;
#endif

 return normalize(dot(n,n) == 0.0 ? vec3(1.0) : n);
}

// ambient occlusion approximation
float ambientOcclusion(vec3 p, vec3 n)
{
    const float delta = 0.5;
    const float weight = 1.0;
    const float d = 0.5 * delta; 

    float a = weight*(d - scene(p + n*d));    

    return clamp(1.0 - a, 0.0, 1.0);
}

// lighting
vec3 shade(vec3 pos, vec3 n, vec3 eyePos)
{
    const vec3 lightPos = vec3(5.0, 10.0, 5.0);

    vec3 color1 = vec3(0.643, 0.776, 0.223);

    vec3 p=pos;
    float x=1.0-clamp(length(p),0.0,1.0);
    float t=cos(sin(atan(p.y,p.x)*3.0+9.0*x+iTime*2.0)*2.0)*x;
    float m=clamp(t,0.0,1.0);

    vec3 color2 = mix(mix(vec3(1.0),vec3(1.0,0.6,0.9),clamp(x*10.0,0.0,1.0)), vec3(1.0,0.0,0.0), m+(x*x*x));

    vec3 color=mix(color1,color2,sceneInterp());

    vec3 specColor = mix(HORIZON_COLOR, SKY_COLOR,n.y*0.5+0.5);
    const float shininess = 30.0;

    vec3 l = normalize(lightPos - pos);
    vec3 v = normalize(eyePos - pos);
    vec3 h = normalize(v + l);

    float diff = dot(n, l);
    float spec = dot(h, n);

    vec2 light = max(vec2(0.0), vec2(diff, spec));
    light = pow(light, vec2(1.0,shininess));


    diff = light.x;
    spec = light.y;
    diff = 0.5+0.5*diff;

    float fresnel = pow(1.0 -dot(n, v), 3.0);
    float ao = ambientOcclusion(pos, n);

    //return vec3(diff) * color + vec3(spec + fresnel*0.5);
    return diff*ao*SUN_COLOR * color + vec3(spec+fresnel) * specColor * 0.5;
   // return vec3(ao);
   // return vec3(fresnel);
   // return vec3(diff);
   //return vec3(spec*specColor);
}

// trace ray using sphere tracing
vec3 trace(vec3 ro, vec3 rd, out bool hit, out float d)
{
    const int maxSteps = 32;
    hit = false;
    vec3 pos = ro + rd;

    for(int i=maxSteps; i >= 0; --i)
    {
        d = scene(pos);

        hit = d<MIN_DISTANCE;
        if(hit) break;

        pos += d*rd;
    }
    return pos;
}

vec3 background(vec3 rd)
{
     return mix(HORIZON_COLOR, SKY_COLOR, rd.y);
}

