/*
 * Gardner Cos Clouds
 *
 * Translated/adapted from the RenderMan implementation in
 * Texturing & Modeling; a Procedural Approach (3rd ed, p. 50)
 *
 * This technique seeks to avoid visible pattern redundancy
 * by shifting the frequencies and phase several times while
 * computing the cloud density component. It fails somewhat
 * miserably as soon as you zoom out; set the ZOOM param to 0.1
 * and see for yourself.
 *
 */

#define PI 3.16
#define NTERMS 3.0
#define ZOOM 1.0

vec3 getLightPosition(){
	return vec3(5.0*sin(iTime), 3, -5);    
}

vec3 getEyePosition(){
    return vec3(0,0,-5);
}

vec4 shadePoint(vec3 pointPosition, vec4 textureColour){
    return textureColour;
}


vec4 getTextureColour(vec2 fragCoord, vec2 uv){
    float cloudDensity = 0.0;
    float amplitude = 0.45;
    float xphase = 0.9*iTime;
    float yphase = 0.7;
    float xfreq = 2.0*PI*0.023;
    float yfreq = 2.0*PI*0.021;

    float offset = 0.5;
    float xoffset = 37.0;
    float yoffzet = 523.0;

    float x = fragCoord.x;
    float y = fragCoord.y;

    float scale = 1.0/iResolution.x * 60.0 * 1.0/ZOOM;
    x = x*scale + offset + iTime;
    y = y*scale + offset - iTime/2.3;

    for(float i = 0.0; i < NTERMS; i++){
        float fx = amplitude * (offset + cos(xfreq * (x + xphase)));
        float fy = amplitude * (offset + cos(yfreq * (y + yphase)));
        cloudDensity += fx * fy;
        xphase = PI/2.0 * 0.9 * cos(yfreq * y);
        yphase = PI/2.0 * 1.1 * cos(xfreq * x);
        amplitude *= 0.602;
        xfreq *= 1.9 + i * .01;
        yfreq *= 2.2 - i * 0.08;
    }

    return mix(vec4(0.5, 0.55, 0.96, 1.0), vec4(1.0, 1.0, 1.0, 1.0), cloudDensity);   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 point = vec3(fragCoord.xy, 0.0);
	fragColor = shadePoint(point, getTextureColour(fragCoord,uv));
}

// source: http://stackoverflow.com/questions/5149544/can-i-generate-a-random-number-inside-a-pixel-shader
const vec2 k = vec2(23.1406926327792690,2.6651441426902251);
float rnd0( vec2 uv ) {return dot(uv,k); }
float rnd1( vec2 uv ) { return 1e-7 + 256. + dot(uv,k); }
float rnd2( vec2 uv ) { return mod( 123456789., 1e-7 + 256. * dot(uv,k) ); }
float rnd3( vec2 uv ) { return cos( mod( 123456789., 1e-7 + 256. * dot(uv,k) ) ); }

// We can even tweak the formula
float rnd4( vec2 uv ) { return fract( cos( mod( 1234., 1024. * dot(uv,k) ) ) ); }
float rnd5( vec2 uv ) { return fract( cos( mod( 12345., 1024. * dot(uv,k) ) ) ); }
float rnd6( vec2 uv ) { return fract( cos( mod( 123456., 1024. * dot(uv,k) ) ) ); }
float rnd7( vec2 uv ) { return fract( cos( mod( 1234567., 1024. * dot(uv,k) ) ) ); }
float rnd8( vec2 uv ) { return fract( cos( mod( 12345678., 1024. * dot(uv,k) ) ) ); }
float rnd9( vec2 uv ) { return fract( cos( mod( 123456780., 1024. * dot(uv,k) ) ) ); }

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    mediump vec2 uv = fragCoord.xy;
    mediump vec2 vu = vec2( uv.y, uv.x );
    float smallTime = iTime * rnd3(vu);
    uv /= smallTime;
    uv /= iResolution.xy;
    // mediump vec2 uv = fragCoord.xy / iResolution.xy;
    float i = rnd9(uv);
    fragColor = vec4(i,i,i,1.);
}

