// An edge antialising experiment (not multisampling used)

#define ANTIALIASING

vec2 sincos( float x ) { return vec2( sin(x), cos(x) ); }

vec2 sdSegment( in vec3 p, in vec3 a, in vec3 b )
{
    vec3 pa = p-a, ba = b-a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return vec2( length( pa-ba*h ), h );
}

vec3 opU( vec3 d1, vec3 d2 ) { return (d1.x<d2.x) ? d1 : d2; }

vec3 map( vec3 p )
{
    vec2 id = floor( (p.xz+1.0)/2.0);
    p.xz = mod( p.xz+1.0, 2.0 ) - 1.0;

    float ph = sin(0.5 + 3.1*id.x + sin(7.1*id.y));

    p.xz += 0.5*sincos(1.0+0.5*iTime+(p.y+11.0*ph)*0.8);

    vec3 p1 = p; p1.xz += 0.15*sincos(1.0*p.y-1.0*iTime+0.0);
    vec3 p2 = p; p2.xz += 0.15*sincos(1.0*p.y-1.0*iTime+2.0);
    vec3 p3 = p; p3.xz += 0.15*sincos(1.0*p.y-1.0*iTime+4.0);

    vec2 h1 = sdSegment(p1, vec3(0.0,-50.0, 0.0), vec3(0.0, 50.0, 0.0) );
    vec2 h2 = sdSegment(p2, vec3(0.0,-50.0, 0.0), vec3(0.0, 50.0, 0.0) );
    vec2 h3 = sdSegment(p3, vec3(0.0,-50.0, 0.0), vec3(0.0, 50.0, 0.0) );

    return opU( opU( vec3(h1.x-0.12,                                         ph + 0.0/3.0, h1.y), 
                     vec3(h2.x-0.12-0.05*cos( 500.0*h2.y - iTime*4.0), ph + 1.0/3.0, h2.y) ), 
                     vec3(h3.x-0.12-0.02*cos(2000.0*h3.y - iTime*4.0), ph + 2.0/3.0, h3.y) );
}

//-------------------------------------------------------

vec3 calcNormal( in vec3 pos, in float dt )
{
    vec2 e = vec2(1.0,-1.0)*dt;
    return normalize( e.xyy*map( pos + e.xyy ).x + 
					  e.yyx*map( pos + e.yyx ).x + 
					  e.yxy*map( pos + e.yxy ).x + 
					  e.xxx*map( pos + e.xxx ).x );
}

float calcOcc( in vec3 pos, in vec3 nor )
{
    const float h = 0.15;
	float ao = 0.0;
    for( int i=0; i<8; i++ )
    {
        vec3 dir = sin( float(i)*vec3(1.0,7.13,13.71)+vec3(0.0,2.0,4.0) );
        dir = dir + 2.5*nor*max(0.0,-dot(nor,dir));            
        float d = map( pos + h*dir ).x;
        ao += max(0.0,h-d);
    }
    return clamp( 1.0 - 0.7*ao, 0.0, 1.0 );
}

//-------------------------------------------------------
vec3 shade( in float t, in float m, in float v, in vec3 ro, in vec3 rd )
{
    float px = 0.0001;//(2.0/iResolution.y)*(1.0/3.0);
    float eps = px*t;

    vec3  pos = ro + t*rd;
    vec3  nor = calcNormal( pos, eps );
    float occ = calcOcc( pos, nor );

    vec3 col = 0.5 + 0.5*cos( m*vec3(1.4,1.2,1.0) + vec3(0.0,1.0,2.0) );
    col += 0.05*nor;
    col = clamp( col, 0.0, 1.0 );
    col *= 1.0 + 0.5*nor.x;
    col += 0.2*clamp(1.0+dot(rd,nor),0.0,1.0);
    col *= 1.4;
    col *= occ;
    col *= exp( -0.15*t );
    col *= 1.0 - smoothstep( 15.0, 35.0, t );

    return col;
}

//-------------------------------------------------------

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
	vec2 p = (-iResolution.xy+2.0*fragCoord.xy)/iResolution.y;

	vec3 ro = 0.6*vec3(2.0,-3.0, 4.0);
	vec3 ta = 0.5*vec3(0.0, 4.0,-4.0);

    float fl = 1.0;
    vec3 ww = normalize( ta - ro);
    vec3 uu = normalize( cross( vec3(1.0,0.0,0.0), ww ) );
    vec3 vv = normalize( cross(ww,uu) );
    vec3 rd = normalize( p.x*uu + p.y*vv + fl*ww );

    float px = (2.0/iResolution.y)*(1.0/fl);

    vec3 col = vec3(0.0);

    //---------------------------------------------
    // raymach loop
    //---------------------------------------------
    const float maxdist = 32.0;

    vec3 res = vec3(-1.0);
    float t = 0.0;
    #ifdef ANTIALIASING
    vec3 oh = vec3(0.0);
    vec4 tmp = vec4(0.0);
    #endif

    for( int i=0; i<128; i++ )
    {
	    vec3 h = map( ro + t*rd );
        float th1 = px*t;
        #ifndef ANTIALIASING
        th1 *= 1.5;
        #endif
        res = vec3( t, h.yz );
        if( h.x<th1 || t>maxdist ) break;


        #ifdef ANTIALIASING
        float th2 = px*t*2.0;
        if( (h.x<th2) && (h.x>oh.x) )
        {
            float lalp = 1.0 - (h.x-th1)/(th2-th1);
            vec3  lcol = shade( t, oh.y, oh.z, ro, rd );
            tmp.xyz += (1.0-tmp.w)*lalp*lcol;
            tmp.w   += (1.0-tmp.w)*lalp;
            if( tmp.w>0.99 ) break;
        }
        oh = h;
        #endif

        t += min( h.x, 0.5 )*0.5;
    }

    if( t < maxdist )
        col = shade( res.x, res.y, res.z, ro, rd );

    #ifdef ANTIALIASING
	col = mix( col, tmp.xyz/(0.001+tmp.w), tmp.w );
    #endif

    //---------------------------------------------

    col = pow( col, vec3(0.5,0.7,0.5) );

    vec2 q = fragCoord.xy/iResolution.xy;
    col *= pow(16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.1);

	fragColor = vec4( col, 1.0 );
}

// Parameters
#define CAMERA_FOCAL_LENGTH	1.2
#define DOT_COUNT			100.0
#define SOUND
#define MOUSE
#define HSV2RGB_FAST

// Constants
#define PI	3.14159265359

// PRNG
float rand (in vec2 seed) {
	return fract (sin (dot (seed, vec2 (12.9898, 78.233))) * 137.5453);
}

// HSV to RGB
vec3 hsv2rgb (in vec3 hsv) {
	#ifdef HSV2RGB_SAFE
	hsv.yz = clamp (hsv.yz, 0.0, 1.0);
	#endif
	#ifdef HSV2RGB_FAST
	return hsv.z * (1.0 + 0.5 * hsv.y * (cos (2.0 * PI * (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0))) - 1.0));
	#else
	return hsv.z * (1.0 + hsv.y * clamp (abs (fract (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0)) * 6.0 - 3.0) - 2.0, -1.0, 0.0));
	#endif
}

// Main function
void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Define the ray corresponding to this fragment
	vec3 ray = vec3 ((2.0 * fragCoord.xy - iResolution.xy) / iResolution.y, CAMERA_FOCAL_LENGTH);

	// Get the music info
	#ifdef SOUND
	float soundBass = texture (iChannel0, vec2 (0.0)).x;
	float soundTreble = texture (iChannel0, vec2 (0.9, 0.0)).x;
	#else
	float soundBass = 0.6 + 0.4 * cos (iTime * 0.2);
	float soundTreble = 0.5 + 0.5 * cos (iTime * 1.2);
	#endif

	// Define the number of rows
	float dotRowCount = floor (20.0 + 60.0 * soundTreble * soundBass) * 2.0;

	// Compute the orientation of the camera
	float yawAngle = cos (iTime * 2.0);
	float pitchAngle = 2.0 * PI * cos (iTime * 0.2 + soundTreble * 0.4);
	#ifdef MOUSE
	yawAngle += 2.0 * PI * iMouse.x / iResolution.x;
	pitchAngle += PI * (1.0 - iMouse.y / iResolution.y);
	#endif

	float cosYaw = cos (yawAngle);
	float sinYaw = sin (yawAngle);
	float cosPitch = cos (pitchAngle);
	float sinPitch = sin (pitchAngle);

	mat3 cameraOrientation;
	cameraOrientation [0] = vec3 (cosYaw, 0.0, -sinYaw);
	cameraOrientation [1] = vec3 (sinYaw * sinPitch, cosPitch, cosYaw * sinPitch);
	cameraOrientation [2] = vec3 (sinYaw * cosPitch, -sinPitch, cosYaw * cosPitch);

	ray = cameraOrientation * ray;

	// Compute the position of the camera
	float cameraDist = -2.0 * (cos (iTime) * cos (iTime * 3.5) + soundBass);
	vec3 cameraPosition = cameraOrientation [2] * cameraDist;

	// Compute the intersection point (ray / cylinder)
	float a = dot (ray.xz, ray.xz);
	float b = dot (cameraPosition.xz, ray.xz);
	float c = b * b - a * (dot (cameraPosition.xz, cameraPosition.xz) - 1.0);
	float ok = step (0.0, c);
	c = sqrt (c);
	vec3 hit;
	if (b < -c) {
		hit = cameraPosition - ray * (b + c) / a;
		if (abs (hit.y * DOT_COUNT / PI + 1.0) > dotRowCount) {
			hit = cameraPosition - ray * (b - c) / a;
		}
	} else {
		hit = cameraPosition - ray * (b - c) / a;
	}
	vec2 frag = vec2 ((atan (hit.z, hit.x) + PI) * DOT_COUNT, hit.y * DOT_COUNT + PI) / (2.0 * PI);

	// Compute the fragment color
	vec2 id = floor (frag);
	float random = rand (id);
	vec3 color = hsv2rgb (vec3 (iTime * 0.05 + id.y * 0.005, 1.0, 1.0));
	color += 0.5 * cos (random * vec3 (1.0, 2.0, 3.0));
	color *= smoothstep (0.5, 0.1, length (fract (frag) - 0.5));
	color *= 0.5 + 1.5 * step (0.9, cos (random * iTime * 5.0));
	color *= 0.5 + 0.5 * cos (random * iTime + PI * 0.5 * soundTreble);
	color *= smoothstep (dotRowCount, 0.0, (abs (id.y + 0.5) - 1.0) * 2.0);
	fragColor = vec4 (color * ok, 1.0);
}

//vec3 ambientLight = vec3(.2 + .2 * sin(iTime), .2 + .2 * cos(iTime), 0.2) * .2;
vec3 ambientLight = vec3(0.0);
vec3 dirLightColor = vec3(0.5, 0.5, 0.5) * 3.0;
vec3 dirLightDir;
float c = 2.;
float r = 0.05;
vec3 b = vec3(0.5);

float dBox(vec3 p, vec3 b)
{
    vec3 d = abs(p) - b;
    return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0)) - r;
}

float map(vec3 p)
{
    float dist = b.x;
    vec2 texcoords = mod(floor(p.xz / c), 256.) / 256.;
    //p.y += 6.0 * texture(iChannel0, texcoords).x;
    vec3 q = vec3(
        mod(p.x, c) - 0.5 * c,
        p.y,
        //mod(p.y, c) - 0.5 * c,
        mod(p.z, c) - 0.5 * c);
    return dBox(q, b);
}

vec3 getNormal(vec3 pos)
{
    float threshold = 0.001;
	vec2 t = vec2(0.0, threshold);
    vec3 n = vec3(map(pos + t.yxx) - map(pos - t.yxx),
           	  map(pos + t.xyx) - map(pos - t.xyx),
              map(pos + t.xxy) - map(pos - t.xxy));
    return normalize(n);
}

bool getDepth(vec3 rayPos, vec3 rayDir, inout float depth)
{
    const int max_iters = 128;
    float phase = (.5 + .5 * sin(iTime));
    float eps = 0.0005;
    vec3 p = rayPos;
    float d;
    float d_total = 0.0;
    for (int i = 0; i < max_iters; ++i)
    {
        d = map(p);
        d_total += d;
        p += d * rayDir;
        if (d < eps)
            break;
    }
    depth = d_total;
    return d < eps;
}

bool intersect(vec3 rayPos, vec3 rayDir, inout vec3 color, inout vec3 normal)
{
    vec3 bg = vec3(0.1, 0.1, 0.1);

    float depth;
    bool hit = getDepth(rayPos, rayDir, depth);

    normal = vec3(0.0);
    vec3 hitPoint = rayPos + depth * rayDir;
    if (hit)
    {
        normal = getNormal(hitPoint);
        //vec3 kd = vec3(0.1, 0.1, 0.1);
        vec3 kd;
        float dotX = abs(dot(normal, vec3(1.0, 0.0, 0.0)));
        float dotY = abs(dot(normal, vec3(0.0, 1.0, 0.0)));
        float dotZ = abs(dot(normal, vec3(0.0, 0.0, 1.0)));
        float maxDot = max(dotX, max(dotY, dotZ));
        if (maxDot == dotX)
            kd = texture(iChannel1, hitPoint.yz).xyz;
        else if (maxDot == dotY)
            kd = texture(iChannel1, hitPoint.xz).xyz;
        else
            kd = texture(iChannel1, hitPoint.xy).xyz;

        vec3 ka = ambientLight;
        color = ka + kd * dirLightColor * max(dot(normal, -dirLightDir), 0.0);
        color /= depth * 0.3;
        return true;
    }
    else
    {
	    color = bg;
        normal = vec3(0.0);
        return false;
    }

}

