struct triangle_t{
	vec2 a;
   	vec2 b;
    vec2 c;
};

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

//returns the pseudo-cross product in two dimensions.
float cross2(vec2 v1, vec2 v2){
    return v1.x*v2.y - v1.y*v2.x;
}


float direction(vec2 p1, vec2 p2, vec2 p3){
	return cross2(p3-p1,p2-p1);

}

//returns 1.0 if the segments a and b intersect strictly.
float intersect(vec2 a1, vec2 a2, vec2 b1, vec2 b2){

    float d1 = direction(b1,b2,a1);
    float d2 = direction(b1,b2,a2);
    float d3 = direction(a1,a2,b1);
    float d4 = direction(a1,a2,b2);

	if ( (((d1 > 0.0) && (d2 < 0.0)) || ((d1 < 0.0) && (d2 > 0.0))) &&
        (((d3 > 0.0) && (d4 < 0.0)) || ((d3 < 0.0) && (d4 > 0.0))) ){
    	return 1.0;
    }

    return 0.0;
}



float inside_tri (vec2 p, triangle_t t){

    float ca = cross2(t.a-t.b,t.a-p);
    float cb = cross2(t.b-t.c,t.b-p);
    float cc = cross2(t.c-t.a,t.c-p);

    float sa = sign(ca);
    float sb = sign(cb);
    float sc = sign(cc);

    if((sa == sb) && (sb == sc)){
    	return 1.0;
    };

    return 0.0;


}

vec2 rotate(vec2 p, float angle){
	return vec2(cos(angle)*p.x+sin(angle)*p.y,
                -sin(angle)*p.x+cos(angle)*p.y);

}

triangle_t rotate(triangle_t t, float angle){
	triangle_t ret;
    ret.a = rotate(t.a,angle);
    ret.b = rotate(t.b,angle);
    ret.c = rotate(t.c,angle);

    return ret;

}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = vec2(-1.0 + 2.0*fragCoord.xy / iResolution.xy);
    float ar = iResolution.x / iResolution.y;
    vec2 r =  vec2(uv.x * ar,uv.y);
    vec2 mouse = vec2(-1.0 + 2.0*iMouse.xy / iResolution.xy);

#ifdef MOUSE    

    vec2 mouser = vec2(mouse.x * ar,mouse.y);

#else    
    vec2 mouser = vec2 (cos(0.89*iTime),0.8*sin(0.5*iTime));

#endif    

    float staticn = rand(r*cos(iTime));
    float staticn2 = rand(r*sin(iTime));

    triangle_t triangles[TRIANGLE_NUMBER];


    triangles[0].a = vec2(-ar/2.0,0.0);
    triangles[0].b = vec2(-ar/2.0 + 0.4,0.0);
    triangles[0].c = vec2(-ar/2.0 + 0.2,sqrt(0.4)/2.0);

    triangles[0] = rotate(triangles[0],0.2*iTime);
    for(int i = 1;i<TRIANGLE_NUMBER;i++){
    	triangles[i] = rotate(triangles[i-1],2.0*PI/float(TRIANGLE_NUMBER));

    }


    vec2 lightpos = mouser;

    float shade = 0.0;

    for(int i = 0;i<TRIANGLE_NUMBER;i++){
		shade += intersect(triangles[i].a,triangles[i].b,lightpos,r);
        shade += intersect(triangles[i].a,triangles[i].c,lightpos,r); 
        shade += intersect(triangles[i].b,triangles[i].c,lightpos,r);        

    }



    float shape = 0.0;
    float lightshape = 0.0;

    for(int i = 0;i<TRIANGLE_NUMBER;i++){
        float s = inside_tri(r,triangles[i]);
        shape += s;

        if (s > 0.5){
        	lightshape += inside_tri(mouser,triangles[i]);
        }
    }





    float col = 0.6 + 0.2*(1.0-length(r));
    col +=1.0- smoothstep(0.0,staticn/3.0,length(mouser-r));
    col += 0.5 -clamp(length(mouser-r)*5.0,0.0,0.5);
    col += 0.2 - clamp(length(mouser-r)/2.0,0.0,0.2);

    if (shade > 0.5) col = staticn;
    if (shape > 0.5) col = staticn*staticn2;
    if (lightshape > 0.5) col = 1.0;



    fragColor=vec4(vec3(col),1.0);

}

