vec3 snoise_permute(vec3 x) {
	return mod((x * 34. + 1.) * x, 289.);
}
float snoise_2d(vec2 v) {
	vec4 C = vec4(.211324865405187, .366025403784439, -.577350269189626, .024390243902439);
	vec2 i = floor(dot(v, C.yy) + v);
	vec2 x0 = v - i + dot(i, C.xx);
	vec2 i1 = x0.x > x0.y ? vec2(1., 0.) : vec2(0., 1.);
	vec4 x12 = x0.xyxy + C.xxzz - vec4(i1, 0., 0.);
	i = mod(i, 289.);
	vec3 p = snoise_permute(snoise_permute(vec3(0., i1.y, 1.) + i.y) + i.x + vec3(0., i1.x, 1.));
	vec3 m = max(.5 - vec3(dot(x0, x0), dot(x12.xy, x12.xy), dot(x12.zw, x12.zw)), 0.);
	m = m * m * m * m;
	vec3 x = 2. * fract(p * C.www) - 1.;
	vec3 h = abs(x) - .5;
	vec3 ox = floor(x + .5);
	vec3 a0 = x - ox;
	m = (1.79284291400159 - .85373472095314 * (a0 * a0 + h * h)) * m;
	return dot(m, vec3(a0.x * x0.x + h.x * x0.y, a0.yz * x12.xz + h.yz * x12.yw)) * 130.;
}
hit scene(vec3 p) {
	return hit(abs(snoise_2d(p.xz + iTime) * .05 - (p.y + .05)), sin(p.x * 10.) + sin(p.z * 15.) + iTime * 5.);
}
vec3 skip_bulk(vec3 ray, vec3 dir, float mat) {
	ray;
	for(int temp_16 = 0; temp_16 < 100; ++temp_16) {
		hit cur = scene(ray);
		ray = ray + dir * max(cur.distance, .05);
		if(cur.distance > .01 || cur.material != mat) {
			break;
		}
	}
	return ray;
}
vec3 hsv_rgb(vec3 hsv) {
	return mix(vec3(1., 1., 1.), clamp(abs(mod(hsv.x / 60. + vec3(0., 4., 2.), 6.) - 3.) - 1., 0., 1.), hsv.y) * hsv.z;
}
material get_material(float id) {
	return material(vec4(hsv_rgb(vec3(id * 72., 1., 1.)), 1.), .3, 1., 1., 10., 5.);
}
vec4 shade(marched cur, vec3 normal, float level) {
	float specular;
	vec3 ivec = normalize(vec3(0., 1., -5.) - cur.pos);
	float incidence = max(dot(ivec, normal), 0.);
	material mat = get_material(cur.material);
	specular = 0.;
	if(mat.specular != 0. && incidence > 0.) {
		specular = pow(max(dot(normalize(ivec + cur.origin), normal), 0.), mat.specular) / length(vec3(0., 1., -5.) - cur.pos);
	}
	return vec4(mat.color.rgb * mat.color.a * (vec3(1., 1., 1.) * incidence * mat.diffuse + .1 * mat.ambient + specular) * pow(level, mat.reflection), mat.reflection);
}
vec3 march_one(vec3 ray, vec3 dir) {
	hit cur;
	float dist;
	ray;
	vec3 color;
	vec3 origin = ray;
	dist = 0.;
	color = vec3(0., 0., 0.);
	for(int temp_7 = 0; temp_7 < 10; ++temp_7) {
		cur = scene(ray);
		dist = min(dist + cur.distance, 20.);
		ray = ray + dir * cur.distance;
		if(dist >= 20.) {
			break;
		}
		if(cur.distance < .01) {
			break;
		}
	}
	marched mcur = marched(cur.distance < .01 ? dist : 20., cur.distance, origin, ray, cur.material, color);
	if(cur.distance < 20.) {
		vec3 normal = normalize(vec3(scene(ray + vec3(.00001, 0., 0.)).distance - scene(ray + vec3(-.00001, 0., 0.)).distance, scene(ray + vec3(0., .00001, 0.)).distance - scene(ray + vec3(0., -.00001, 0.)).distance, scene(ray + vec3(0., 0., .00001)).distance - scene(ray + vec3(0., 0., -.00001)).distance));
		vec4 shaded = shade(mcur, normal, .9);
		color = shaded.rgb;
	}
	return color;
}
marched march(vec3 ray, vec3 dir) {
	vec3 color;
	hit cur;
	float dist;
	ray;
	vec3 origin = ray;
	dist = 0.;
	color = vec3(0., 0., 0.);
