	float cyan_shadow = smoothstep(.01, .0, abs(uv.y-slope(uv.x, 3.,-.699)))/3.;
	float almost_bg_shadow = smoothstep(.02, .0, abs(uv.y-slope(uv.x, 1.8,-.39)))/3.;


    col += bigpurp_col * bigpurp;
    col += topred_col * topred;
        float f = .5;

    //col += vec3(1., -.1, -.5)*circle;                          	
	//if (circle < f)
      //  col -= circle_shadow;

    if (bigpurp < f && topred < f) {

        col -= topred_shadow;
        col -= bigpurp_shadow;
        col += midred_col * midred;
        if (midred < f) {

            col -= midred_shadow;
            col -= bigpurp_shadow;
            col += botred_col * botred;
            if (botred < f) {
                col-=botred_shadow;
                col += cyan_col * cyan;
            	if (cyan < f) {
                    col -= cyan_shadow;
                    col += whiteish_col * white;
                    if (white < f){
                        col += almost_bg_col * almost_bg;
                        if (almost_bg < f) {
                        	col -= almost_bg_shadow;
                			col += bg_col * bg;
                        }
                    }
                }
            }
        }
    }
        	col += noise(uv)/18.;

    fragColor = vec4(col,1.0);
}

vec4 rect = vec4(0,0,iResolution); // uniform
vec4 fillColor = vec4(1,0,0,1); // uniform
vec4 strokeColor = vec4(0,1,0,1); // uniform
float strokeWidth = abs(sin(iTime)) * 40.0; // uniform


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 vertCoord = fragCoord.xy; // varying


    vec2 ab = rect.zw / 2.0;
	vec2 center = rect.xy + ab;
    vec2 pos = vertCoord  - center;
    pos *= pos;
    float outerDist = dot(pos, 1.0 / (ab * ab));

	ab -= strokeWidth / 2.0;
    float innerDist = dot(pos, 1.0 / (ab * ab));
    float outerDelta = fwidth(outerDist) * 0.8;
    float innerDelta = fwidth(innerDist) * 0.8;

    float innerAlpha = smoothstep(1.0 - innerDelta, 1.0 + innerDelta, innerDist);
    float outerAlpha = smoothstep(1.0 - outerDelta, 1.0 + outerDelta, outerDist);

    vec4 sc = strokeColor * 1.0;//step(innerDelta, strokeWidth);

    fragColor = mix(sc, vec4(1, 1, 1, 1), outerAlpha);
	fragColor = mix(fillColor, fragColor, innerAlpha);
}

