void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;
    vec2 q = p - vec2(0.34, 0.7);

    vec3 col = mix(vec3(1.0, 0.4, 0.1), vec3(1.0, 0.8, 0.3), sqrt(p.y) * p.y);
    col *= smoothstep(1.0, 0.0, length(p - vec2(sin(iTime / 3.0) / 2.0 + 0.5, cos(iTime / 3.0) / 2.0)));

    float b = smoothstep(0.4, 0.2, length(p - vec2(sin(iTime / 3.0) / 2.0 + 0.5, cos(iTime / 3.0) / 2.0)));
    col = mix(col, vec3(1.0, 0.9, 0.3), b * sqrt(p.y));

    float r = 0.2 + 0.1 * cos(atan(q.y, q.x) * 10.0 + q.x * 20.0 + 1.0);    
    col *= smoothstep(r, r + 0.01, length(q));

    r = 0.015 * cos(q.y * 110.0) * 0.1 + 0.015;
    r += exp(p.y * -40.0);
    col *= 1.0 - (1.0 - smoothstep(r, r + 0.002, abs(q.x - 0.2 * sin(q.y * 2.0)))) * (1.0 - smoothstep(0.0, 0.1, q.y));

    #ifdef BOBMARLEY
    col *= texture(iChannel0, -p*2.0).xyz * 4.0;
    #endif

    fragColor = vec4(col, 1.0);    
}

