void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float factor1 = 5.0;
    float factor2 = 6.0;

    float blah =(sin(uv.y*factor2-iTime))+sin(uv.x*factor1-iTime*2.0);
    float blah2 =(sin(uv.x*factor2-iTime*2.0))+sin(uv.y*factor1-iTime*2.0);

	fragColor = vec4(uv, uv.y, 1.0) +
        		   vec4(blah-0.5,blah-0.5,blah+0.5,1.0)*
        		   vec4(blah2-0.5,blah2+0.3,blah2,1.0);
}

