void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= vec2(.5,.5);
    float aspect = iResolution.x/iResolution.y;
    uv.x *= aspect;

    float ringColor[3];
    for (int i=0; i<3; ++i)
    {
	    vec2 center = vec2(
            cos(iTime/4. +float(i*20)       )/2.,
            sin(iTime/4.3+float(i*20) + 100.)/2.);

        float dist = distance(uv,center);
        float frequency = (sin(iTime/4.7+float(i*3))/2.+.6)*32.;
   		float sdist = sin(dist*frequency + 3.14/2.);
   		float delta = fwidth(sdist);
        float pulseWidth = sin(iTime/5.+float(i*13))/1.01;
   		ringColor[i] = smoothstep(sdist-delta,sdist,pulseWidth);
    }

	fragColor = vec4(
        ringColor[0],
        ringColor[1],
        ringColor[2],
        1);
}

