    // speed of the dots
    S.x -= iTime*4.0;						// animate stream

    vec2 sxy = sin(3.14159*S);
    float a = sxy.x * sxy.y;					// combine sine waves using product

    a = 0.5*a + 0.5;							// remap to [0..1]
	a = smoothstep( 0.85-b, 0.85+b, a );		// threshold

