const int MaxIter = 16;
float preci;
float zoom;


vec2 cmul( vec2 a, vec2 b )  { return vec2( a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x ); }
vec2 csqr( vec2 a )  { return vec2( a.x*a.x - a.y*a.y, 2.*a.x*a.y  ); }

vec3 dmul( vec3 a, vec3 b )  {
    float r = length(a);

    b.xy=cmul(normalize(a.xy), b.xy);
    b.yz=cmul(normalize(a.yz), b.yz);
    b.xz=cmul(normalize(a.xz), b.xz);

    return r*b;
}


vec2 iSphere( in vec3 ro, in vec3 rd, in vec4 sph )
{
	vec3 oc = ro - sph.xyz;
	float b = dot( oc, rd );
	float c = dot( oc, oc ) - sph.w*sph.w;
	float h = b*b - c;
	if( h<0.0 ) return vec2(-1.0);
	h = sqrt(h);
	return vec2(-b-h, -b+h );
}


vec4 map( vec3 p)
{
	float dr = 1.0;
	vec3 ot = vec3(1000.0);
        float ot1=1.;
	float r2;

        vec3 c= p;
	for( int i=0; i<MaxIter;i++ )
	{            
            r2 = dot(p,p);
            if(r2>16.)continue;

            ot = min( ot, abs(p) );
            ot1=min(ot1,r2);
            dr=2.*sqrt(r2)*dr+1.0;
            p=dmul(p,p)+c;						
	}	
	float d =.5*length(p)*log(length(p))/dr;	
        return vec4(ot/(.3+ot1),d);	
}



vec4 raymarch( in vec3 ro, in vec3 rd , in vec2 tminmax )
{
	vec4 col = vec4(0, 0, 0, 0);

	float t = tminmax.x;
        float dt = 1.;
	for(int i=0; i<128; i++)
	{

        if( t>tminmax.y || dt<=preci*t ) continue;
		vec3 pos = ro + t*rd;		
		col = map( pos );
		dt = col.a;		
                t += dt;                        		
	}
        col =clamp( col, 0.0, 1.0 );
	col.a = min(0.002/col.a,1.);

	return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0*q;
    p.x *= iResolution.x/ iResolution.y;
    vec2 mo = iMouse.xy / iResolution.xy;
    float an = 2.0 + 0.2*iTime - mo.x;

    preci = .3/min(iResolution.x,iResolution.y);
	zoom = 1.+.4*sin(.1*iTime);

    vec3 ro = zoom*3.0*vec3(cos(an), 0.5*cos(2.*an), sin(an));
    vec3 ta = vec3(0.0, 0.0, 0.0);
    vec3 ww = normalize( ta - ro);
    vec3 uu = normalize( cross( vec3(0.0,1.0,0.0), ww ) );
    vec3 vv = normalize( cross(ww,uu) );
    vec3 rd = normalize( p.x*uu + p.y*vv + 2.0*ww );

    vec3 col = vec3(0.08,0.09,0.18);
    vec2 seg = iSphere( ro, rd, vec4(0.0,0.0,0.0,2.0) );
	if( seg.x<0.0 )seg.x=0.;
    if( seg.y>0.0 )
		{
        	vec4 res = raymarch( ro, rd, seg );
            vec3 col1 =.5*(res.xyz+res.yxz);
            col1.r+=.5*col1.g-.3*col1.b;            
	    	col = mix( col, col1, res.a );
		}

	col = pow( col, vec3(0.4) ) * 1.2;

	col *= sqrt( 16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y) );

    fragColor = vec4( col, 1.0 );
}

