// Constant state data

#define NUM_SPHERES 6
#define NUM_PLANES 5
#define NUM_ITERATIONS 4
#define SRO ((curr_iteration == 0) ? 4.0 : 1.0)
#define SHADOW_RAYS (SRO*SRO*SRO)

vec4 g_spheres[NUM_SPHERES];
vec4 g_sphereColors[NUM_SPHERES];
vec4 g_planes[NUM_PLANES];
vec4 g_planeColors[NUM_PLANES];

// Variable global state
int curr_iteration;
vec4 g_light;

bool isect_sphere(vec3 o, vec3 d, vec3 c, float rsq, out float t)
{
    vec3 l = c-o;
    float tca = dot(l,d);
    if (tca < 0.0) return false;
    float dsq = -tca*tca + dot(l,l);
    if (dsq > rsq) return false;
    float thc = sqrt(rsq - dsq);
    t = tca - thc;
    return true;    
}

float fmod(float x, float y)
{
  return x - y * floor(x/y);
}

bool shadowRayCast(vec3 r, vec3 light_pos)
{
    vec3 d = light_pos - r;

    float lowest_t = length( d );

    d = normalize(d);

    bool hit = false;

    for( int j = 0; j < NUM_SPHERES - 1; ++j )
    {
		float t;
        if( isect_sphere( r, d, g_spheres[j].xyz, g_spheres[j].w, t ) )
        {
			if( t < lowest_t )
            {
                lowest_t = t; hit = true;
            }
        }
    }

    for( int j = 0; j < NUM_PLANES; ++j )
    {
		vec3 np = g_planes[j].xyz;
        float np_dot_d = dot(np, d);
        if( abs(np_dot_d) > 0.001 )
        {
            float t = (g_planes[j].w - dot(np, r)) / np_dot_d;
            if( t > 0.0 && t < lowest_t )
            {
				lowest_t = t; hit = true;
            }
        }
    }

    return hit;
}

float getShadowTerm(vec3 r)
{
	float light_amount = 1.0;

    if( curr_iteration == 0 )
    { 
        for( int i=0; i < 64; ++i )
        {
            vec3 light_pos = g_light.xyz;
			float ofsx = fmod(float(i), SRO);
        	float ofsy = (float(i)*SRO)/SRO;
    		float ofsz = fmod(float(i)*SRO*SRO, SRO);
    		vec3 offset = 1.0 * vec3(ofsx, ofsy, ofsz);
    		light_pos += offset;

        	if( shadowRayCast(r, light_pos) ) 
        	{
				light_amount -= 1.0 / 64.0;
        	}
  	  	}
    }
    else
    {
        // LOLHAX!!!!
        light_amount = 0.33;
        if( shadowRayCast(r, g_light.xyz) )
        {
			light_amount = 0.0;
        }
    }

    return light_amount;
}

vec4 isect( inout vec3 r, inout vec3 d, inout float reflectance )
{
	float lowest_t = 10000000.0;
    bool hit = false;
    vec3 n = vec3(0, 1, 0);
    vec3 p = vec3(0, 0, 0);
    vec3 c = vec3(0, 0, 0);

    for( int i = 0; i < NUM_SPHERES; ++i )
    {
		float t;
        if( isect_sphere( r, d, g_spheres[i].xyz, g_spheres[i].w, t ) )
        {
         	if( t < lowest_t )
            {
				p = r + d * t;
                n = normalize(p - g_spheres[i].xyz);
                c = g_sphereColors[i].rgb;
                reflectance = g_sphereColors[i].w;
                lowest_t = t;
                hit = true;
            }
        }
    }

    for( int i = 0; i < NUM_PLANES; ++i )
    {
		vec3 np = g_planes[i].xyz;
        float np_dot_d = dot(np, d);

        if( abs(np_dot_d) > 0.001 )
        {
			float t = (g_planes[i].w - dot(np, r)) / np_dot_d;
            if( t > 0.0 && t < lowest_t )
            {
				p = r + (d * t);
                n = np;
                bool xeven = fract(p.x * 0.1) > 0.5;
                bool zeven = fract(p.z * 0.1) > 0.5;

                c = g_planeColors[i].rgb;
                reflectance = g_planeColors[i].w;
                lowest_t = t; hit = true;
            }
        }
    }

    if( hit )
    {
		vec3 l = (g_light.xyz - p);
        float dist = length( l );
        l = l / dist;

        float ndotl = dot(n, l) * clamp(1.0 / dist*dist, 0.0, 1.0);
        vec3 ambient = clamp(c.rgb * 0.25, 0.0, 1.0);
        vec3 diffuse = clamp(ndotl * c.rgb, 0.0, 1.0);
        float fresnel = 1.0 - clamp( abs( dot( n, d ) ), 0.0, 0.5);
        reflectance = fresnel*fresnel * reflectance;

        vec3 inc = normalize(p-r);
        r = p + n;
        d = normalize( reflect(inc, n) );

        vec3 rl = reflect( l, n );
        float rdotv = pow( clamp( dot( rl, d ), 0.0, 1.0 ), 3.0 );
        vec3 specular = vec3(rdotv, rdotv, rdotv);
        vec3 result = ambient.rgb;
        result += getShadowTerm( r ) * (diffuse.rgb + specular.rgb);

        return vec4( result.rgb, 1 );
    }

    reflectance = 0.0;
    return vec4( 0, 0, 0, 0 );
}

