	vec2 point = floor(mod(iTime, 48.) / 12.) == 0. ? vec2(-.746, .09997) : floor(mod(iTime, 48.) / 12.) == 1. ? vec2(.275, .0077) : floor(mod(iTime, 48.) / 12.) == 2. ? vec2(-.101, .837) : vec2(-.088, .6555);
	float mtime = 6. - abs(6. - mod(iTime, 12.));
	vec2 z = vec2(0., 0.);
