// CONSTANTS

#define PI 3.14159
#define TWO_PI 6.28318
#define PI_OVER_TWO 1.570796
#define ONE_OVER_PI 0.318310

#define SMALL_FLOAT 0.0001
#define BIG_FLOAT 1000000.

// **************************************************************************
// DEFINES

#define COWL_MATL 1.
#define LAMP_MATL 2.
#define BULB_MATL 3.
#define FLOOR_MATL 4.
#define SPRING_MATL 5.
#define TAIL_MATL 6.
#define WALL_MATL 7.
#define DEBUG_MATL 10.

