// **************************************************************************
// UTILITIES

// Rotate the input point around the y-axis by the angle given as a  cos(angle)
// and sin(angle) argument.  There are many times where  I want to reuse the
// same angle on different points, so why do the  heavy trig twice.
vec3 rot_around_y( vec3 point, float cosangle, float sinangle )
{
    return vec3(point.x * cosangle  + point.z * sinangle,
                point.y,
                point.x * -sinangle + point.z * cosangle);
}

// Rotate the input point around the x-axis by the angle given as a  cos(angle)
// and sin(angle) argument.  There are many times where  I want to reuse the
// same angle on different points, so why do the  heavy trig twice.
vec3 rot_around_x( vec3 point, float cosangle, float sinangle )
{
    return vec3(point.x,
                point.y * cosangle - point.z * sinangle,
                point.y * sinangle + point.z * cosangle);
}

// Rotate the input point around the 2d origin by the angle given as a cos(angle)
// and sin(angle) argument.
vec2 rot_vec2( vec2 xy, float cosangle, float sinangle )
{
    return vec2(xy.x * cosangle - xy.y * sinangle,
                xy.x * sinangle + xy.y * cosangle);
}

vec3 orient_to_y( vec3 p, vec3 lookdir )
{
    // assume lookdir is a normalized vector that we will use to
    // normalize with the y-axis

    // if lookdir is pointing exactly in the positive or negative
    // y direction, then we can return the positive or negative
    // identity respectively.
    if (abs(lookdir.y) >= 1.) return sign(lookdir.y) * p;

    vec3 v1 = lookdir;
    vec3 up = vec3(0., 1., 0.); // assuming up vector in world is y
    vec3 v3 = normalize( cross(v1, up) );
    vec3 v2 = cross(v3, v1);

    // orthogonal matrix so inverse == transpose
    return vec3( dot(p,v2),
                 dot(p,v1),
                 dot(p,v3) );
}

float pow5(float v)
{
    float tmp = v*v;
    return tmp*tmp*v;
}

vec2 mergeobjs(vec2 a, vec2 b) { return mix(b, a, step(a.x, b.x)); }
float uniondf(float a, float b) { return min(a, b); }
float intersdf(float a, float b) { return max(a, b); }
float diffdf(float a, float b) { return max(a, -b); }

#define NOISE_DIMENSION 64.

float noise1f( float n )
{   

    vec2 coords = vec2(mod(floor(n),NOISE_DIMENSION)/NOISE_DIMENSION, 
                       floor(n/NOISE_DIMENSION)/NOISE_DIMENSION);

    return texture(iChannel0, coords, -100. ).r;
} 

// **************************************************************************
// INTERSECTION FOR ACCELERATION STRUCTURE

// intersection for a sphere with a ray. If the ray origin is inside the
// sphere or there is any interesection, >1 is returned, otherwise 0.

float intersect_sphere(vec3 ro, vec3 rd, float r, vec3 sphc)
{

    vec3 so = ro - sphc;

    float a = dot(rd, rd);
    float b = dot(so, rd);
    float c = dot(so, so) - r*r;
    float discr = b*b - a*c;

    float zero_discr = step(SMALL_FLOAT, discr);
    float tmin = (-b - sqrt(discr))/a;

    return zero_discr * (step(0., tmin) + step(dot(so, so), r*r)); 
}

// **************************************************************************
// DISTANCE FIELDS

float roundboxdf( vec3 p, vec3 bounds, float r )
{
    return length(max(abs(p)-bounds * vec3(1., .5, 1.),0.0))-r;
}

float cylinderdf( vec3 p, float r, float h)
{
    return max( length(p.xz)-r, abs(p.y) - h*.5 );
}

float spheredf( vec3 p, float r )
{
    return length(p) - r;    
}

float clippedconedf( vec3 p, vec2 dims, vec2 clips )
{

    vec2 q = vec2( length(p.xz), p.y );
    return max( max( dot(q, dims.xy), p.y), max(p.y+clips.x, -p.y-clips.y ));
}

float torusdf( vec3 p, float r, float d )
{
  vec2 q = vec2(length(p.xz)-r,p.y);
  return length(q)-d;
}

float oroundboxdf( vec3 p, vec3 a, vec3 b, 
                   vec3 bounds, float r )
{
    vec3 o = p - a;
    o = orient_to_y(o, normalize(b - a)); o.y -= bounds.y * .5;
    return roundboxdf(o, bounds, r);
}

float ocylinderdf( vec3 p, vec3 a, vec3 b, 
                   float r, float h)
{
    vec3 o = p - a;
    o = orient_to_y(o, normalize(b - a)); o.y -= h * .5;
    return cylinderdf(o, r, h);
}


float ospheredf( vec3 p, vec3 a, vec3 b,
                float r )
{
    vec3 o = p - a;
    o = orient_to_y(o, normalize(b - a)); o.y *= -1.; o.y += r;
    return spheredf(o, r);
}

float oclippedconedf( vec3 p, vec3 a, vec3 b, 
                      vec2 dims, vec2 clips)
{
    vec3 o = p - a;
    o = orient_to_y(o, normalize(b - a));

    return clippedconedf(o, dims, clips);
}


vec2 lampobj( vec3 p, vec3 rd, vec3 a, vec3 b )
{

    vec2 obj = vec2(BIG_FLOAT, -1.);

    vec3 o = p - a;    
    o = orient_to_y(o, normalize(b - a)); // EXPENSIVE

    obj.x = uniondf( obj.x, spheredf(o - vec3(0., .16, 0.), .365));

    o.x += -.09;

    float ty = -.04;

    // cowl
    vec2 cowl = vec2(BIG_FLOAT, COWL_MATL);
    vec3 cowlo = o; cowlo.y += ty;

    float c = min(0., -.55*cos(4.8*cowlo.y-.25)*(1.2 * cowlo.y- .06) - .1);

    cowlo.xz += .7 * c * normalize(cowlo.xz);

    float m = .11;

    cowlo *= -1.; cowlo.y -= .4;
    cowl.x = clippedconedf(cowlo, 
                  vec2(.4, m), vec2(0.21, 0.7));

    cowl.x = diffdf( cowl.x, clippedconedf( cowlo, 
                     vec2(.4, m * .95), vec2(0.22, 1.)));

    // - vent slits
    float numSlits = 16.;
    float ang = TWO_PI * (mod(atan(o.x,o.z)/TWO_PI, 1./numSlits) - (.5/numSlits));
    float l = length(o.xz);
    vec3 modo = vec3(l * cos(ang), o.y + ty, l * sin(ang));

    cowl.x = diffdf(cowl.x, ocylinderdf(modo, 
                                         vec3(.08, -0.16, 0.0), vec3(.42, 1., 0.), 
                                        .008, .07)); // EXPENSIVE


    // + nubbin and base
    cowl.x = uniondf(cowl.x, cylinderdf(o + vec3(0., .18 + ty, 0.), .017, .08));
    cowl.x = uniondf(cowl.x, spheredf((vec3(.6, 1., 0.6) * o) + vec3(0., .22 + ty, 0.), .018));

    // + bulb seat
    cowl.x = uniondf(cowl.x, cylinderdf(o + vec3(0., .12 + ty, 0.), .045, .12));

    // + bulb
    vec2 bulb = vec2(BIG_FLOAT, BULB_MATL);
    bulb.x = spheredf(o - vec3(0., .12 - ty, 0.), .15);

    obj = mergeobjs(cowl, bulb);

    return obj;
}

vec2 upperarm( vec3 p, vec3 rd, vec3 a, vec3 b)
{
    vec2 obj = vec2(BIG_FLOAT, LAMP_MATL);

    vec3 o = p - a;
    o = orient_to_y(o, normalize(b - a)); // EXPENSIVE

    vec3 t = vec3(-0.05, -.045, 0.);
    o += t;

    // symmetry along the xy plane
    o.z = abs(o.z);

    // neck
    obj.x = cylinderdf(o - vec3(-.05, .51, 0.), .025, .12);

    obj.x = uniondf( obj.x, cylinderdf(o - vec3(-.05, .59, 0.),  .04, .07));

    // + rear top block
    obj.x = uniondf( obj.x, oroundboxdf(o - vec3(-.06, .5,.035),
                                        vec3(0.), vec3(.25, -.25, .0),
                                        vec3(.022, .19, .01), .002)); // EXPENSIVE

    // + front top block
    obj.x = uniondf( obj.x, oroundboxdf(o - vec3(-.05, .5, .035),
                                        vec3(.0), vec3(-.08, -.3, .0),
                                        vec3(.022, .19, .01), .002)); // EXPENSIVE

    // + rear top rod
    obj.x = uniondf( obj.x,
                    cylinderdf(o - vec3(.06, .24, .0), .025, .35));

    // + front top rod
    obj.x = uniondf( obj.x,
                    cylinderdf(o - vec3(-.09, .22, .0), .025, .35));

    // + bolts
    obj.x = uniondf( obj.x, spheredf(o - vec3(-.05,  .48, .04), .016));
    obj.x = uniondf( obj.x, spheredf(o - vec3(.06, .38, .04), .016));
    obj.x = uniondf( obj.x, spheredf(o - vec3(-.09, .35, .04), .016));

    // + horizontal rods and nubs
    vec3 hrodo = o - vec3(0.06, .09, 0.);
    hrodo = orient_to_y(hrodo, normalize(vec3(0., 0., 1.))); // EXPENSIVE

    obj.x = uniondf( obj.x, cylinderdf(hrodo, .01, .15));
    obj.x = uniondf( obj.x, cylinderdf(hrodo - vec3(.0, .07, .0), .015, .01));
    obj.x = uniondf( obj.x, cylinderdf(hrodo - vec3(.125, .0, .15), .01, .15));
    obj.x = uniondf( obj.x, cylinderdf(hrodo - vec3(.125, .07, .15), .015, .01));

    // + upper spring
    vec2 springobj = vec2(BIG_FLOAT, SPRING_MATL);

    vec3 springo = o;
    springo -= vec3(.055, .09, .06);
    springo = orient_to_y(springo, normalize(vec3(-1., 0.85, 0.))); // EXPENSIVE
    springo.y -= .1;
    float  c = cos(240.0*springo.y);
    float  s = sin(240.0*springo.y);
    mat2   m = mat2(c,s,-s,c);
    springo.xz = m*springo.xz;
    springo -= vec3(.0, .0, .01);
    springobj.x = cylinderdf(springo, .0095, .2);

    obj = mergeobjs(obj, springobj);


    return obj;
}

vec2 elbow( vec3 p, vec3 rd, vec3 a, vec3 b)
{

    vec2 obj = vec2(BIG_FLOAT, LAMP_MATL);

    vec3 o = p - a;
    o = orient_to_y(o, normalize(b - a)); // EXPENSIVE

    vec3 t = vec3(-0.05, -.045, 0.);
    o += t;

    // symmetry along the xy plane
    o.z = abs(o.z);

    // rear mid block   
    vec3 baro = o - vec3(-.063, -.04, .035);
    vec3 barb = vec3(.3, .3, .01);
    vec3 bounds = vec3(.05, .21, .01);
    baro = orient_to_y(baro, normalize(barb)); baro.y -= bounds.y * .5; // EXPENSIVE
    baro.x *= mix(1., 2.5, smoothstep(-.2, .26, baro.y));
    obj.x = uniondf( obj.x, roundboxdf(baro, bounds, .002));

    // + front mid block
    obj.x = uniondf( obj.x, oroundboxdf(o - vec3(-.06, -.08, .035),
                                        vec3(.0), vec3(-.1644, .9864, .0),
                                        vec3(.025, .21, .01), .002)); // EXPENSIVE  
    // + bolt
    obj.x = uniondf( obj.x, spheredf(o - vec3(.0, .0, .04)-t, .016));

    return obj;
}

vec2 lowerarm( vec3 p, vec3 rd, vec3 a, vec3 b)
{
    vec2 obj = vec2(BIG_FLOAT, LAMP_MATL);

    vec3 o = p - a;
    o = orient_to_y(o, normalize(b - a)); 

    // symmetry along the xy plane
    o.z = abs(o.z);

    // rear bottom rod
    obj.x = roundboxdf(o - vec3(0.01, .2, 0.),
                       vec3(0.022, .35, 0.012), .007);

    // + rear ankle joint    
    vec2 rearank = rot_vec2(vec2(0.9578, 0.2873), g_lux.celbowang, g_lux.selbowang);
    obj.x = uniondf( obj.x, oroundboxdf(o - vec3(-.01, .04,.035),
                                        vec3(0.), vec3(rearank.x, rearank.y, .0),
                                        vec3(.022, .25, .01), .002));

    // + front ankle joint
    vec2 frontank = rot_vec2(vec2(0.9987, -.05), g_lux.celbowang, g_lux.selbowang);
    obj.x = uniondf( obj.x, oroundboxdf(o - vec3(-.01, .04,.035),
                                        vec3(0.), vec3(frontank.x, frontank.y, .0),
                                        vec3(.018, .18, .01), .002));

    // + horizontal rods and nubs

    vec2 tfo = vec2(-.01, .04) + .155 * frontank;
    vec2 tro = vec2(-.01, .04) + .22 * rearank;

    vec3 hrodo = orient_to_y(o, normalize(vec3(0., 0., 1.)));

    obj.x = uniondf( obj.x, cylinderdf(hrodo-vec3(tfo.y, 0., -tfo.x), .01, .26));    
    obj.x = uniondf( obj.x, cylinderdf(hrodo-vec3(tfo.y + 0.15, 0., -tfo.x), .01, .2));
    obj.x = uniondf( obj.x, cylinderdf(hrodo-vec3(tro.y, 0., -tro.x), .01, .16));

    // + zig zag strut    
    obj.x = uniondf( obj.x, roundboxdf(o - vec3(tfo.x, tfo.y + .09, 0.105),
                       vec3(0.011, .2, 0.008), .007));

    obj.x = uniondf( obj.x, oroundboxdf(o - vec3(tfo.x, tfo.y + .185, 0.105),
                                        vec3(0.), vec3(0., 1., -1.),
                                        vec3(0.008, .05, 0.011), .007));

    obj.x = uniondf( obj.x, roundboxdf(o - vec3(tfo.x, tfo.y + .285, 0.07),
                       vec3(0.011, .13, 0.008), .007));

    // + bolts
    vec2 bfo = vec2(-.01, .04) + .02 * frontank;
    obj.x = uniondf( obj.x, spheredf(o - vec3(bfo.x, bfo.y, .04), .016));
    obj.x = uniondf( obj.x, spheredf(o - vec3(tfo.x, tfo.y + .33, .085), .016));

    // + lower spring
    vec2 springobj = vec2(BIG_FLOAT, SPRING_MATL);

    vec3 so = vec3(tfo.x + .03, tfo.y + .115, .07);
    vec3 springo = orient_to_y(o - so, normalize(vec3(tro.x - so.x, tro.y - so.y, 0.)));
    springo.y *= 0.06/length(vec3(tro.x - so.x, tro.y - so.y, 0.));
    float  c = cos(300.0*springo.y);
    float  s = sin(300.0*springo.y);
    mat2   m = mat2(c,s,-s,c);
    springo.xz = m*springo.xz;
    springo -= vec3(.0, .0, .01);
    springobj.x = cylinderdf(springo, .0095, .1);

    obj = mergeobjs(obj, springobj);

    return obj;
}

vec2 base( vec3 p, vec3 rd, vec3 a, vec3 b)
{    
    vec2 obj = vec2(BIG_FLOAT, LAMP_MATL);

    vec3 o = p - a;    

    o = orient_to_y(o, normalize(b - a));

    vec3 baseo = o;
    baseo *= vec3(.9, .9, .9);
    baseo.y += -.16;

    // base ring rounded curve
    obj.x = torusdf( baseo, .3, .05);

    // + base cylinder
    obj.x = uniondf( obj.x, cylinderdf( baseo, .3, .1));

    // - base bottom
    obj.x = diffdf( obj.x, -baseo.y );

    // + base bottom piping
    obj.x = uniondf( obj.x, torusdf(baseo, .35, .015));

    baseo.y += .0603;

    // + base neck scarf
    float baseneck = cylinderdf( baseo, .08, .05);
    baseneck = diffdf( baseneck, torusdf(baseo + vec3(0., .012, 0.), .07, .024));
    obj.x = uniondf( obj.x, baseneck);

    baseo.y += .025;

    // + base neck piping 
    obj.x = uniondf( obj.x, torusdf(baseo, .045, .015));

    // + base neck
    obj.x = uniondf( obj.x, cylinderdf(baseo, .025, .2));

    return obj;
}

vec2 tailobj( vec3 p, vec3 rd, vec3 a)
{    
    vec2 obj = vec2(BIG_FLOAT, TAIL_MATL);

    vec3 o = p - a;    

    o.z += .2 * sin(2. * o.x);

    obj.x = ocylinderdf( o, vec3(.4, -.18, 0.2), vec3(1., -.18, 0.2), .015, 5.);// EXPENSIVE

    return obj;
}

vec2 floorobj( vec3 pos ) 
{
    return vec2(abs( pos.y + 0.3 ), FLOOR_MATL);
}

vec2 wallsobj( vec3 pos )
{
    return vec2(10. - length(pos.xz), WALL_MATL);
}

// **************************************************************************
// SCENE MARCHING

vec2 scenedf( vec3 pos, vec3 rd )
{
    vec2 obj = vec2(BIG_FLOAT, -1.);

    // Base
    vec3 sphc = g_lux.footjoint - vec3(0., .34, .0);
    float sphr = .44;
    if (intersect_sphere(pos, rd, sphr, sphc) > .5)
    {    
        obj = mergeobjs(obj, base( pos, rd, g_lux.footjoint, g_lux.footorient));

        #ifdef DEBUG_ACCEL_MARCH
        obj = mergeobjs(obj, vec2(spheredf(pos - sphc, sphr), DEBUG_MATL));
        #endif
    }

    // Lower Arm
    sphc = .4 * (g_lux.footjoint + g_lux.midjoint);
    sphr = .85 * length(g_lux.midjoint - g_lux.footjoint);
    if (intersect_sphere(pos, rd, sphr, sphc) > .5)
    {    
        obj = mergeobjs(obj, lowerarm( pos, rd, g_lux.footjoint, g_lux.midjoint));

        #ifdef DEBUG_ACCEL_MARCH
        obj = mergeobjs(obj, vec2(spheredf(pos - sphc, sphr), DEBUG_MATL));
        #endif
    }

    // Elbow
    sphc = g_lux.midjoint + vec3(-.01, .12, .0);
    sphr = .18;
    if (intersect_sphere(pos, rd, sphr, sphc) > .5)
    {    
        obj = mergeobjs(obj, elbow(pos, rd, g_lux.midjoint, g_lux.headjoint));

        #ifdef DEBUG_ACCEL_MARCH
        obj = mergeobjs(obj, vec2(spheredf(pos - sphc, sphr), DEBUG_MATL));
        #endif
    }

    // Upper Arm
    sphc = .5 * (g_lux.midjoint + g_lux.headjoint);
    sphr = .5 * length(g_lux.headjoint - g_lux.midjoint);
    if (intersect_sphere(pos, rd, sphr, sphc) > .5)
    {    
        obj = mergeobjs(obj, upperarm(pos, rd, g_lux.midjoint, g_lux.headjoint));

        #ifdef DEBUG_ACCEL_MARCH
        obj = mergeobjs(obj, vec2(spheredf(pos - sphc, sphr), DEBUG_MATL));
        #endif
    }

    // Lamp Head 
    sphc = g_lux.headjoint + vec3(-.2, .05, 0.);
    sphr = .5;
    if (intersect_sphere(pos, rd, sphr, sphc) > .5)
    {    
        obj = mergeobjs(obj, lampobj( pos, rd, g_lux.headjoint, g_lux.headorient) );
        #ifdef DEBUG_ACCEL_MARCH
        obj = mergeobjs(obj, vec2(spheredf(pos - sphc, sphr), DEBUG_MATL));
        #endif
    }

    // Lamp Tail
    obj = mergeobjs(obj, tailobj( pos, rd, g_lux.footjoint) );

    // distance from a floor
    obj = mergeobjs(obj, floorobj( pos ));

    // distance from surrounding cylinder wall
    obj = mergeobjs(obj, wallsobj( pos ));

    return obj;
}

#define DISTMARCH_STEPS 60
#define DISTMARCH_MAXDIST 40.

vec2 distmarch( vec3 ro, vec3 rd, float maxd )
{    
    float dist = 10. * SMALL_FLOAT;
    float t = 0.;
    float material = 0.;
    for (int i=0; i < DISTMARCH_STEPS; i++) 
    {
        if ( abs(dist) < SMALL_FLOAT || t > maxd ) break;
        // advance the distance of the last lookup
        t += dist;
        vec2 dfresult = scenedf( ro + t * rd, rd );
        dist = dfresult.x;
        material = dfresult.y;
    }

    if( t > maxd ) material = -1.0; 
    return vec2( t, material );
}

// **************************************************************************
// SHADOWING & NORMALS

vec3 compute_normal( vec3 p )
{
    vec3 d = vec3( 0.001, 0.0, 0.0 );
    vec3 n = vec3(
        scenedf(p + d.xyy, normalize(d.xyy)).x - scenedf(p - d.xyy, -normalize(d.xyy)).x,
        scenedf(p + d.yxy, normalize(d.yxy)).x - scenedf(p - d.yxy, -normalize(d.yxy)).x,
        scenedf(p + d.yyx, normalize(d.yyx)).x - scenedf(p - d.yyx, -normalize(d.yyx)).x );
    return normalize( n );
}

#define SOFTSHADOW_STEPS 90
#define SOFTSHADOW_STEPSIZE .025

float soft_shadow( vec3 ro, 
                      vec3 rd, 
                      float mint, 
                      float maxt, 
                      float k )
{
    float shadow = 1.0;
    float t = mint;

    for( int i=0; i < SOFTSHADOW_STEPS; i++ )
    {
        if( t < maxt )
        {
            float h = scenedf( ro + rd * t, rd ).x;
            shadow = min( shadow, k * h / t );
            t += SOFTSHADOW_STEPSIZE;
        }
    }
    return clamp( shadow, 0.0, 1.0 );

}

#define AO_NUMSAMPLES 8
#define AO_STEPSIZE .02
#define AO_STEPSCALE .7

float ambient_occlusion( vec3 p, 
              vec3 n )
{
    float ao = 0.0;
    float aoscale = 1.0;

    for( int aoi=0; aoi < AO_NUMSAMPLES ; aoi++ )
    {
        float step = 0.01 + AO_STEPSIZE * float(aoi);
        vec3 aop =  n * step + p;

        float d = scenedf( aop, n ).x;
        ao += -(d-step)*aoscale;
        aoscale *= AO_STEPSCALE;
    }

    return clamp( ao, 0.0, 1.0 );
}

// **************************************************************************
// CAMERA

struct CameraData
{
    vec3 origin;
    vec3 dir;
    vec2 st;
};

CameraData setup_camera(vec2 fragCoord)
{

    // aspect ratio
    float invar = iResolution.y / iResolution.x;
    vec2 st = fragCoord.xy / iResolution.xy - .5;
    st.y *= invar;

    // calculate the ray origin and ray direction that represents
    // mapping the image plane towards the scene
    vec3 iu = vec3(0., 1., 0.);

    vec3 iz = normalize( g_camPointAt - g_camOrigin );
    vec3 ix = normalize( cross(iz, iu) );
    vec3 iy = cross(ix, iz);

    vec3 dir = normalize( st.x*ix + st.y*iy + 1.0 * iz );

    return CameraData(g_camOrigin, dir, st);

}

// **************************************************************************
// SHADING

struct SurfaceData
{
    vec3 point;
    vec3 normal;
    vec3 basecolor;
    vec3 emissive;
    float roughness;
    float specular;
    float metallic;
    float ambocc_amount;
    float cowl_shadow;
};

#define INITSURF(p, n) SurfaceData(p, n, vec3(0.), vec3(0.), 0., 1., 0., 1., 1.)

struct BRDFVars
{
    // vdir is the view direction vector
    vec3 vdir;
    // The half vector of a microfacet model 
    vec3 hdir;
    // cos(theta_h) - theta_h is angle between half vector and normal
    float costh; 
    // cos(theta_d) - theta_d is angle between half vector and light dir/view dir
    float costd;      
    // cos(theta_l) - theta_l is angle between the light vector and normal
    float costl;
    // cos(theta_v) - theta_v is angle between the viewing vector and normal
    float costv;
};


void calc_material(float matid,
                   inout SurfaceData surf)
{
    vec3 surfcol = vec3(1.);
    if (matid - .5 < COWL_MATL) 
    { 
        surf.basecolor = vec3(.95); 
        surf.roughness = .25;
        surf.metallic = .0;
        surf.specular = 1.;
        surf.ambocc_amount = 0.;
        surf.cowl_shadow = 0.;
    } 
    else if (matid - .5 < LAMP_MATL) 
    { 
        surf.basecolor =  vec3(.95); 
        surf.roughness = .25;
        surf.metallic = 0.;
        surf.specular = 1.;
    } 
    else if (matid - .5 < BULB_MATL)
    {
        surf.ambocc_amount = 0.;
        surf.basecolor = vec3(.3);
        surf.emissive = 2.8 * vec3(1., 1., .6);
        surf.roughness = 0.;
        surf.specular = 1.;
    }
    else if (matid - .5 < FLOOR_MATL)
    {
        float board = 1.2 * surf.point.x;
        float rboard = noise1f(floor(board));
        float grainrot = mix(-.5, .5, rboard);
        vec2 boarduv = rot_vec2(vec2(.5, 1.) * surf.point.zx, cos(grainrot), sin(grainrot)) + vec2(40.323, 17.232) * rboard;

        vec4 pavem = texture(iChannel1, boarduv);
        float floordivide = smoothstep(.0, .03, fract(board)) * smoothstep(1., .99, fract(board));
        surf.basecolor = pavem.rgb * (.3 + .7 * floordivide);
        surf.metallic = .0;
        surf.roughness = .2;
        surf.specular = .1;

        // hacky bump map
        surf.normal.xz += .2 * pavem.bg + vec2(mix(-.1, .1, rboard), 0.);
        surf.normal = normalize(surf.normal);
    }
    else if (matid - .5 < SPRING_MATL)
    {
        surf.basecolor = vec3(.2, .2, .3);
        surf.metallic = 1.;
        surf.roughness = .02;
        surf.specular = .5;
    }
    else if (matid - .5 < TAIL_MATL)
    {
        surf.basecolor = vec3(1.);
        surf.metallic = .0;
        surf.roughness = .8;
        surf.specular = .2;
    }
    else if (matid - .5 < DEBUG_MATL)
    {
        surf.basecolor = vec3(.6, 0., 0.);
        surf.metallic = 0.;
        surf.roughness = 1.;
        surf.specular = 0.;
        surf.emissive = vec3(.3, 0., 0.);
    }

}


BRDFVars calc_BRDFvars(SurfaceData surf, vec3 ldir)
{
    vec3 vdir = normalize( g_camOrigin - surf.point );
    vec3 hdir = normalize(ldir + vdir);
	/*
    float costh = max(0., dot(surf.normal, hdir)); 
    float costd = max(0., dot(ldir, hdir));      
    float costl = max(0., dot(surf.normal, ldir));
    float costv = max(0., dot(surf.normal, vdir));
	*/

    float costh = dot(surf.normal, hdir); 
    float costd = dot(ldir, hdir);      
    float costl = dot(surf.normal, ldir);
    float costv = dot(surf.normal, vdir);
    return BRDFVars(vdir, hdir, costh, costd, costl, costv);

}

vec3 integrate_dirlight(vec3 ldir, vec3 lcolor, float shadowAtten, SurfaceData surf)
{

    BRDFVars bvars = calc_BRDFvars( surf, ldir );

    vec3 cout = vec3(0.);

    if (bvars.costl > SMALL_FLOAT)
    {
        float frk = .5 + 2.* bvars.costd * bvars.costd * surf.roughness;        
        vec3 diff = surf.basecolor * ONE_OVER_PI * (1. + (frk - 1.)*pow5(1.-bvars.costl)) * (1. + (frk - 1.) * pow5(1.-bvars.costv));

        float rroughness = max(0.05, surf.roughness);
        // D(h) factor
        // using the GGX approximation where the gamma factor is 2.

        float alpha = rroughness * rroughness;
        float denom = bvars.costh * bvars.costh * (alpha*alpha - 1.) + 1.;
        float D = (alpha*alpha)/(PI * denom*denom); 

        // G(h,l,v) factor    
        // remap hotness of roughness for analytic lights
        float k = rroughness / 2.;
        float Gv = step(0., bvars.costv) * (bvars.costv/(bvars.costv * (1. - k) + k));
        float Gl = step(0., bvars.costl) * (bvars.costl/(bvars.costl * (1. - k) + k));

        float G = Gl * Gv;

        // F(h,l) factor
        vec3 F0 = surf.specular * mix(vec3(1.), surf.basecolor, surf.metallic);
        vec3 F = F0 + (1. - F0) * pow5(1. - bvars.costd);

        vec3 spec = D * F * G / (4. * bvars.costl * bvars.costv);

        float shad = 1.;
        #ifdef CALC_SHADOWS
        if ( bvars.costl > SMALL_FLOAT && shadowAtten > SMALL_FLOAT)
        {        
            shad = mix(1., soft_shadow( surf.point, ldir, 0.1, 2.5, 50.), shadowAtten);
        }
        #endif

        cout  += diff * bvars.costl * shad * lcolor;
        cout  += spec * bvars.costl * shad * lcolor;
    }
    cout += surf.emissive;

    return cout;
}

vec3 shade(SurfaceData surf)
{    

    // ambient occlusion is amount of occlusion.  So 1 is fully occluded
    // and 0 is not occluded at all.  Makes math easier when mixing 
    // shadowing effects.
    float ao = 0.;
    #ifdef CALC_AMBIENTOCCLUSION
    ao = ambient_occlusion(surf.point, surf.normal) * surf.ambocc_amount;
    //g_debugcolor = vec4(vec3(ao), 1.);
    #endif

    // MAIN KEY
    vec3 keydir = normalize(vec3(-1.5, 1.,-0.8));
    vec3 keyillum  = vec3(3.);
    vec3 cout   = (1. - 2.5 * ao) * integrate_dirlight(keydir, keyillum, 1., surf);

    // LAMP
    vec3 lamporient = normalize(g_lux.headorient - g_lux.headjoint);
    vec3 lamppos = .32 * lamporient + g_lux.headjoint;

    vec3 lampv = lamppos - surf.point;
    float lampvl = length(lampv);
    vec3 lampdir = normalize(lampv);

    float lampatten = 15./(lampvl * lampvl);
    lampatten *= mix(1., smoothstep(.0, .4, dot(-lamporient, lampdir)), surf.cowl_shadow);
    lampatten *= .04 + .96 * surf.cowl_shadow;
    vec3 lampillum = lampatten * vec3(1., 1., .8);    

    if (lampatten > SMALL_FLOAT)
    {
        cout   += integrate_dirlight(lampdir, lampillum, 0., surf);
    }

    // AMBIENT
    vec3 amb = vec3(.15) * surf.basecolor;

    // BOUNCE
    vec3 bncpos = (.5 * lamporient + g_lux.headjoint) * vec3(1., -1., 1.);
    vec3 bncv = bncpos - surf.point;
    float bncvl = length(bncv);
    vec3 bncdir = normalize(bncv);
    float ndbnc =  max(0., dot(bncdir, surf.normal));
    vec3 bnc = surf.basecolor * vec3(1., .9, .8) * (1./(bncvl * bncvl)) * ndbnc * ndbnc;
    //g_debugcolor = vec4(bnc, 1.) * max(0., (1. - 5.5 * ao));
    cout       += (amb + bnc) * max(0., (1. - 5.5 * ao));

    return cout;

}

// **************************************************************************
// GLOBALS

// reference: https://www.shadertoy.com/view/ldlGR7
vec3 ik_solve( vec3 foot,
               vec3 head,
               float larm,
               float uarm)
{

    vec3 q = head - foot;
    q = q * ( 0.5 + 0.5*(larm*larm-uarm*uarm)/dot(q, q ) );
    float s = larm*larm/dot(q,q) - 1.0;
    return q + q.yxz*sqrt( s ) + foot;
}

void animate_globals()
{
    // remap the mouse click ([-1, 1], [-1/ar, 1/ar])
    vec2 click = iMouse.xy / iResolution.xx;    
    click = 2.0 * click - 1.0;  

    g_time = iTime;

    // camera position
    g_camOrigin = vec3(3., 0.0, 3.);

    float rotXAng    = -PI * (.5 * sin(.88 * PI * click.y) + .5);
    float cosrotXAng = cos(rotXAng);
    float sinrotXAng = sin(rotXAng);

    float rotYAng    = .2 * g_time + TWO_PI * click.x;
    float cosrotYAng = cos(rotYAng);
    float sinrotYAng = sin(rotYAng);

    // Rotate the camera around the origin
    g_camOrigin = rot_around_x(g_camOrigin, cosrotXAng, sinrotXAng);
    g_camOrigin = rot_around_y(g_camOrigin, cosrotYAng, sinrotYAng);

    g_camPointAt   = vec3(0., 0.2, 0.);

    // animate luxo
    g_lux.headjoint.y += -.03 + .12 * sin(3.8 * g_time);
    g_lux.headorient.z += .05 * cos(3.6 * g_time);   
    g_lux.headorient.y += .2 * sin(.8 * g_time) - .1;    


    g_lux.midjoint = ik_solve(g_lux.footjoint, 
                              g_lux.headjoint,
                              .45, .6);

    vec3 larmv = normalize(g_lux.midjoint - g_lux.footjoint);
    vec3 uarmv = normalize(g_lux.headjoint - g_lux.midjoint);
    float larmang = atan( larmv.y, larmv.x );
    float uarmang = atan( uarmv.y, uarmv.x );
    // change in elbow angle from the default modeled position
    float elbowang = -(.9048 - 2.1508) + (larmang - uarmang);
    g_lux.celbowang = cos(elbowang);
    g_lux.selbowang = sin(elbowang);

    g_lux.headorient.z += .5 * sin(3.8 * g_time);  

}


// **************************************************************************
// MAIN

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   

    // ----------------------------------
    // Animate globals

    animate_globals();

    // ----------------------------------
    // Setup Camera

    CameraData cam = setup_camera(fragCoord);

    // ----------------------------------
    // SCENE MARCHING

    vec2 scenemarch = distmarch( cam.origin, 
                                 cam.dir, 
                                 DISTMARCH_MAXDIST );

    // ----------------------------------
    // SHADING
    vec3 scenecol = vec3(0.);
    if (scenemarch.y > 0.5)
    {
        vec3 mp = cam.origin + scenemarch.x * cam.dir;
        vec3 mn = compute_normal( mp );

        SurfaceData currSurf = INITSURF(mp, mn);
        calc_material(scenemarch.y, currSurf);

        scenecol = shade( currSurf );
    }

    // ----------------------------------
    // POST PROCESSING

    // fall off exponentially into the distance (as if there is a spot light
    // on the point of interest).
    scenecol *= exp( -0.05*scenemarch.x*scenemarch.x );

    // Gamma correct
    scenecol = pow(scenecol, vec3(0.45));

    if (g_debugcolor.a > 0.) {        
        fragColor.rgb = g_debugcolor.rgb;
    } else {
        fragColor.rgb = scenecol;
    }
    fragColor.a = 1.;
}

// Parameters
#define CAMERA_FOCAL_LENGTH	1.5
#define VOXEL_STEP			50.0
#define SOUND
#define MOUSE
#define HSV2RGB_FAST

// Constants
#define PI		3.14159265359
#define SQRT2	1.41421356237

// PRNG
// From https://www.shadertoy.com/view/4djSRW
float rand (in vec2 seed) {
	seed = fract (seed * vec2 (5.3983, 5.4427));
	seed += dot (seed.yx, seed.xy + vec2 (21.5351, 14.3137));
	return fract (seed.x * seed.y * 95.4337);
}

// HSV to RGB
vec3 hsv2rgb (in vec3 hsv) {
	#ifdef HSV2RGB_SAFE
	hsv.yz = clamp (hsv.yz, 0.0, 1.0);
	#endif
	#ifdef HSV2RGB_FAST
	return hsv.z * (1.0 + 0.5 * hsv.y * (cos (2.0 * PI * (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0))) - 1.0));
	#else
	return hsv.z * (1.0 + hsv.y * clamp (abs (fract (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0)) * 6.0 - 3.0) - 2.0, -1.0, 0.0));
	#endif
}

// Main function
void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Define the ray corresponding to this fragment
	vec3 ray = normalize (vec3 ((2.0 * fragCoord.xy - iResolution.xy) / iResolution.y, CAMERA_FOCAL_LENGTH));

	// Get the music info
	#ifdef SOUND
	float soundBass = texture (iChannel0, vec2 (0.0)).x;
	float soundTreble = texture (iChannel0, vec2 (0.9, 0.0)).x;
	#else
	float soundBass = 0.6 + 0.4 * cos (iTime * 0.2);
	float soundTreble = 0.5 + 0.5 * cos (iTime * 1.2);
	#endif

	// Set the camera
	vec3 origin = vec3 (0.0, 6.0 - 3.0 * cos (iTime * 0.3), iTime * 2.0 + 700.0 * (0.5 + 0.5 * sin (iTime * 0.1)));
	float cameraAngle = iTime * 0.1;
	#ifdef MOUSE
	cameraAngle += 2.0 * PI * iMouse.x / iResolution.x;
	#endif
	vec3 cameraForward = vec3 (cos (cameraAngle), cos (iTime * 0.3) - 1.5, sin (cameraAngle));
	vec3 cameraUp = vec3 (0.2 * cos (iTime * 0.7), 1.0, 0.0);
	mat3 cameraRotation;
	cameraRotation [2] = normalize (cameraForward);
	cameraRotation [0] = normalize (cross (cameraUp, cameraForward));
	cameraRotation [1] = cross (cameraRotation [2], cameraRotation [0]);
	ray = cameraRotation * ray;

	// Voxel
	vec3 color = vec3 (0.0);

    vec2 voxelSign = sign (ray.xz);
	vec2 voxelIncrement = voxelSign / ray.xz;
	float voxelTimeCurrent = 0.0;
	vec2 voxelTimeNext = (0.5 + voxelSign * (0.5 - fract (origin.xz + 0.5))) * voxelIncrement;
	vec2 voxelPosition = floor (origin.xz + 0.5);
	float voxelHeight = 0.0;
	bool voxelDone = false;
	vec3 voxelNormal = vec3 (0.0);
	for (float voxelStep = 1.0; voxelStep <= VOXEL_STEP; ++voxelStep) {

		// Compute the height of this column
		voxelHeight = 2.0 * rand (voxelPosition) * smoothstep (0.2, 0.5, soundBass) * sin (soundBass * 8.0 + voxelPosition.x * voxelPosition.y) - 5.0 * (0.5 + 0.5 * cos (voxelPosition.y * 0.15));

		// Check whether we hit the side of the column
		if (voxelDone = voxelHeight > origin.y + voxelTimeCurrent * ray.y) {
			break;
		}

		// Check whether we hit the top of the column
		float timeNext = min (voxelTimeNext.x, voxelTimeNext.y);
		float timeIntersect = (voxelHeight - origin.y) / ray.y;
		if (voxelDone = timeIntersect > voxelTimeCurrent && timeIntersect < timeNext) {
			voxelTimeCurrent = timeIntersect;
			voxelNormal = vec3 (0.0, 1.0, 0.0);
			break;
		}

		// Next voxel...
		voxelTimeCurrent = timeNext;
		voxelNormal.xz = step (voxelTimeNext.xy, voxelTimeNext.yx);
		voxelTimeNext += voxelNormal.xz * voxelIncrement;
		voxelPosition += voxelNormal.xz * voxelSign;
	}
	if (voxelDone) {
		origin += voxelTimeCurrent * ray;

		// Compute the local color
		vec3 mapping = origin;
		mapping.y -= voxelHeight + 0.5;
		mapping *= 1.0 - voxelNormal;
		mapping += 0.5;
		float id = rand (voxelPosition);
		color = hsv2rgb (vec3 ((iTime + floor (mapping.y)) * 0.05 + voxelPosition.x * 0.01, smoothstep (0.2, 0.4, soundBass), 0.7 + 0.3 * cos (id * iTime + PI * soundTreble)));
		color *= smoothstep (0.8 - 0.6 * cos (soundBass * PI), 0.1, length (fract (mapping) - 0.5));
		color *= 0.5 + smoothstep (0.90, 0.95, cos (id * 100.0 + soundTreble * PI * 0.5 + iTime * 0.5));
		color *= 1.0 - voxelTimeCurrent / VOXEL_STEP * SQRT2;
	}

	// Set the fragment color
	fragColor = vec4 (color, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 position = (fragCoord.xy/iResolution.xy) - 0.5 ;
    float time = iTime;

    float beat = texture( iChannel1, vec2(0.0, 0.01 ) ).x;

    float light = 1.5/length(20. / beat*length(0.5 * position.x - vec2(0, 0)));

    float dia = position.y *2.*sin(0.01*position.y + 2. * time * 0.1) * beat;
    dia = 1.0 / (200. *(position.x - dia));
    dia += 0.001/length(position - vec2(0., position.y));

    float dia2 = -(position.y *2.*sin(0.01*position.y + 2. * time * 0.1) * beat);
    dia2 = -(1.0 / (200. *(position.x - dia2)));
    dia2 += 0.001/length(position - vec2(0., position.y));

    float stripe = position.y * 0.15* sin(500.0*position.y*beat - 5. * time * 0.5) * 0.9;
    stripe += 0.5 / (1000. * abs(position.x - stripe*0.5));

	vec4 veclight = vec4(light *5. , light, light*5. , 1.0);
    vec4 diag = vec4(position.y*1.1 - dia, dia, dia * 45., 3.);
    vec4 diag2 = vec4(position.y*1.1 - dia2, dia2, dia2 * 45., 3.);
    vec4 alldiag = mix(diag,diag2,0.5);

    vec4 vecstripe = vec4(position.y*1. - stripe, stripe, stripe/5.,1.0);

    fragColor = mix(alldiag,mix(veclight,vecstripe,0.5),0.9);
}

float pi = 3.141592;

float hash(float n) 
{ 
    return fract(sin(n)*43758.5453123); 
}

float noise2(in vec2 x)
{
    vec2 p = floor(x);
    vec2 f = fract(x);
    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*157.0;
    return mix(mix(hash(n+0.0), hash(n+1.0),f.x), mix(hash(n+157.0), hash(n+158.0),f.x),f.y);
}

float noise3(in vec3 x)
{
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*157.0 + 113.0*p.z;
    return mix(
        mix(mix(hash(n+  0.0), hash(n+  1.0),f.x), mix(hash(n+157.0), hash(n+158.0),f.x),f.y),
        mix(mix(hash(n+113.0), hash(n+114.0),f.x), mix(hash(n+270.0), hash(n+271.0),f.x),f.y),f.z);
}

vec2 r(vec2 v,float y)
{
    return cos(y)*v+sin(y)*vec2(-v.y,v.x);
}

vec3 smin(vec3 a, vec3 b)
{
    if (a.x < b.x)
        return a;

    return b;
}

vec3 smax(vec3 a, vec3 b)
{
	if (a.x > b.x)
        return a;

    return b;
}

vec3 sinv(vec3 a)
{
	return vec3(-a.x, a.y, a.z);    
}

float sdSphere(vec3 p, float s)
{
  return length(p)-s;
}

float sdBox(vec3 p, vec3 b, float r)
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) +
         length(max(d,0.0)) - r;
}

float sdCylinder( vec3 p, vec3 c )
{
  return length(p.xz-c.xy)-c.z;
}

float smoothmax( float a, float b, float k )
{
    return -log(exp(k*a) + exp(k*b))/-k;
}

float smoothmin( float a, float b, float k )
{
    return -log(exp(-k*a) + exp(-k*b))/k;
}

float cylsphere(vec3 p)
{
    float d = max(sdCylinder(p, vec3(0.0, 0.0, 0.04)), sdBox(p, vec3(0.3), 0.0));
    d = smoothmin(d, sdSphere(p+vec3(0.0, 0.35, 0.0), 0.08), 48.0);
    d = smoothmin(d, sdSphere(p-vec3(0.0, 0.35, 0.0), 0.08), 48.0);
    return d;
}

vec3 greeble0(vec3 p, float phase)
{
    float t = mod(phase + iTime * 0.5, 1.0);
    float rotation = sign(phase-0.5) * min(1.0, max(0.0, -0.2 + 5.0 * t)) * pi / 2.0;
    float translation = min(1.0, max(0.0, 2.0 * sin(min(t - 0.02, 0.5) * 10.0)));

    float d = sdBox(p, vec3(0.4), 0.075);
    float e = sdSphere(p - vec3(0.0, 0.6, 0.0), 0.2);
    d = smoothmax(d, -e, 32.0);
    p.y -= translation * 0.3 - 0.1;
    p.xz = r(p.xz, rotation);
    e = max(sdCylinder(p, vec3(0.0, 0.0, 0.1)), sdBox(p, vec3(0.8), 0.0));
    vec3 q = p;    
    q.y -= 0.8;
    q.yz = r(q.yz,pi/2.0);
    e = smoothmin(e, cylsphere(q), 16.0);
    q.xy = r(q.xy,pi/2.0);
    e = smoothmin(e, cylsphere(q), 16.0);
    return smin(vec3(d, 0.0, 0.0), vec3(e, 1.0, 0.0));
}

vec3 greeble1(vec3 p, float phase)
{
    float d = sdBox(p, vec3(0.425), 0.05);
    d = smoothmax(d, -sdBox(p + vec3(0.0, 0.0, 0.3), vec3(0.3, 1.0, 0.01), 0.0), 32.0);
    d = smoothmax(d, -sdBox(p - vec3(0.0, 0.0, 0.3), vec3(0.3, 1.0, 0.01), 0.0), 32.0);
    d = smoothmax(d, -sdBox(p + vec3(0.3, 0.0, 0.0), vec3(0.01, 1.0, 0.3), 0.0), 32.0);
    d = smoothmax(d, -sdBox(p - vec3(0.3, 0.0, 0.0), vec3(0.01, 1.0, 0.3), 0.0), 32.0);

    float t = mod(phase + sign(phase-0.5) * iTime * 0.5, 1.0);
    float x = max(-1.0, min(1.0, 4.0*cos(t*2.0*pi)));
    float y = max(-1.0, min(1.0, 4.0*sin(t*2.0*pi)));
    x *= 0.3;
    y *= 0.3;
    vec3 q = p + vec3(x, 0, y);
    float e = sdBox(q, vec3(0.03, 0.75, 0.03), 0.0);
    q.y -= 0.75;
    e = smoothmin(e, sdSphere(q, 0.1), 32.0);
    return smin(vec3(d, 2.0, 0.0), vec3(e, 3.0, 0.0));
}

vec3 greeble2(vec3 p, float phase)
{
    float d = sdBox(p, vec3(0.425), 0.05);
    d = smoothmax(d, -sdBox(p + vec3(0.2, 0.0, 0.0), vec3(0.01, 1.0, 0.3), 0.0), 32.0);
    d = smoothmax(d, -sdBox(p - vec3(0.2, 0.0, 0.0), vec3(0.01, 1.0, 0.3), 0.0), 32.0);

    float x = pow(mod(phase + sign(phase-0.5) * iTime * 0.5, 1.0), 2.0) * 2.0 * pi;
    float t = max(-0.5, min(0.5, sin(x)));
    p.yz = r(p.yz, t);
    vec3 q = p + vec3(0.0, 0.25, 0.0);
    float e =  sdBox(q - vec3(0.2, 0.0, 0.0), vec3(0.02, 1.0, 0.02), 0.0);
    e = min(e, sdBox(q + vec3(0.2, 0.0, 0.0), vec3(0.02, 1.0, 0.02), 0.0));
    e = min(e, sdBox(q - vec3(0.0, 1.0, 0.0), vec3(0.175, 0.02, 0.02), 0.0));
    e = smoothmin(e, sdSphere(q - vec3(0.2, 1.01, 0.0), 0.03), 32.0);
    e = smoothmin(e, sdSphere(q - vec3(-0.2, 1.01, 0.0), 0.03), 32.0);
    q.y -= 1.0;
    q.xy = r(q.xy, pi / 2.0);
    e = smoothmin(e, max(sdCylinder(q, vec3(0.0, 0.0, 0.03)), sdBox(q, vec3(0.1), 0.0)), 32.0);
    return smin(vec3(d, 4.0, 0.0), vec3(e, 5.0, 0.0));
}

vec3 greeble3(vec3 p, float phase)
{
    float d = sdBox(p, vec3(0.4), 0.08);
    ivec2 i = ivec2(p.xz / 0.15 + floor(phase * 815.0));
    float phase2 = noise2(vec2(i));
    vec3 q = p;
    q.xz = mod(q.xz, 0.15);
    q.xz -= 0.075;
    q.y -= 0.5;
    float hole = max(sdBox(q, vec3(0.05, 1.0, 0.05), 0.0), sdBox(p, vec3(0.3, 2.0, 0.3), 0.0));
    d = smoothmax(d, -hole, 96.0);

    float t = phase2 * 2.0 * pi + iTime * 8.0;
    q.y -= 0.1 * max(-0.5, min(0.5, sin(t)));
    q.y += 0.5;
    float e = sdBox(q, vec3(0.025, 0.6, 0.025), 0.0);
    e = max(e, sdBox(p, vec3(0.3, 2.0, 0.3), 0.0));
    return smin(vec3(d, 6.0, 0.0), vec3(e, 7.0, 0.0));
}

vec3 greeble4(vec3 p, float phase)
{
    float angle = floor(phase * 4.0) * 0.5 * pi;
    p.xz = r(p.xz, angle);
    float d = sdBox(p, vec3(0.4), 0.08);
    d = smoothmax(d, -sdBox(p - vec3(0.2, 0.0, 0.1), vec3(0.1, 1.0, 0.2), 0.0), 32.0);
    d = smoothmax(d, -sdBox(p + vec3(0.2, 0.0, -0.1), vec3(0.1, 1.0, 0.2), 0.0), 32.0);
    vec3 q = p - vec3(0.0, 0.8, -0.3);
    float e = sdBox(q + vec3(0.0, 0.2, 0.0), vec3(0.0, 0.15, 0.0), 0.1) / 0.6;
    q /= 0.6;
    q.yz = r(q.yz,pi/2.0);

    float t = phase + 0.2 * iTime;
    angle = 0.45 * max(-1.0, min(1.0, 4.0*cos(t*2.0*pi)));
    float y = 0.5 + 0.5 * max(-1.0, min(1.0, 4.0*sin(t*2.0*pi)));
    y = pow(y, 1.25 + 0.75 * cos(t*2.0*pi));
    q.xy = r(q.xy, angle);
    q.y += 0.4;

    e = smoothmin(e, cylsphere(q), 16.0);
    q += vec3(0.0, 0.35, 0.05);
    e = min(e, sdBox(q, vec3(0.0, 0.0, -0.1), 0.2)) * 0.6;
    float f = sdBox(q + vec3(0.0, 0.0, 1.2 - y), vec3(0.1), 0.0) * 0.6;
    return smin(smin(vec3(d, 8.0, 0.0), vec3(e, 9.0, 0.0)), vec3(f, 10.0, 0.0));
}

vec3 greeble(vec3 p, float findex, float phase)
{
    const int indexCount = 6;
    int index = int(findex * float(indexCount));
    p.y -= phase * 0.2 - 0.2;
    if (index == 0)
        return greeble0(p, phase);
    else if (index == 1)
        return greeble1(p, phase);
    else if (index == 2)
        return greeble2(p, phase);
    else if (index == 3)
        return greeble3(p, phase);
    else if (index == 4)
        return greeble4(p, phase);

    return vec3(sdBox(p, vec3(0.4), 0.025), 10.0, 0.0);
}

vec3 f( vec3 p )
{
    ivec3 h = ivec3(p+1337.0);
    float hash = noise2(vec2(h.xz));
    h = ivec3(p+42.0);
    float phase = noise2(vec2(h.xz));
    vec3 q = p;
    q.xz = mod(q.xz, 1.0);
    q -= 0.5;
	return greeble(q, hash, phase);
}

vec3 colorize(float index)
{
    if (index == 0.0)
        return vec3(0.4, 0.6, 0.2);

    if (index == 1.0)
        return vec3(0.6, 0.3, 0.2);

    if (index == 2.0)
        return vec3(1.0, 0.8, 0.5);

    if (index == 3.0)
        return vec3(0.9, 0.2, 0.6);

    if (index == 4.0)
        return vec3(0.3, 0.6, 0.7);

    if (index == 5.0)
        return vec3(1.0, 1.0, 0.3);

    if (index == 6.0)
        return vec3(0.7, 0.5, 0.7);

    if (index == 7.0)
        return vec3(0.4, 0.3, 0.4);

    if (index == 8.0)
        return vec3(0.8, 0.3, 0.2);

    if (index == 9.0)
        return vec3(0.5, 0.8, 0.2);

	return vec3(index / 10.0);
}

float ao(vec3 v, vec3 n) 
{
    const int ao_iterations = 10;
    const float ao_step = 0.2;
    const float ao_scale = 0.75;

	float sum = 0.0;
	float att = 1.0;
	float len = ao_step;

	for (int i = 0; i < ao_iterations; i++)
    {
		sum += (len - f(v + n * len).x) * att;		
		len += ao_step;		
		att *= 0.5;
	}

	return 1.0 - max(sum * ao_scale, 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor.xyz = vec3(0);

    vec3 q = vec3((fragCoord.xy / iResolution.xy - 0.5), 1.0);     
    float vignette = 1.0 - length(q.xy);
    q.x *= iResolution.x / iResolution.y;
    q.y -= 0.5;
    vec3 p = vec3(0, 0.0, -10.0);
    q = normalize(q);
    q.xz = r(q.xz, iTime * 0.1);
    p.y += 2.5;
	p.z -= iTime*0.5;

    float t=0.0;
    vec3 d = vec3(0);
    float steps = 0.0;
    const float maxSteps = 96.0;
    for (float tt = 0.0; tt < maxSteps; ++tt)
    {
        d = f(p+q*t);
        t += d.x*0.45;
        if(!(t<=50.0)||d.x<=0.0001)
        {
            break;
        }
        steps = tt;
    }

    vec3 glow = vec3(1.1, 1.1, 1.0);
    vec3 fog = vec3(0.7, 0.75, 0.8);
    vec3 color = fog;

    if (t <= 50.0)
    {
        vec3 hit = p+q*t;

        vec2 e = vec2(0.001, 0.00);
        vec3 normal= vec3( f(hit + e.xyy).x - f(hit - e.xyy).x, f(hit + e.yxy).x - f(hit - e.yxy).x, f(hit + e.yyx).x - f(hit - e.yyx).x) / (2.0 * e.x);

        normal= normalize(normal);

        float fao = ao(hit, normal);
        vec3 ldir = normalize(vec3(1.0, 1.0, -1.0));
        vec3 light = (0.5 * fog.rgb + vec3(0.5 * fao * abs(dot(normal, ldir)))) * colorize(d.y); // diffuse
        light += (1.0 - t / 50.0) * vec3(fao * pow(1.0 - abs(dot(normal, q)), 4.0)); // rim
        q = reflect(q, normal);
        light += fao * vec3(pow(abs(dot(q, ldir)), 16.0)); // specular
        color = min(vec3(1), light);
        color *= fao;
    }

    float luma = dot(color.rgb, vec3(0.3, 0.5, 0.2));
    color = mix(color, 1.0 * luma * vec3(1.0, 0.9, 0.5), 2.0 * max(0.0, luma-0.5)); // yellow highlights
    color = mix(color, 1.0 * luma * vec3(0.2, 0.5, 1.0), 2.0 * max(0.0, 0.5-luma)); // blue shadows
    //color = mix(color, glow, 0.8 * pow(steps / 90.0, 8.0)); // glow
    color = mix(color, fog, pow(min(1.0, t / 50.0), 0.5)); // fog
    color = pow(color, vec3(0.8)); // gamma
    color = smoothstep(0.0, 1.0, color); // contrast
    color *= pow(vignette + 0.3, 0.5); // vignette
    fragColor = vec4(color, 1.0);
}

//#define MORE_MOUSE_MOVEMENT
//#define RANDOM_BALL_OFFSETS

// Gets the normal vector along which to trace a ray for the current pixel.
vec3 lens(vec2 fragCoord) {
    return normalize(vec3(fragCoord.xy / iResolution.xy - 0.5, 0.3));
}

// Terrain.
float terrainWave(vec3 at, float xzScale, float yScale) {
    return (sin(at.x * xzScale) + cos(at.z * xzScale)) * yScale;
}

float terrain(vec3 at) {
    return 
        terrainWave(at, 0.01, 32.0)
        + terrainWave(at, 0.1, 2.0)
        -abs(sin((at.x/40.0) + sin(at.z / 100.0) * 1.0)) * 50.0;
}

float terrainDetailed(vec3 at) {
    return terrain(at);
}

// Shapes.
struct shapeResult {
    float distance;
    int materialId;
};

shapeResult sphere(vec3 origin, float radius, int materialId, vec3 at) {
    return shapeResult(distance(origin, at) - radius, materialId);
}

shapeResult plane(float origin, int materialId, float at) {
    return shapeResult(at - origin, materialId);
}

// Shape CSG.
shapeResult add(shapeResult left, shapeResult right) {
    return shapeResult(left.distance < right.distance ? left.distance : right.distance, left.distance < right.distance ? left.materialId : right.materialId);
}

// Scene analysis.
#define MATERIAL_SKY 0
#define MATERIAL_SAND 1
#define MATERIAL_MIRROR 2

shapeResult scene(vec3 at) {
    return add(
        sphere(vec3(30.0, 30.0, 30.0)
				#ifdef RANDOM_BALL_OFFSETS
					+ sin(floor(at.x / 60.0) * 2.0 + floor(at.y / 60.0) * 3.1 + floor(at.z / 60.0) * 4.6) * 16.0
				#endif
			, 5.0, MATERIAL_MIRROR, mod(at, 60.0)), 
        plane(terrainDetailed(at), MATERIAL_SAND, at.y)
    );
}

#define NORMAL_OFFSET vec2(0.01, 0.0)
vec3 sceneNormal(vec3 at) {
    return normalize(vec3(
        scene(at + NORMAL_OFFSET.xyy).distance - scene(at - NORMAL_OFFSET.xyy).distance,
        scene(at + NORMAL_OFFSET.yxy).distance - scene(at - NORMAL_OFFSET.yxy).distance,
        scene(at + NORMAL_OFFSET.yyx).distance - scene(at - NORMAL_OFFSET.yyx).distance
    ));
}

struct raymarchResult {
    vec3 location;
    int materialId;
};

raymarchResult raymarch(vec3 at, vec3 normal) {
    for(int iteration = 0; iteration < 128; iteration++) {
        shapeResult sam = scene(at);
        if(sam.distance < 0.1)
            return raymarchResult(at, sam.materialId);
        at += normal * sam.distance * 0.5;
    }
    return raymarchResult(vec3(0.0), MATERIAL_SKY);
}

raymarchResult raymarchFast(vec3 at, vec3 normal) {
    for(int iteration = 0; iteration < 24; iteration++) {
        shapeResult sam = scene(at);
        if(sam.distance < 0.1)
            return raymarchResult(at, sam.materialId);
        at += normal * sam.distance;
    }
    return raymarchResult(vec3(0.0), MATERIAL_SKY);
}

// Materials.


// Computes the sky color at a given normal.
vec3 sky(vec3 normal) {
    if(normal.y < -0.1) return vec3(0.0);
    // Sun normal.
    float sunBlend = dot(normal, normalize(vec3(1.0, 2.0, 2.0))) * 0.5 + 0.5;
    return mix(
        mix(
            // Horizon color.
            vec3(0.4, 0.5, 0.7),
            mix(
                // Sky color.
                vec3(0.0, 0.0, 1.0), 
                vec3(0.0, 0.0, 0.1),
            	sunBlend
            ),
            normal.y
        ), 
        // Sun color.
        vec3(2.0, 2.0, 1.0),
        pow(
 			sunBlend,
            // Sun size.
            32.0
        )
    );
}

float occlusion(vec3 at, vec3 normal, float radius) {
    return max(scene(at + normal * radius).distance / radius, 0.0);
}

vec3 diffuse(vec3 at, vec3 color) {
    vec3 normal = sceneNormal(at);
    vec3 light = sky(normal) * occlusion(at, normal, 8.0);
    return light * color;
}

vec3 sand(vec3 at) {
    return diffuse(at, vec3(0.9, 0.4, 0.01));
}

vec3 secondary(vec3 at, vec3 normal) {
    raymarchResult sam = raymarchFast(at, normal);
    if(sam.materialId == MATERIAL_SAND) {
        return sand(sam.location);
    } else if(sam.materialId == MATERIAL_MIRROR) {
        vec3 refl = sceneNormal(sam.location);
        return sky(reflect(normal, refl)) * occlusion(sam.location, refl, 8.0);
    }    
    return sky(normal);
}

vec3 mirror(vec3 at, vec3 inboundNormal) {
    vec3 normal = reflect(inboundNormal, sceneNormal(at));
    return secondary(at + normal * 0.1, normal);
}

vec3 primary(vec3 at, vec3 normal) {
    raymarchResult sam = raymarch(at, normal);
    if(sam.materialId == MATERIAL_SAND) {
        return sand(sam.location);
    } else if(sam.materialId == MATERIAL_MIRROR) {
        return mirror(sam.location, normal);
    }    
    return sky(normal);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 cameraLocation = vec3(0.0, 50.0, -2.0) + vec3((iMouse.xy / iResolution.xy) * 
		#ifdef MORE_MOUSE_MOVEMENT
			72.0, 
		#else
			8.0,
		#endif
		-50.0 + iTime * 5.0);
    vec3 cameraNormal = lens(fragCoord);
	fragColor = vec4(pow(primary(cameraLocation, cameraNormal), vec3(1.0 / 2.2)),1.0);
}

