float dp(float i)
{ 
    i=floor(i);
    return i*2.0-floor(i/2.0)-floor(i/3.0)*4.0;
}


float dith(float2 xy)
{
    float iTime=0.0;
    float x=floor(xy.x+iTime*16.0*64.0*60.0);
    float y=floor(xy.y+iTime*16.0*16.0*60.0);
    float v=0.0;
    float sz=16.0;
    float mul=1.0;
    for(int i=0;i<5;i++)
    {
    		v+=dp(
                fltmod(fltmod(x/sz,2.0)+2.0*fltmod(y/sz,2.0),4.0)
            )*mul;
        sz/=2.0;
        mul*=4.0;
    }
	return float(v)/float(mul-1.0);
}

