float noise(in vec2 p)
{
	return sin(p.x * 1.5) * sin(p.y * 1.5);   
}

float fbm(in vec2 p)
{
	float f = 0.0;
    float factor = 2.5;
    float factor2 = 0.5;
    for(int i = 0; i < 6; ++i)
    {
        f += factor2 * noise(p * factor);
        factor2 *= 0.54;
        factor *= 2.0;
    }
    return f;
}

float pattern(in vec2 p)
{
    p *= 0.5;
    float rad = fbm(p);
    rad = mix(0.2, 0.8, rad);
	p.x += sin(iTime*rad * 0.1) * 0.02 * rad;
    p.y += cos(iTime*rad * 0.1) * 0.02 * rad;

    vec2 q;
    q.x = fbm(p);
    q.y = fbm(p*2.0);

    q.x += sin(iTime*rad*0.6) * 0.03 * rad;
    q.y += cos(iTime*rad*0.6) * 0.02 * rad;

    vec2 r;
    r.x = fbm(q);
    r.y = fbm(q*2.0);

    float f = fbm(r);

    return f;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;
    float f = pattern(p);
    fragColor = vec4(f, f, f, 1.0);
}

